const float tau = 6.28318530717958647692;

// Gamma correction
#define GAMMA (2.2)

vec3 ToLinear( in vec3 col )
{
	// simulate a monitor, converting colour values into light values
    return pow( col, vec3(GAMMA) );
}

vec3 ToGamma( in vec3 col )
{
	// convert back into colour values, so the correct light will come out of the monitor
	return pow( col, vec3(1.0/GAMMA) );

}

vec3 localRay;

// Set up a camera looking at the scene.
// origin - camera is positioned relative to, and looking at, this point
// distance - how far camera is from origin
// rotation - about x & y axes, by left-hand screw rule, relative to camera looking along +z
// zoom - the relative length of the lens
void CamPolar( out vec3 pos, out vec3 ray, in vec3 origin, in vec2 rotation, in float distance, in float zoom, in vec2 fragCoord )
{


  //    rotation = vec2(.34,.3);    

    // get rotation coefficients
	vec2 c = vec2(cos(rotation.x),cos(rotation.y));
	vec4 s;
	s.xy = vec2(sin(rotation.x),sin(rotation.y)); // worth testing if this is faster as sin or sqrt(1.0-cos);
	s.zw = -s.xy;

	// ray in view space
	ray.xy = fragCoord.xy - iResolution.xy*.5;// смещение камеры 0.5 центр


	ray.z = iResolution.y*zoom*1.3; // зум камеры
	ray = normalize(ray);
	localRay = ray;

	// rotate ray
	ray.yz = ray.yz*c.xx + ray.zy*s.zx;
	ray.xz = ray.xz*c.yy + ray.zx*s.yw;

	// position camera
	pos = origin - distance*vec3(c.x*s.y,s.z,c.x*c.y);
}


// Noise functions, distinguished by variable types

vec2 Noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
//	vec3 f2 = f*f; f = f*f2*(10.0-15.0*f+6.0*f2);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z);

	vec4 rg = textureLod( iChannel0, (uv+f.xy+0.5)/256.0, 0.0 );

	return mix( rg.yw, rg.xz, f.z );
}

vec2 NoisePrecise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
//	vec3 f2 = f*f; f = f*f2*(10.0-15.0*f+6.0*f2);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z);

	vec4 rg = mix( mix(
				textureLod( iChannel0, (uv+0.5)/256.0, 0.0 ),
				textureLod( iChannel0, (uv+vec2(1,0)+0.5)/256.0, 0.0 ),
				f.x ),
				  mix(
				textureLod( iChannel0, (uv+vec2(0,1)+0.5)/256.0, 0.0 ),
				textureLod( iChannel0, (uv+1.5)/256.0, 0.0 ),
				f.x ),
				f.y );


	return mix( rg.yw, rg.xz, f.z );
}

vec4 Noise( in vec2 x )
{
    vec2 p = floor(x.xy);
    vec2 f = fract(x.xy);
	f = f*f*(3.0-2.0*f);
//	vec3 f2 = f*f; f = f*f2*(10.0-15.0*f+6.0*f2);

	vec2 uv = p.xy + f.xy;
	return textureLod( iChannel0, (uv+0.5)/256.0, 0.0 );
}

vec4 Noise( in ivec2 x )
{
	return textureLod( iChannel0, (vec2(x)+0.5)/256.0, 0.0 );
}

vec2 Noise( in ivec3 x )
{
	vec2 uv = vec2(x.xy)+vec2(37.0,17.0)*float(x.z);
	return textureLod( iChannel0, (uv+0.5)/256.0, 0.0 ).xz;
}


float Waves( vec3 pos )
{
	pos *= .2*vec3(1,1,1);

	const int octaves = 5;
	float f = 0.0;

	// need to do the octaves from large to small, otherwise things don't line up
	// (because I rotate by 45 degrees on each octave)
		pos += iTime*vec3(0,.1,.1);
	for ( int i=0; i < octaves; i++ )
	{
		pos = (pos.yzx + pos.zyx*vec3(1,-1,1))/sqrt(2.0);
		f  = f*2.0+abs(Noise(pos).x-.5)*2.0;
		pos *= 2.0;
	}
	f /= exp2(float(octaves));

	return (.5-f)*1.0;
}

float WavesDetail( vec3 pos )
{
	pos *= .2*vec3(1,1,1);

	const int octaves = 8;
	float f = 0.0;

	// need to do the octaves from large to small, otherwise things don't line up
	// (because I rotate by 45 degrees on each octave)
		pos += iTime*vec3(0,.1,.1);
	for ( int i=0; i < octaves; i++ )
	{
		pos = (pos.yzx + pos.zyx*vec3(1,-1,1))/sqrt(2.0);
		f  = f*2.0+abs(NoisePrecise(pos).x-.5)*2.0;
		pos *= 2.0;
	}
	f /= exp2(float(octaves));

	return (.5-f)*1.0;
}

float WavesSmooth( vec3 pos )
{
	pos *= .2*vec3(1,1,1);

	const int octaves = 2;
	float f = 0.0;

	// need to do the octaves from large to small, otherwise things don't line up
	// (because I rotate by 45 degrees on each octave)
		pos += iTime*vec3(0,.1,.1);
	for ( int i=0; i < octaves; i++ )
	{
		pos = (pos.yzx + pos.zyx*vec3(1,-1,1))/sqrt(2.0);
		//f  = f*2.0+abs(Noise(pos).x-.5)*2.0;
		f  = f*2.0+sqrt(pow(NoisePrecise(pos).x-.5,2.0)+.01)*2.0;
		pos *= 2.0;
	}
	f /= exp2(float(octaves));

	return (.5-f)*1.0;
}

float WaveCrests( vec3 ipos, in vec2 fragCoord )
{
	vec3 pos = ipos;
	pos *= .2*vec3(1,1,1);

	const int octaves1 = 6;
	const int octaves2 = 16;
	float f = 0.0;

	// need to do the octaves from large to small, otherwise things don't line up
	// (because I rotate by 45 degrees on each octave)
	pos += iTime*vec3(0,.1,.1);
	vec3 pos2 = pos;
	for ( int i=0; i < octaves1; i++ )
	{
		pos = (pos.yzx + pos.zyx*vec3(1,-1,1))/sqrt(2.0);
		f  = f*1.5+abs(Noise(pos).x-.5)*2.0;
		pos *= 2.0;
	}
	pos = pos2 * exp2(float(octaves1));
	pos.y = -.05*iTime;
	for ( int i=octaves1; i < octaves2; i++ )
	{
		pos = (pos.yzx + pos.zyx*vec3(1,-1,1))/sqrt(2.0);
		f  = f*1.5+pow(abs(Noise(pos).x-.5)*2.0,1.0);
		pos *= 2.0;
	}
	f /= 1500.0;

	f -= Noise(ivec2(fragCoord.xy)).x*.01;

	return pow(smoothstep(.4,-.1,f),6.0);
}


vec3 Sky( vec3 ray )
{
	return vec3(.4,.45,.5);
    //return vec3(.01,.1,.01);
}


vec3 boatRight, boatUp, boatForward;
vec3 boatPosition;

void ComputeBoatTransform( void )
{
	vec3 samples[5];

	samples[0] = vec3(0,0, 0);
	samples[1] = vec3(0,0, .5);
	samples[2] = vec3(0,0,-.5);
	samples[3] = vec3( .5,0,0);
	samples[4] = vec3(-.5,0,0);

	samples[0].y = WavesSmooth(samples[0]);
	samples[1].y = WavesSmooth(samples[1]);
	samples[2].y = WavesSmooth(samples[2]);
	samples[3].y = WavesSmooth(samples[3]);
	samples[4].y = WavesSmooth(samples[4]);

	boatPosition = (samples[0]+samples[1]+samples[2]+samples[3]+samples[4])/5.0;

	boatRight = samples[3]-samples[4];
	boatForward = samples[1]-samples[2];
	boatUp = normalize(cross(boatForward,boatRight));
	boatRight = normalize(cross(boatUp,boatForward));
	boatForward = normalize(boatForward);

	boatPosition += .0*boatUp;
}

vec3 BoatToWorld( vec3 dir )
{
	return dir.x*boatRight+dir.x*boatUp+dir.x*boatForward;
    //return vec3(0.5,0.5,0.5);
}

vec3 WorldToBoat( vec3 dir )
{
	return vec3( dot(dir,boatRight), dot(dir,boatUp), dot(dir,boatForward) );
    //return vec3(0.5,0.3,0.1);
}

float TraceBoat( vec3 pos, vec3 ray )
{
	vec3 c = boatPosition;
	float r = 0.8;

	c -= pos;

	float t = dot(c,ray);

	float p = length(c-t*ray);
	if ( p > r )
		return 0.0;
	//return 0.4;
	return t-sqrt(r*r-p*p);
}


vec3 ShadeBoat( vec3 pos, vec3 ray )
{
	pos -= boatPosition;
	vec3 norm = normalize(pos);
	pos = WorldToBoat(pos);

	vec3 lightDir = normalize(vec3(-2,3,1));
	float ndotl = dot(norm,lightDir);

	// allow some light bleed, as if it's subsurface scattering through plastic
	vec3 light = smoothstep(-.1,1.0,ndotl)*vec3(1.0,.9,.8)+vec3(.06,.1,.1);

	// anti-alias the albedo
	float aa = 4.0/iResolution.x;

    // Цвет на шаре
    vec3 albedo = vec3(1.0,1.0,1.0);
    float sin_factor = sin(iTime/4.);
    float cos_factor = cos(iTime/4.);
    vec2 coord = mat2(cos_factor, sin_factor, -sin_factor, cos_factor) * vec2(pos.y,pos.x);
    float x = coord.x;
    float y = coord.y;
    if (x < -0.15 && y < -0.15) albedo = vec3(0.7,.0,.0);
    if (x < -0.15 && y > 0.15)  albedo = vec3(0.9,0.9,.0);
    if (x > 0.15 && y < -0.15)  albedo = vec3(0.0,0.0,0.8);
    if (x > 0.15 && y > 0.15)	albedo = vec3(0.0,0.9,0.0);

	vec3 col = albedo*light;

	// specular
	vec3 h = normalize(lightDir-ray);
	float s = pow(max(0.0,dot(norm,h)),100.0)*100.0/32.0;

	vec3 specular = s*vec3(1,1,1);

	vec3 rr = reflect(ray,norm);
	specular += mix( vec3(0,.04,.04), Sky(rr), smoothstep( -.1, .1, rr.y ) );

	float ndotr = dot(norm,ray);
	float fresnel = pow(1.0-abs(ndotr),5.0);
	fresnel = mix( .001, 1.0, fresnel );

	col = mix( col, specular, fresnel );

	return col;
}


float OceanDistanceField( vec3 pos )
{
	return pos.y - Waves(pos);
}

float OceanDistanceFieldDetail( vec3 pos )
{
	return pos.y - WavesDetail(pos);
}

vec3 OceanNormal( vec3 pos )
{
	vec3 norm;
	vec2 d = vec2(.01*length(pos),0);

	norm.x = OceanDistanceFieldDetail( pos+d.xyy )-OceanDistanceFieldDetail( pos-d.xyy );
	norm.y = OceanDistanceFieldDetail( pos+d.yxy )-OceanDistanceFieldDetail( pos-d.yxy );
	norm.z = OceanDistanceFieldDetail( pos+d.yyx )-OceanDistanceFieldDetail( pos-d.yyx );

	return normalize(norm);
}

float TraceOcean( vec3 pos, vec3 ray )
{
	float h = 1.0;
	float t = 0.0;
	for ( int i=0; i < 100; i++ )
	{
		if ( h < .01 || t > 100.0 )
			break;
		h = OceanDistanceField( pos+t*ray );
		t += h;
	}

	if ( h > .1 )
		return 0.0;

	return t;
}


vec3 ShadeOcean( vec3 pos, vec3 ray, in vec2 fragCoord )
{
	vec3 norm = OceanNormal(pos);
	float ndotr = dot(ray,norm);

	float fresnel = pow(1.0-abs(ndotr),5.0);

	vec3 reflectedRay = ray-2.0*norm*ndotr;
	vec3 refractedRay = ray+(-cos(1.33*acos(-ndotr))-ndotr)*norm;	
	refractedRay = normalize(refractedRay);

	const float crackFudge = .0;

	// reflection
	vec3 reflection = Sky(reflectedRay);
	float t=TraceBoat( pos-crackFudge*reflectedRay, reflectedRay );

	if ( t > 0.0 )
	{
		reflection = ShadeBoat( pos+(t-crackFudge)*reflectedRay, reflectedRay );
	}


	// refraction
	t=TraceBoat( pos-crackFudge*refractedRay, refractedRay );

	vec3 col = vec3(0,.04,.04); // under-sea colour
	if ( t > 0.0 )
	{
		col = mix( col, ShadeBoat( pos+(t-crackFudge)*refractedRay, refractedRay ), exp(-t) );
	}

	col = mix( col, reflection, fresnel );

	// foam
	col = mix( col, vec3(1), WaveCrests(pos,fragCoord) );

	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	ComputeBoatTransform();

	vec2 camRot = vec2(.5,.5)+vec2(-.35,4.5)*(iMouse.yx/iResolution.yx);
	vec3 pos, ray;
	CamPolar( pos, ray, vec3(0), camRot, 3.0, 1.0, fragCoord );

	float to = TraceOcean( pos, ray );
	float tb = TraceBoat( pos, ray );

	vec3 result;
	if ( to > 0.0 && ( to < tb || tb == 0.0 ) )
		result = ShadeOcean( pos+ray*to, ray, fragCoord );
	else if ( tb > 0.0 )
		result = ShadeBoat( pos+ray*tb, ray );
	else
		result = Sky( ray );

	// vignette effect
	result *= 1.1*smoothstep( .35, 1.0, localRay.z );

	fragColor = vec4(ToGamma(result),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y /= (iResolution.x / iResolution.y); // obey aspect ratio

    float paramRainDensity = 1.; //.3;    
    float paramTime = float(iFrame) / 10.;


    //
    // ---------------------------------------------
    //

    #define RainSpatialDensityX 45.
    #define RainSpatialDensityY 30.
    #define RainMaxDensity .4
    #define RainSpeed 7.
    #define DropletLength .9
    #define DropletWidth .08

    vec2 scaledUV = uv * vec2(RainSpatialDensityX, RainSpatialDensityY);

    // Calculate tile X coordinate
    float tileX = floor(scaledUV.x);

	// Offset Y based off time, and in two different ways to provide 
    // a sense of depth
    scaledUV.y += 
        paramTime 
        * RainSpeed 
        * (.8 + mod(tileX, 3.) * .1);

    // Offset Y randomly based off its tile X coordinate
    scaledUV.y += 407.567 * fract(351.5776456 * tileX);

    // Calculate tile Y coordinate
    float tileY = floor(scaledUV.y);

    // Decide whether tile is turned off
    float randOnOff = fract(sin(tileX * 71. + tileY * 7.));
    float onOffThickness = 1. - step(paramRainDensity * RainMaxDensity, randOnOff);

    //if (onOffThickness == 0.)
    //    discard;

    // Calculate coords within the tile
    vec2 inTile = fract(scaledUV);

    // Shuffle tile center X based on its tile coordinates
    float rand = fract(sin(77.7 * tileY + 7.7 * tileX));
    float tileCenterX = .5 + (abs(rand) - .5) * (1. - DropletWidth);

    // Distance from center of tile
    float xDistance = abs(tileCenterX - inTile.x);
    float yDistance = abs(.5 - inTile.y);

    // Thickness of droplet:
    //    > 0.0 only within Width
    //    ...with some tile-specific randomization
    float clampedXDistance = smoothstep(.0, DropletWidth, xDistance);
    float dropletThickness = (1. - clampedXDistance) * smoothstep(1. - DropletLength + rand/2.4, 1., 1. - yDistance);

    // TODOTEST
    vec2 dropletHead = vec2(tileCenterX, .2);
    dropletThickness = 1.0 * mix(
        dropletThickness, 
        1. - min(1., distance(inTile, dropletHead)),
        0.);
    //dropletThickness = 1. - min(1., distance(inTile, dropletHead));


    //
    // ---------------------------------------------
    //

    // Turning off tiles
    dropletThickness *= onOffThickness;

    //vec3 c = mix(vec3(.5, .7, .95), vec3(dropletThickness), smoothstep(.4, 1., dropletThickness));
    vec3 c = mix(vec3(.5, .7, .95), vec3(1.), smoothstep(.4, 1., dropletThickness));

    // Output to screen
    fragColor = vec4(c, 1.);
}

// Code by Flopine
// Thanks to wsmind, leon, XT95, lsdlive, lamogui, Coyhot, Alkama and YX for teaching me
// Thanks LJ for giving me the love of shadercoding :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and other to sprout :)  https://twitter.com/CookieDemoparty

#define PI 3.141592
#define ITER 100.

struct EoH
{
    float dist;
    vec3 color;
};

EoH struct_min(EoH a, EoH b)
{
   if (a.dist < b.dist)return a;
    else return b;
}

float stmin (float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5*(u+a+abs(mod(u-a+st,2.*st)-st)));
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float od (vec3 p, float d)
{  
    p.xz *= rot(iTime);
    return dot(p,normalize(sign(p)))-d;
}

float box(vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0., max(q.x,max(q.y, q.z))) + length(max(q,0.));
}

float plane (vec3 p, vec3 n, float dist)
{return dot(p,normalize(n))-dist;}


float crystal (vec3 p)
{
  p.xz *= rot(iTime);
  float b = box(p, vec3(.5, 0.7, 0.15));
  p = abs(p);
  float p1 = plane (p, vec3(0.6,1.,0.7), 0.5);
  float p2 = plane (p, vec3(1.,0.,0.8), 0.3);
  float d = max(p2,max(b,p1));
  return d;
}

EoH SDF (vec3 p)
{
    EoH magic = EoH(stmin(crystal(p),od(p,0.4),0.2,4.), vec3(.8,0.1,0.7));
    p.xy *= rot(iTime*0.5);
    EoH honesty = EoH(crystal(p+vec3(0.,-2.,0.)),vec3(0.8,0.3,0.));
    EoH generosity = EoH(crystal(p+vec3(2.,-.5,0.)), vec3(0.5,0.,0.6));
    EoH laughter = EoH(crystal(p+vec3(-2.,-.5,0.)), vec3(0.,0.5,0.8));
    EoH loyalty = EoH(crystal(p+vec3(1.,1.8,0.)), vec3(0.8,0.1,0.));
    EoH kindness = EoH(crystal(p+vec3(-1.,1.8,0.)), vec3(.8,0.4,0.7));   
    return struct_min(honesty, 
                      struct_min(generosity,
                      			struct_min(laughter,
                                           struct_min(loyalty, 
                                                      struct_min(kindness, magic)
                                                                )
                                          )
                                )
                     );
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(SDF(p).dist-vec3(SDF(p-eps.xyy).dist,
                                 SDF(p-eps.yxy).dist,
                                 SDF(p-eps.yyx).dist));
}

void crystal_shading (inout vec3 color, vec3 n, vec3 l, vec3 rd)
{
    // fresnel
    float fre = pow(clamp(1.-dot(-rd,n),0.,1.), 4.);
    color += fre* color*3.;

    // specular
    vec3 h = normalize(l-rd);
    float spe = pow(max(0.,dot(h,n)),7.);
    color += spe*0.8;
}

// courtesy of Alkama
vec3 pales (vec2 uv)
{
  uv *= rot(-iTime*.2);
  return floor(smoothstep(0.1, 0.2,cos(atan(uv.y, uv.x)*5.)))*vec3(0.4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0.,0.,-3.),
        p = ro,
        rd = normalize(vec3(uv,1.)),
        col = mix(vec3(0.5,0.,0.6),vec3(0.8,0.5,1.),pales(uv))*0.8;

    float shad = 0.;
    bool hit = false;

    for (float i=0.; i<ITER; i++)
    {
        float d = SDF(p).dist;
        if (d<0.001)
        {
            hit = true;
            shad = i/ITER;
            break;
        }
        p += d * rd;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        vec3 l = vec3(0.,6.,-6.);
        col = SDF(p).color;
        crystal_shading(col,n,l,rd);
        col *= 1.-shad;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Author: bitless
// Title: another triangular voronoi 
// Thanks to Patricio Gonzalez Vivo & Jen Lowe for "The Book of Shaders" and inspiration 
// Thanks to Inigo Quiles for his amazing articles
// "Smooth Voronoi"  http://www.iquilezles.org/www/articles/smoothvoronoi/smoothvoronoi.htm


vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

float sm_vr (in vec2 st) {
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    float c = 0.;
    for (int j=-1; j<=1; j++ ) {
        for (int i=-1; i<=1; i++ ) {
            vec2 neighbor = vec2(float(i),float(j));
            vec2 point = random2(i_st + neighbor);
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);
            vec2 diff = neighbor + point - f_st;
            float dist = length(diff)+(sin(iTime*length(random2(i_st + neighbor)))*0.25+0.25);
            c +=  exp( -16.*dist );
        }
    }
    return -(1.0/16.)*log(c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(0.135,0.077,0.095);

    // Scale
    st *= 5.;

    float c=0.;
    for (float i = 3.;i >= 0.; i--)   {
        float vr = sm_vr(st*pow(2.,i)+vec2(sin(iTime)+iTime,0.));
        vr = smoothstep(0.,1.5,vr*(sin(iTime+i)+4.25)*0.25);
        c = mix(c,vr,1.0-smoothstep(0.4,0.5,vr));
        c = (c + 0.325)*(1.-i*0.25);

    }
    color = vec3(0.425,0.110,0.103)*(c);

    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 O, in vec2 U )
{
    vec2 p = U / iResolution.y;
    p += vec2(-0.9, -0.5);
    p *= (1.5 + 0.1 * (cos(3. * iTime) + sin(2. * iTime)));

    float cosTime = cos(iTime);

    vec3 col = vec3 (1., 1., 1.);

    const int n = 7;
    const float pi = 4. * atan(1.);

    vec2 q[n];

    for (int i=0; i < n; i++) {
        q[i] = 0.4 * vec2(cos(iTime / 5. + 2. * float(i) * pi / float(n)),
                          sin(iTime / 5. + 2. * float(i) * pi / float(n)));
    }

    float r[n];

    for (int i=0; i < n; i++) {
        r[i] = 0.05 + 0.05 * sin(iTime / 2.) +
        0.05 * cos((float (i) + 2. + 3. * cosTime) * atan(p.y - q[i].y, p.x - q[i].x) + 4. * iTime);
    }

    for (int i = 0; i < n; i++) {
    	col *= smoothstep (r[i], r[i] + 0.01, length (p - q[i]));
    }

    vec3 bGround = vec3 (0.3 + 0.2 * cosTime,
                            0.4 + 0.2 * cosTime,
                            0.9 + 0.2 * cosTime);

    vec3 fGround = vec3 (2. * p.x, 2. * p.y, 0.5 + p.x * p.y);

    vec3 invCol = 1. - col;

    O = vec4(bGround * col + fGround * invCol,1.0);
}

// Anaglyph Structure
// Framed for https://fanzine.cookie.paris/
// Licensed under hippie love conspiracy
// Leon Denise (ponk) 2019.10.24
// Using code from Inigo Quilez

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float random (in vec2 st) { return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123); }

vec3 look (vec3 eye, vec3 target, vec2 anchor) {
    vec3 forward = normalize(target-eye);
    vec3 right = normalize(cross(forward, vec3(0,1,0)));
    vec3 up = normalize(cross(right, forward));
    return normalize(forward * .5 + right * anchor.x + up * anchor.y);
}


float map (vec3 pos) {
    float scene = 10.0;
    float r = 1.0;
    const float count = 7.0;
    for (float index = count; index > 0.0; --index)
    {
        pos.xz = abs(pos.xz)-1.5*r;
        pos.xz *= rot(0.4/r + iTime * 0.1);
        pos.yz *= rot(1.5/r + iTime * 0.05);
        pos.yx *= rot(.2/r + iTime * 0.05);
        scene = min(scene, length(pos.xy)-0.001);
        scene = min(scene, length(pos)-0.3*r);
        r /= 1.8;
    }
    return scene;
}

vec4 raymarch (vec3 eye, vec3 ray) {
    float dither = random(ray.xy+fract(iTime));
    vec4 result = vec4(eye, 0);
    float total = 0.0;
    float maxt = 20.0;
    const float count = 30.;
    for (float index = count; index > 0.0; --index) {
        result.xyz = eye + ray * total;
        float dist = map(result.xyz);
        if (dist < 0.001 + total * .002 || total > maxt) {
            result.w = index / count;
            break;
        }
        dist *= 0.9 + 0.1 * dither;
        total += dist;
    }
    result.w *= step(total, maxt);
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    vec3 eye = vec3(1.,0.5,-4.);
    vec3 at = vec3(0);
    vec3 ray = look(eye, at, uv);
    vec3 eyeoffset = 0.02*normalize(cross(normalize(at-eye), vec3(0,1,0)));

    vec4 resultLeft = raymarch(eye-eyeoffset, ray);
    vec4 resultRight = raymarch(eye+eyeoffset, ray);
    fragColor = vec4(resultLeft.w,vec2(resultRight.w),1);
}

// SHADERTOBER #01 Ring
// Poulet Vert 01/10/2019
// using code from Leon, iq

#define VOLUME 0.001
#define PI 3.14159

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }

float uSDF(float s1, float s2) { return min(s1, s2);}

float sSDF( float d1, float d2 ) { return max(-d1,d2); }

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdRing( vec3 p, vec2 t, float s)
{
    float c = 0.0;
    vec3 pr = p + vec3(0.0, -0.15, 0.0);
    pr.xy *= rot(PI/2.);
    float c1 = sdCappedCylinder(pr, t.x, t.y);
    float c2 = sdCappedCylinder(pr, t.x-s, t.y+s);
    return sSDF(c2,c1);
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); 
}



float map(vec3 pos)
{
    float t = 0.0;

    vec3 ringPos = pos + vec3(1.8, -1.15, 0.0);
    t = sdRing(ringPos, vec2(0.3, .05), .09);

    vec3 wallPos = pos + vec3(1.8, 0.0, 0.0);
    float rightWall = sdBox(wallPos, vec3(1.0, 8., 4.0));
    vec3 subWallPos = pos + vec3(0.0, -3.5, 0.0);
    rightWall = sSDF(sdBox(subWallPos, vec3(5.0, 2.5, 2.0)), rightWall);

    wallPos = pos + vec3(-3.0, 0.0, -4.0);
    float backgroundWall = sdBox(wallPos, vec3(5.0, 8.0, 1.0));

    t = uSDF(t, rightWall);
    t = uSDF(t, backgroundWall);

    return t;
}

float CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<64 ; i++)
    {
        float ray = map(ro + rd * t);

        if(ray < (0.0001*t))
        {
            return t;
        }

        t += ray;
    }

    return -1.0;
}

float GetShadow (vec3 pos, vec3 at, float k) {
    vec3 dir = normalize(at - pos);
    float maxt = length(at - pos);
    float f = 01.;
    float t = VOLUME*50.;
    for (float i = 0.; i <= 1.; i += 1./15.) {
        float dist = map(pos + dir * t);
        if (dist < VOLUME) return 0.;
        f = min(f, k * dist / t);
        t += dist;
        if (t >= maxt) break;
    }
    return f;
}

vec3 GetNormal (vec3 p) 
{ 
    vec2 e = vec2(0.01, 0.0); 
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
        )); 
}

vec3 Render(vec3 ro, vec3 rd, vec2 screenUV)
{
    float t = CastRay(ro, rd);

    vec3 col = vec3(0.0);

    if(t == -1.0)
    {
        col = vec3(rd.y);
        vec2 rain = screenUV;
        rain *= rot(PI/16.);
        rain.x *= 100.;

        float randRain = fract(sin(floor(rain.x)));
        col = vec3(step(fract(rain.x), .5)*(fract(rain.y+iTime*randRain)));
        col = mix(vec3(0.0, 0.1, 0.2), vec3(0.0, 0.44, .94), col.r);
        col *= .5;

    }
    else
    {
        vec3 pos = ro + rd * t;
        vec3 N = GetNormal(pos);
        vec3 L = vec3(-5.0, 5.0, 0.0);
        float light = dot(N,  L)*.2;

        col = vec3(.5);
        col *= light*.2 + .5;
        col *= GetShadow(pos, L, 4.);

    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 cp = vec3(3.0, 2.0, -4.0);
    cp.x += sin(iTime*0.05)*0.52;
    vec3 ct = vec3(1.0, 2.5, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = vec3(length(uv));
    vec2 screenUV = fragCoord.xy / iResolution.xy;
    col = Render(cp, vd, screenUV);

    // compositing
    col *= 1.-vec3(step(screenUV.x, .4)); // COMPOSITING CHEAT CODE



    col.x = .2*screenUV.x; // horizontal red ramp

    fragColor = vec4(col,1.0);
}

