float hash21 (vec2 x)
{return fract(sin(dot(vec2(22.44,54.5),x))*1245.5);}

// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float stmin(float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5*(u+a+abs(mod(u-a+st, 2.*st)-st)));
}

float cyl (vec2 p, float r)
{return length(p)-r;}

float od (vec3 p, float d)
{
    p.xz *= rot(iTime);
    return dot(p,normalize(sign(p)))-d;
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0., max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float pillars (vec3 p)
{
    float per = 3.;
    p.xz = mod(p.xz,per)-per*0.5;
    float c = cyl(p.xz, 0.45);
    p.y = abs(p.y)-3.;
    p.xz = mod(p.xz,per)-per*0.5;
    return min(od(p,0.5),c);
}

float ashes (vec3 p)
{
    float r = 0.2;
    float per = 0.5;
    vec3 id = floor(p/per);
    p.y -= iTime*hash21(id.xz*5.+1.5);
    p.x += texNoise(p.yz*0.5+iTime*0.1).r;
    p.z += texNoise(p.xy*0.8+iTime*0.1).r;
    p = mod(p, per)-per*0.5;
    return length(p)-r;
}

float SDF (vec3 p)
{
    return max(ashes(p),stmin(-box(p, vec3(15.,4., 15.)),pillars(p), 0.5, 4.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float dither = hash21(uv);

    vec3 ro = vec3(0.001,0.001,-5.),
        p = ro,
        rd = normalize(vec3(uv,1.)),
        col = vec3(0.);

    float shad = 0.;

    for (float i=0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.01)
        {
            shad = i/ITER;
            break;
        }
        d *= 0.9+dither*0.21;
        p+=d*rd;
    }

    float t = length(ro-p);

    col = vec3(shad);
	col = mix(col, vec3(0.1,0.05,0.05), 1.-exp(-0.006*t*t*texNoise(uv*0.5-vec2(0.,iTime*0.1)).r));
    // Output to screen
    fragColor = vec4(col,1.0);
}

