// Four bands of Spherical Harmonics functions (or atomic orbitals if you want). For
// reference and fun.

precision highp float;

// azimuthal quantum number
#define L 17

// magnetic quantum number
#define M 3

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 2   // antialias level (try 1, 2, 3, ...)
#endif

//#define SHOW_SPHERES

//---------------------------------------------------------------------------------

#define PI 3.1415926535898

// Clenshaw Legendre normalized
float Pgn(int l, int m, float x)
{
    float p0 = 0.;

    float p1 = 0.;

    float p2 = 0.;

    for (int k = l; k >= 0; k--)
	{
		float k1 = float(k + 1);
		float m1 = float(2 * m) + k1;
        float m2 = float(2 * (m + k) + 1);

		p2 = p1;
        p1 = p0;

		p0 = 0.;
        if (l == m + k)
            p0 += 1.;

        float u0 = sqrt(
			(m2 * (m2 + 2.0)) /
			(k1 * m1)
		);

        float u1 = sqrt(
            (k1 * m1 * (m2 + 4.0)) / 
			((k1 + 1.0) * (m1 + 1.0) * m2)
        );

        p0 += p1 * u0 * x;
        p0 += -u1 * p2;
	}

    for (int k = 1; k <= m; k++)
	{
		p0 *= sqrt(
            (1.0 - 0.5/float(k)) * (1.0 - x) * (1.0 + x)
        );
	}

    p0 *= sqrt((0.5 * float(m) + 0.25)/PI);

    return p0;
}

// Y_l_m(s), where l is the band and m the range in [-l..l] 
float SH( in int l, in int m, in vec3 s ) 
{
    vec3 ns = normalize(s);

    if (m < 0) {
        float c = ns.x;
        ns.x = ns.z;
        ns.z = c;
        m = -m;
    }

    // spherical coordinates
    float thetax = ns.y;
    float phi = atan(ns.z, ns.x);

    float pl = Pgn(l, m, thetax);

    float r = pow(-1.0, float(m)) * cos(float(m) * phi) * pl;

    return r;
}

vec3 map( in vec3 p )
{
    vec3 p00 = p;

	float r, d; vec3 n, s, res;

    #ifdef SHOW_SPHERES
	#define SHAPE (vec3(d-0.35, -1.0+2.0*clamp(0.5 + 16.0*r,0.0,1.0),d))
	#else
	#define SHAPE (vec3(d-abs(r), sign(r),d))
	#endif

	d=length(p00); n=p00/d; r = SH(L, M, n ); s = SHAPE; res = s;

	return vec3( res.x, 0.5+0.5*res.y, res.z );
}

vec3 intersect( in vec3 ro, in vec3 rd )
{
	vec3 res = vec3(1e10,-1.0, 1.0);

	float maxd = 10.0;
    float h = 1.0;
    float t = 0.0;
    vec2  m = vec2(-1.0);
    for( int i=0; i<200; i++ )
    {
        if( h<0.001||t>maxd ) break;
	    vec3 res = map( ro+rd*t );
        h = res.x;
		m = res.yz;
        t += h*0.1;
    }
	if( t<maxd && t<res.x ) res=vec3(t,m);


	return res;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.001,0.0,0.0);

	return normalize( vec3(
           map(pos+eps.xyy).x - map(pos-eps.xyy).x,
           map(pos+eps.yxy).x - map(pos-eps.yxy).x,
           map(pos+eps.yyx).x - map(pos-eps.yyx).x ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera
    float an = 10.0*iMouse.x/iResolution.x + iTime;
    float am = 2.*iMouse.y/iResolution.y - 1.;
    vec3  ro = vec3(1.5*sin(an),1.5*sin(am),1.5*cos(an)) * 1.0;
    vec3  ta = vec3(0.0,0.0,0.0);

    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 tot = vec3(0.0);

    #define ZERO min(iFrame,0)
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {        
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+vec2(float(m),float(n))/float(AA))) / iResolution.y;

        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

        // background 
        vec3 col = vec3(0.3) * clamp(1.0-length(p)*0.5,0.0,1.0);

        // raymarch
        vec3 tmat = intersect(ro,rd);
        if( tmat.y>-0.5 )
        {
            // geometry
            vec3 pos = ro + tmat.x*rd;
            vec3 nor = calcNormal(pos);
            vec3 ref = reflect( rd, nor );

            // material		
            vec3 mate = 0.5*mix( vec3(1.0,0.6,0.15), vec3(0.2,0.4,0.5), tmat.y );

            float occ = clamp( 2.0*tmat.z, 0.0, 1.0 );
            float sss = pow( clamp( 1.0 + dot(nor,rd), 0.0, 1.0 ), 1.0 );

            // lights
            vec3 lin  = 2.5*occ*vec3(1.0,1.00,1.00)*(0.6+0.4*nor.y);
                 lin += 1.0*sss*vec3(1.0,0.95,0.70)*occ;		

            // surface-light interacion
            col = mate.xyz * lin;
        }

        // gamma
        col = pow( clamp(col,0.0,1.0), vec3(0.4545) );
        tot += col;
    }
    tot /= float(AA*AA);
    fragColor = vec4( tot, 1.0 );
}

