#define PI 3.14192

float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(11.1,25.4)))*1245.54);}

void moda (inout vec2 p, float rep)
{
    float per = (2.*PI)/rep;
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a,per)-per*0.5;
    p = vec2(cos(a),sin(a))*l;
}

mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r, abs(p.z)-h);}

float cog (vec3 p, float in_r, float out_r)
{   
    float anim  = ((2.*PI)/3.) * (floor(iTime) + pow(fract(iTime),6.));
    p.xy *= rot(anim);
    float c1 = max(abs(cyl(p,in_r,0.25))-0.098, abs(p.z)-0.1);
    moda(p.xy, 5.);
    p.x -= in_r + out_r;
    float c2 = cyl(p.yzx, 0.1-p.x*0.2, out_r);
    return min(c1,c2);
}

float cogs (vec3 p)
{
    float i_r = 1.;
    float o_r = 0.5;
    float d = cog(p,i_r,o_r);
    for (int i=0; i<3; i++)
    {
        p = abs(p)-3.;
        i_r -= 0.2;
        o_r -= 0.1;
        p.xz *= rot(PI/4.);
        moda(p.xy, 5.);
        p.x -= (i_r + o_r)*2.;

        d = min(d,cog(p, i_r, o_r));
    }
    return d;
}

float SDF (vec3 p)
{
    p.xy *= rot(iTime*0.5);
    p.x += mix(0.,texture(iChannel0, p.yz).r,clamp(exp(-fract(iTime)*8.)*6.,0.,1.));
    float per = 20.;
    p.xy *= rot(p.z*0.05);
	p.z = mod(p.z, per)-per*0.5;
    return cogs(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float dither = hash21(uv); 

    // scanline effect from superplek
	vec3 weight_G = vec3(1., 0.7, 1.);
    vec3 weight_RB = vec3(0.7, 1., 0.7);
    vec3 weight = mix(weight_G, weight_RB, mod(floor(fragCoord.y*iTime*5.), 2.));


    vec3 ro = vec3(0.001,0.001,-10.+iTime),
        p = ro,
        rd = normalize(vec3(uv,1.)),
        col = vec3(0.);

    float shad = 0.; 
    bool hit = false;

    for (float i=0.;i<64.;i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            hit = true;
            shad = i/64.;
            break;
        }        
        d *= 0.6+dither*0.1;
        p += d*rd;
    }

	if (hit)
    {
        col = vec3(1.-shad)*weight;
    }

    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}

float PI = 3.14159265359;
float PI2 = 6.28318530717;


float whiteNoise(vec2 seed)
{
    return fract(sin(dot(seed, vec2(12.9898, 78.233))) * 43758.5453);
}


vec2 toPolar(vec2 uv, vec2 origin)
{
    uv -= origin;
    uv = vec2(
        length(uv),
        (atan(uv.y, uv.x) + PI) / PI2
    );

    return uv;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.y;

    vec2 screenCenter = vec2(iResolution.x / iResolution.y / 2.0, .5);
    uv = toPolar(uv, screenCenter);

    // defines how many rings
    uv.x *= 100.0;

    // shifts each ring randomly
    uv.y += whiteNoise(vec2(ceil(uv.x))) * iTime * .1;

    // brings the shift into the 0 to 1 range again
    uv.y = fract(uv.y);

    // defines how many trails are on one ring
    uv.y *= 3.0;

    // get a uniqe id for each trail
    vec2 trailId = ceil(uv);
    uv = fract(uv);

    // the mask defines the inner and outer radius and also the trail thickness
    float ringMask = step(20.0, trailId.x) * step(trailId.x, 50.0) * step(.8, uv.x);

    // defines by probability wich trails are golden
    float isGolden = step(.9, whiteNoise(trailId));

    // finally coloring the trails
    vec3 pixelColor = isGolden * vec3(0.83, 0.68, 0.21);
    pixelColor += (1.0 - isGolden) * vec3(.5);
    pixelColor *= ringMask;

    // this is for a fading the ending of the trails
    pixelColor *= 1. - pow(uv.y, 2.0);



    fragColor = vec4(pixelColor, 1.0);
}

#define MAX_DIST 500.
#define T iTime*20.
mat2 rot(float x){return mat2(cos(x),-sin(x),sin(x),cos(x));}

float box(vec3 p, vec3 d)
{
  vec3 q = abs(p) - d;
  return min(max(q.x,max(q.y,q.z)),0.0)+length(max(q,0.0));
}

float g;
float map(vec3 p)
{
    float r=0.;

    float hw = 4.;
    float hh = 5.;
    float cz = cos(p.z/15.);
    p.xy*=rot(cos(p.z/100.)/5.);
    float b = box(p-vec3(0,-3.75-cz,T+10.+cos(iTime)*.5),vec3(1.5,2.-(p.z-T)*.1,3));
    b=max(b,-box(p-vec3(0,-1.75-cz,T+10.+cos(iTime)*.5),vec3(1.,2.8,2.)));
    g+=0.01/(0.01+b*b);
    float f = p.y+hh+cz+smoothstep(-1.,.5,cos(clamp(p.x*3.,-5.,5.))*3.);
    float rw = dot(p+vec3(hw,0,0),normalize(vec3(1,0,0)));
    float lw = dot(p-vec3(hw,0,0),normalize(vec3(-1,0,0)));
    float tw = dot(p-vec3(0,hh-cos(p.z/20.),0),normalize(vec3(0,-1,0)));
    b*=.9;
    f*=.5;
    r=min(f,rw);
    r=min(r,lw);
    r=min(r,tw);
    r=min(r,b);
    return r;
}
float ray(vec3 ro, vec3 rd)
{
    float t=0.;
    for(int i=0;i<128;i++)
    {
        vec3 p = ro+rd*t;
        float s = map(p);
        if(s<0.01)break;
        t+=s;
        if(t>MAX_DIST){t=-1.;break;}
    }
    return t;
}

vec3 normal(vec3 p)
{
    vec2 e = vec2(0.0001,0);
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
        ));
}

vec3 cam(vec3 ro, vec3 ta, vec2 uv)
{
    vec3 cf = normalize(ta-ro);
    vec3 cu = normalize(cross(cf,vec3(0,1,0)));
    vec3 cr = normalize(cross(cu,cf));
    return normalize(uv.x*cu+uv.y*cr+2.*cf);
}


void mainImage( out vec4 c, in vec2 f )
{
    vec2 uv = (2.*f-iResolution.xy)/iResolution.y;

    float h = -1.75;
    vec3 ro = vec3(0,2.+h+cos(T/15.-10.),T-10.);
    vec3 ta = vec3(0,h+cos(T/15.-9.),T);
    vec3 rd = cam(ro,ta,uv);

    float r = ray(ro,rd);

    vec3 col = vec3(0);

    if(r>0.)
    {
        vec3 p = ro+rd*r;
        vec3 n = normal(p);
        vec3 sun = normalize(vec3(0.,0.1,.3));
        float dif = clamp(dot(n,sun),0.,1.);

        col=vec3(1.)*dif+r/300.+g/100.;
    }
	c.rgb=col;
}

#define iTime .5*iTime
float hash(in vec3 p)
{
	ivec3 q = ivec3(p*5000.);
    int h = 15*q.x ^ q.y ^ q.z;
    return fract(713.*sin(float(h)));
}

float noise(in vec3 p)
{
    vec3 F = floor(p);
    vec2 o = vec2(0,1);
    vec3 f = smoothstep(0.,1.,fract(p));
    float r1 = mix(hash(F), hash(F+o.yxx), f.x);
    r1 = mix(r1,mix(hash(F+o.xyx),hash(F+o.yyx), f.x),f.y);
    float r2 = mix(hash(F+o.xxy), hash(F+o.yxy), f.x);
    r2 = mix(r2,mix(hash(F+o.xyy),hash(F+o.yyy), f.x),f.y);
    return mix(r1,r2,f.z);
}

float fbm(in vec3 p)
{
    float s = 1.;
    float a = 1.;
    float g = 2.;
    float A = 0.;
    float r = 0.;
    for(int i = 0; i<3; i++){
        r += a*noise(1.2*float(i)+p*s);
        A += a;
        a /= g;
        s *= g;
    }
    return r/A;
}

vec3 fbm3(in vec3 p)
{
    return vec3(fbm(p-10.3),fbm(p),fbm(p+10.3));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;

    vec3 col = vec3(0);
    float t = iTime*.1;

    t *= (.5+.5*smoothstep(.0,1.,length(uv)));

    mat2 rot = mat2(cos(t),sin(t),-sin(t),cos(t));

    uv *=rot;

    vec2 rad = vec2(atan((uv.y/uv.x)), length(uv));

    int rf = int(abs(floor(6.*rad.x/3.16)));
    rad.x = fract(6.*rad.x/3.16);
    if(rf%2==0) rad.x = 1.-rad.x; 
    rad.x *= 3.16/6.;

    vec3 p =  vec3(rad.x,16.*rad.y-1.5*iTime,.5*iTime);

    t = -iTime*.001;
    mat2 rot2 = mat2(cos(t),sin(t),-sin(t),cos(t));

    p.xy *= rot2;

    vec3 q = fbm3(vec3(p.xy,p.z));
    vec3 r = .5*fbm3(2.*(p+q));
    vec3 s = .2*fbm3(8.*(p+q+r));

    col += pow(fbm3(p+s)*fbm(r+.3*s)*3.5,vec3(.8,1.5,1.2));

    col *= fbm(p)*1.5;

    //col *= pow(fbm(p+s)*1.5,2.2);

    col *= 1.+smoothstep(.05,.0,rad.y);

    float vig = smoothstep(1.,.2,rad.y);
    col = pow(col, vec3(vig));
    col *= vig;

    fragColor = vec4(col,1.0);
}

