{
    vec2 P = U/iResolution.xy;
    O = fract( ( P.x < .5 
                   ? texelFetch( iChannel1, ivec2(U)%256,  0 )
                   : texelFetch( iChannel0, ivec2(U)%1024, 0 )
                ) + float(iFrame)/256. ).xxxx; // col: DEL .xxxx

  //O = mix( texture(iChannel2, P), texture(iChannel3, P), O ); // try mixing
}

