float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(16.4,34.5)))*1212.4);}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+ length(max(q,0.));
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r, abs(p.z)-h);}

float well (vec3 p)
{
    p.y = abs(p.y)-2.;
    p.xz = fract(p.xz)-0.5;
    return max(abs(p.y)-0.25,abs(cyl(p.xzy, .45,1.))-0.05);
}

vec2 s_id;
float g1 = 0.;
float spheres (vec3 p)
{ 
    s_id = floor(p.xz);

    float anim = (mod(s_id.x,2.) == 0.)? iTime*hash21(s_id)*0.5 : -iTime*hash21(s_id)*0.5;
    p.y -= anim;
   	p.xz = fract(p.xz)-.5;

    p.y = fract(p.y)-.5;

   	float d = length(p)-0.2;
    g1 += 0.01/(0.01+d*d);
    return d;
}

float SDF (vec3 p)
{
    return min(-box(p,vec3(7.,2.5,5.)),min(spheres(p),well(p)));
}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
    vec3 f = normalize(tar-ro);
    vec3 l = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = normalize(cross(f,l));
    return normalize(f + l*uv.x + u*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    float dither = hash21(uv);

   	vec3 ro = vec3(0.8,0.3,-3.5),
        p = ro,
        rd = getcam(ro, vec3(0.),uv),
        col = vec3(0.);

    float shad = 0.;

    for (float i=0.; i<64.; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            shad = i/64.;
            break;
        }
        d *= 0.9+dither*0.1;
        p+=d*rd;
    }

    col = vec3 (shad)*0.2;
    vec3 c1 = clamp(vec3(hash21(s_id),hash21(s_id*0.2),1.),0.,1.);
    vec3 c2 = clamp(vec3(1.,hash21(s_id),hash21(s_id)*0.1),0.,1.);
    vec3 fc = mod(s_id.x, 2.) == 0. ? c1 : c2;
	col += g1*fc*0.02*exp(-fract(iTime));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Code by Flopine
// Thanks to wsmind, leon, XT95, lsdlive, lamogui, Coyhot, Alkama and YX for teaching me
// Thanks LJ for giving me the love of shadercoding :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and others to sprout :)  https://twitter.com/CookieDemoparty

#define PI 3.141592

vec2 rand (vec2 x)
{return fract(sin(vec2(dot(x, vec2(1.2,5.5)), dot(x, vec2(4.54,2.41))))*4.45);}

float hash11 (float x)
{return fract(sin(x*45.15)*124.5);}

float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(12.45,43.158)))*1245.5);}

mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float moda (inout vec2 p, float rep)
{
    float per = 2.*PI/rep;
    float a= atan(p.y,p.x);
    float l = length(p);
    float id = floor(a/per);
    a = mod(a, per)-per*0.5;
    p = vec2(cos(a),sin(a))*l;
    if (abs(id) >= (rep/2.)) id = abs(id);
    return id;
}

float stmin(float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b),0.5*(u+a+abs(mod(u-a+st,2.*st)-st)));
}

// voronoi function which is a mix between Book of Shaders : https://thebookofshaders.com/12/?lan=en
// and iq article : http://www.iquilezles.org/www/articles/voronoilines/voronoilines.htm
vec3 voro (vec2 uv)
{
    vec2 uv_id = floor (uv);
    vec2 uv_st = fract(uv);

    vec2 m_diff;
    vec2 m_point;
    vec2 m_neighbor;
    float m_dist = 10.;

    for (int j = -1; j<=1; j++)
    {
        for (int i = -1; i<=1; i++)
        {
            vec2 neighbor = vec2(float(i), float(j));
            vec2 point = rand(uv_id + neighbor);
            point = 0.5+0.5*sin(2.*PI*point+iTime);
            vec2 diff = neighbor + point - uv_st;

            float dist = length(diff);
            if (dist < m_dist)
            {
                m_dist = dist;
                m_point = point;
                m_diff = diff;
                m_neighbor = neighbor;
            }
        }
    }

    m_dist = 10.;
    for (int j = -2; j<=2; j++)
    {
        for (int i = -2; i<=2; i++)
        {
            if (i==0 && j==0) continue;
            vec2 neighbor = m_neighbor + vec2(float(i), float(j));
            vec2 point = rand(uv_id + neighbor);
            point = 0.5+0.5*sin(point*2.*PI+iTime);
            vec2 diff = neighbor + point - uv_st;
            float dist = dot(0.5*(m_diff+diff), normalize(diff-m_diff));
            m_point = point;
            m_dist = min(m_dist, dist);
        }
    }

    return vec3(m_point, m_dist);
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0., max(q.x,max(q.y,q.z))) + length(max(q,0.));
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r, abs(p.z)-h);}

float od (vec3 p, float r)
{return dot(p,normalize(sign(p)))-r;}

float g1 = 0.;
float room (vec3 p)
{
    p.y -= 5.;
    p.y -= step(0.05,voro(p.xz).z)*0.05;
    p.y += sin(length(p.xz*1.5)-iTime)*0.1;

    p.x -= step(0.05,voro(p.yz).z)*0.05;
    p.x += sin(length(p.yz*1.5)-iTime)*0.1;

    float d = -box(p, vec3(10.,8.,15.));
    g1 += 0.1/(0.1+d*d);
    return d;
}

float t_id;
float g2 = 0.;
float tentacles (vec3 p)
{
    p.xz *= rot(sin(iTime+p.y*0.8)*0.5);
    t_id = moda(p.xz, 7.);

    p.x -= 2.;
    float d = cyl(p.xzy, 0.15 - p.y*0.1,3.);
    g2 += 0.1/(0.1+d*d);
    return d;
}


float gem (vec3 p)
{
    p.y -= 1.5;
    p.xz *= rot(iTime);
    p.xy *= rot(iTime);
    p.y += sin(iTime)*0.5+0.5;
    return stmin(od(p, 1.),box(p,vec3(0.9)),0.5,3.);
}

float SDF (vec3 p)
{
    return min(gem(p),min(room(p),tentacles(p)));
}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
    vec3 f = normalize(tar-ro);
    vec3 l = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = normalize(cross(f,l));
    return normalize(f + l*uv.x + u*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
    uv.x *= iResolution.x/iResolution.y;

    float dither = hash21(uv);

    vec3 ro = vec3(0.001,2.,-5.5),
        tar = vec3(0.,0.,0.),
        rd = getcam(ro, tar, uv),
        p = ro,
        col = vec3(0.);

    float shad = 0.;

    for (float i=0.; i<64.;i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            shad = i/64.;
            break;
        }
        d *= 0.9+dither*0.1;
        p += d*rd;
    }

    float t = length(ro-p);

    col = vec3(shad);
    col += g1 * step(voro(p.xz+p.yz).z,0.05) * vec3(0.,0.3,0.4);
    col += g2 * vec3(hash11(t_id)*0.1,1.,hash11(t_id))*0.2;

    col = mix(col, vec3(0.2,0.2,0.3), 1.-exp(-0.018*t*t));

    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359

float insideTriangle(vec2 uv, float h) {
    return step(max(-uv.y, dot(vec2(abs(uv.x), uv.y), vec2(sqrt(3.0)/2.0, .5))), h);
}

float deg2rad(float deg) {
    return deg*PI/180.0;
}

mat2 rot(float rad) 
{
    float s = sin(rad);
    float c = cos(rad);
    return mat2(c, s, -s, c);
}

float sn(float x) {
    return sin(x) / 2. + .5;
}

vec4 getColor(vec2 uv, float time)
{
    float z = mod(time, 1.0) + 1.0;
    uv *= pow(2.0, z);
    float inside = 0.0;
    float x = length(uv);
    float i = -floor(log2(x + 0.001));
    float c = pow(2.0, -i+1.0);
    uv /= c;
    float lvl = i;
    i -= floor(time);

    uv = rot(i*deg2rad(15.0) + time*.5) * uv;
    //return vec4(vec3(inside), 1.0);
    float iR = insideTriangle(uv, .3 + sn(time*.7) * .23);
    float depth = min((-lvl + 6.0 - fract(time)) * .2, 1.0);
    return vec4(depth * iR);
}

vec2 frag2uv(vec2 f) 
{
    vec2 r = iResolution.xy;
    return (f / r * 2.0 - 1.0) * vec2(max(r.x/r.y, 1.0), max(r.y/r.x, 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = frag2uv(fragCoord);
    fragColor = getColor(uv, iTime) + getColor(uv, iTime*.8 + 3.) + getColor(uv, iTime*1.2 + 6.);
}

// Andi Smithers 
// button shader for smooth border edges
// includes shadow both internal and drop shadow
// 9 patch is basically preserving the edge with rectangle and doesnt stretch the image
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 0.6666666, 0.3333333, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float progress = mod(iTime, 1.0);

    vec4 backCol = vec4(1,1,1,1);
    // uniforms
    float borderSize = 0.05;
    float antiAliasSize = 0.03;
    vec4 borderColor = vec4(1.0,1.0,0.95, 1);
    vec4 centreColor = vec4(0.1,0.5,0.9,1);
    float wobbly = iTime;
	float rounding = sin(wobbly)*3.0+4.0;
    vec2 shadowVec = vec2(-0.01, 0.05);
    float scaleBox = 1.2;

    // alias thickness
    float aascalar = 1.0 / antiAliasSize;
    float outerAlias = 1.0 - antiAliasSize;

    // convert UV into normalized coord
    vec2 iuv = fragCoord.xy / iResolution.xy;
	vec2 uv = fragCoord.xy / iResolution.y;

    // compute 9patch centre - xaxis
    float centre = (iResolution.x - iResolution.y)/iResolution.x * 0.5 ;
    if (abs(iuv.x-0.5)<centre) uv.x=0.5;
    if(iuv.x>centre+0.5) uv.x-=0.5 + (iResolution.y/iResolution.x) * centre*2.0;

    vec2 uvUnit = (uv*2.0-1.0) * scaleBox;
    vec2 shadowUnit = uvUnit + shadowVec;

    // rounded edge - have to abs the value incase shader doesnt support negative powers
    uvUnit = pow(abs(uvUnit), vec2(rounding));
    shadowUnit = pow(abs(shadowUnit), vec2(rounding));

    // compute border
    float borderEdge = pow(1.0-borderSize, rounding);
    float borderAlias = borderEdge - antiAliasSize;

    // edge
    float edge = length(uvUnit);
    float outer = 1.0-(clamp(edge, outerAlias, 1.0)-outerAlias) * aascalar;
    float inner = 1.0-(clamp(edge, borderAlias, borderEdge)-borderAlias) * aascalar;
    float shadowInner = 1.0-(clamp(length(shadowUnit), borderAlias, borderEdge)-borderAlias) * aascalar;
    float shadowOuter = 1.0-(clamp(length(shadowUnit), outerAlias, 1.0)-outerAlias) * aascalar;

    // blow out outer then chose minimum this ensures a mask around the alpha output
    float outermask = min(outer*1000.0, 1.0);

    // fetch image and poor mans chromakey alpha
    vec4 barCol = vec4(hsv2rgb(vec3(progress*0.33333 - 0.1, 1.0, 1.0)), 1.0);
    float blend = ((iuv.x - progress) <= 0.0 ? 1.0 : 0.0);
    vec4 image = blend*barCol + backCol*(1.0-blend);


    //image*= texture(iChannel0, iuv); if you want it textured
    float alpha = length(image.rgb-vec3(0,1,0));
    alpha = alpha> 0.5 ? 1.0 : 0.0;

    // shadow
    float shade = shadowInner<0.9 ? 0.8 + shadowInner*0.2: 1.0;
    float shadeOut = shadowOuter>0.5 ? 0.0 + shadowOuter*0.2: 0.0;	

    // blend inner/outer and image with image alpha
    vec4 color = (centreColor*inner*(1.0-alpha)+image*alpha*inner)*shade + borderColor*(1.0-inner)*outermask;
    color.a =  max(outer, shadeOut);

    // blend against a background texture - because shadertoy
    vec4 background = texture(iChannel1, iuv);

    // and set the final color 
    fragColor = (color*color.a) + background*(1.0-color.a);
}

#define MAXSTEPS 50
#define HITTHRESHOLD .009
#define FAR 25.
#define AA 1
#define NIFS 2
#define SCALE 2.3
#define TRANSLATE 3.5

mat2x2 rot(float angle)
{
    float c=cos(angle);
    float s=sin(angle);
    return mat2x2(c,-s,
    s,c);
}

vec4 sd2d(vec2 p,float o)
{
    float time=.25*o+.6*iTime;
    float s=.5;
    p*=s;
    float RADIUS=(1.5+sin(iTime));
    int i;
    vec3 col;
    p=p*rot(-.4*time);// twist

    for(i=0;i<NIFS;i++)
    {
        if(p.x<0.){p.x=-p.x;col.g++;}
        p=p*rot(.9*sin(time));
        if(p.y<0.){p.y=-p.y;col.b++;}
        if(p.x-p.y<0.){p.xy=p.yx;col.r++;}
        p=p*SCALE-TRANSLATE;
        p=p*rot(.3*(iTime));
    }

    float d=.425*(length(p)-RADIUS)*pow(SCALE,float(-i))/s;
    col/=float(NIFS);
    vec3 oc=mix(vec3(.7,col.g,.2),vec3(.2,col.r,.7),col.b);

    return vec4(oc,d);
}

vec4 map(vec3 p)
{
    return sd2d(p.xz,p.y);
}

float shadow(vec3 ro,vec3 rd)
{
    float h=0.;
    float k=3.;//shadowSmooth
    float res=1.;
    float t=.2;//bias
    for(int i=0;t<15.;i++)// t < shadowMaxDist
    {
        h=map(ro+rd*t).w;
        res=min(res,k*h/t);
        if(h<HITTHRESHOLD)
        {
            break;
        }
        t=t+h;
    }
    return clamp(res+.05,0.,1.);
}
