void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//-----------------
//camera
float height=-.4;
float rot=iTime*.1;
float dist=9.+1.*sin(.5*iTime);
vec3 ro=dist*vec3(cos(rot),height,sin(rot));
vec3 lookAt=vec3(0.,0.,0.);
vec3 fw=normalize(lookAt-ro);

vec3 right=normalize(cross(vec3(0.,1.,1.),fw));
vec3 up=normalize(cross(fw,right));
right=normalize(cross(up,fw));

//light
rot+=sin(iTime)*.2;
vec3 lightPos=dist*vec3(cos(rot),height,sin(rot));

//raymarch
vec3 pos,closest;
float t;
float smallest;
int i,iAtClosest;
vec3 sdfCol;
vec3 col;

for(int x=0;x<AA;x++)
for(int y=0;y<AA;y++)
{
    t=0.;smallest=500.;
    vec2 o=vec2(float(x),float(y))/float(AA)-.5;
    vec2 uv=(fragCoord+o)/iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 rd=normalize(fw*.5+right*uv.x+up*uv.y);

    for(i=0;i<MAXSTEPS;i++)
    {
        pos=ro+rd*t;
        vec4 mr=map(pos);
        float d=mr.w;
        if(d<smallest)smallest=d;closest=pos;iAtClosest=i;sdfCol=mr.rgb;
        if(abs(d)<HITTHRESHOLD||t>FAR){break;}
        t+=d;
    }
    pos=closest;
    i=iAtClosest;
    vec3 c;
    if(t<FAR)
    {
        c=sdfCol;
        vec3 toLight=normalize(lightPos-pos);
        float s=shadow(pos,toLight);
        c*=s;
        c=mix(c,1.5*c,1.-s);
    }
    else
    {
        c=vec3(0.);
    }
    col+=c;
}
col/=float(AA*AA);


    fragColor = vec4 (col,t);
}

// Let's write a plasma!
//
// http://en.wikipedia.org/wiki/Plasma_effect
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = vec2(fragCoord.xy / iResolution.xy);
	vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
	float t = iTime;
    r = r * 8.0;

    float v1 = sin(r.x +t);
    float v2 = sin(r.y +0.00178*t*t);
    float v3 = sin(r.x+r.y +t);
    float v4 = sin(length(r) +1.7*t);
	float v = v1+v2+v3+v4;

	vec3 ret;
	if(p.x < 0.1/10.) { 
		ret = vec3(v1);
	} 
	else if(p.x < 1./10.) { 
		ret = vec3(v2);
	} 
	else if(p.x < 2./10.- 0.15*sin(iTime/3.)) { 
		ret = vec3(v);
	}	
	else if(p.x < (6./10. + 0.35*cos(iTime/2.))) { 
		ret = vec3(sin(0.05*0.5*t*t*vec3(0.4,0.77,0.34)*v));
	}
	else if(p.x < 10./10.) { 
		ret = 2.*vec3(sin(v)+0.25, sin(v+0.25*3.14), sin(v+1.0*3.14));
	}	

	ret = 0.5 +0.5*ret;

    vec3 pixel = ret;
    fragColor = vec4(pixel, 1.);
}

vec2 normz(vec2 x) {
    return x == vec2(0.0, 0.0) ? vec2(0.0, 0.0) : normalize(x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime*1.0;
	vec2 uv = ( gl_FragCoord.xy / iResolution.xy )*4.0;

	float i0=1.2;
	float i1=0.95;
	float i2=1.5;
	vec2 i4=vec2(0.0,0.0);
	for(int s=0;s<4;s++)
	{
		vec2 r;
		r=vec2(cos(uv.y*i0-i4.y+time/i1),sin(uv.x*i0+i4.x+time/i1))/i2;
		r+=vec2(-r.y,r.x)*0.2;
		uv.xy+=r;

		i0*=1.93;
		i1*=1.25;
		i2*=1.7;
		i4+=r.xy*1.0+0.5*time*i1;
	}
	float r=sin(uv.x-time)*0.7+0.5;
	float b=sin(uv.y+time)*0.5+0.5;
	float g=sin((sqrt(uv.x*uv.x+uv.y*uv.y)+time))*0.5+0.5;
	vec3 c=vec3(r,g,b);
    if (iMouse.z > 0.0) {
        vec2 d = gl_FragCoord.xy - iMouse.xy;
        float m = exp(-length(d) / 50.0);
        c.xy += m * normz(d);
    }
	fragColor = vec4(c,1.5);
}

// Revisiting those objects with new tools is amazing to me !
// In the 80's, I had to wait about 20' to get a single frame :)

// Before clicking the mouse, what you see is the Julia set
// transforming itself while the c parameter moves along roughly
// the boundary of the Mandelbrot set.
// After clicking the mouse, interactive mode is entered.
// Coming back to demo mode is achieved by clicking near
// the bottom-left corner.

const float pi = 4. * atan(1.);

// map : z --> z**2 + c
vec2 f(vec2 c, vec2 z) {
    //return vec2(z.x * z.x - z.y * z.y + c.x, 2. * z.x * z.y + c.y);
    return mat2(z, -z.y, z.x) * z + c;
}


// Computes the so-called "escape-time"
int iter (vec2 c, vec2 z, int n, float threshold) {
    vec2 zn = z;
    int k = 0;
    while ((k <= n) && (length(zn) < threshold)) {
     	zn = f(c, zn);
        k++;
    }
    return k;
}


// Computes x**n
float ipow(float x, int n) {
    float y = 1.;
    for (int k = 0; k < n; k++) {
        y *= x;
    }
    return y;
}


void mainImage( out vec4 O, in vec2 U )
{
    O = vec4(0.);
    vec2 z = 5. * (U / iResolution.x - vec2(0.5,0.3));

    vec2 c;
    float escape;

    // Computes the Mandelbrot set
    escape = float(iter(z,vec2(0.),256, 2.)) / 256.;
    vec4 MSet = vec4(vec3((1. - escape) / 4.), 1.);

    if (length (iMouse.xy) < 20. ) {
        float t = iTime;
        int n = int(floor(t / pi));
        if (n % 6 == 0) { // upper half cardioid
        	c = 0.5 * vec2(cos(t), sin(t)) - 0.25 * vec2(cos(2.*t), sin(2.*t));
        }
        else if (n % 6 == 1) { // upper half disk
            c = vec2(-1. + 0.25 * cos(t - pi), 0.25 * sin(t - pi));
        }
        else if (n % 6 == 2) { // segment -1.75 to -2
            c = cos(t / 2.) * cos(t / 2.) * vec2(-1.25, 0.) +
                sin(t / 2.) * sin(t / 2.) * vec2(-2., 0.) +
                vec2 (0., 0.001);
        }
        else if (n % 6 == 3) { // segment -2 to -1.75
            c = cos(t / 2.) * cos(t / 2.) * vec2(-1.25, 0.) +
                sin(t / 2.) * sin(t / 2.) * vec2(-2., 0.) +
                vec2 (0., -0.001);
        }
        else if (n % 6 == 4) { // lower half disk
            c = vec2(-1. - 0.25 * cos(t), -0.25 * sin(t));
        }
        else { // lower half cardiod
            c = 0.5 * vec2(cos(t), sin(t)) - 0.25 * vec2(cos(2.*t), sin(2.*t));
        }
        // shows c as a small red disk
        float dist_to_c = smoothstep (0.1, 0., length(z - c));
        vec4 red_spot = vec4(dist_to_c, 0., 0., 0.);

        // julia_set
        escape = float(iter(c, z, 256, 2.)) / 256.;
        vec4 jul = vec4 (mix (vec3(0.), vec3(.4,.6,1.), escape), 1.0);

        // alltogether
    	O = jul + MSet + red_spot;
    }
    else {
        c = 5. * (iMouse.xy / iResolution.x - vec2(0.5,0.3));
        escape = float(iter(c, z, 256, 2.)) / 256.;
        vec4 jul = vec4 (mix (vec3(0.), vec3(.4,.6,1.), escape), 1.0);
    	O = jul + MSet;
    }
}

vec3 fade(vec3 fg, vec3 bg, float alpha) {
    return mix(bg, fg, alpha);
    return bg*(1.0-alpha) + fg*(min(1.0,alpha));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = 0.0;
    float h = iResolution.y;
    float w = iResolution.x;
    float distort = abs(sin(iTime/10.0));
    float scale = h/450.0;

    vec2 uv = fragCoord/iResolution.xy;
	float mic = texture( iChannel0, vec2(uv.x,1.0)).x;
    vec3 col = vec3(0.15,0.0,0.15)*(uv.y+0.05)*distort;

    //sun drop
    float distSun = distance(fragCoord, iResolution.xy*0.5-vec2(0.0,(-50.0)));
    if (distSun < h*(2.5) && distSun > h*0.0) {
        col = fade(col, vec3(0.4,0.24,0.4), distSun/h*(distort*6.0));
    }

    //sun
    if (distance(fragCoord, iResolution.xy*0.5-vec2(0.0,10.0)) < h*max(0.25+(distort/4.0), 0.1)) {
        if (fragCoord.y < h/2.0 - 1.5*scale || fragCoord.y > h/2.0 + 3.0*scale) {
            if (fragCoord.y < h/2.0 + 5.0*scale || fragCoord.y > h/2.0 + 22.0*scale) {
                if (fragCoord.y < h/2.0 + 25.5*scale || fragCoord.y > h/2.0 + 35.5*scale) {
                    if (fragCoord.y < h/2.0 + 42.0*scale || fragCoord.y > h/2.0 + 47.0*scale) {
                        if (fragCoord.y < h/2.0 + 53.0*scale || fragCoord.y > h/2.0 + 55.5*scale) {
                            if (fragCoord.y < h/2.0 + 62.0*scale || fragCoord.y > h/2.0 + 64.5*scale) {
                                float maxSun = max(distort, 0.25);
                                col = vec3(maxSun, maxSun, maxSun-0.2)*(uv[1]*5.0);

                            }
                        }
                    }
                }
            }
        }
   	}

    // waves
    if (fragCoord.y*0.5 < cos(fragCoord.x*0.01)+h*(max(0.25, 0.225+mic/10.0))) {
        col = vec3((1.0-distort)*0.3, 0.0, (1.0-distort)*0.3);
		//vec3 lineCol = 0.9-(uv.y*(min(0.6, max(0.5, distort)))*2.0)-vec3(distort, 0.85, 0.2);
        vec3 lineCol = 0.7-(1.0*uv.y*(min(0.5, max(0.3, distort/2.0))))-vec3(distort/5.0, 0.65, 0.1);
        float roadMod = 0.7 + (mic*2.0);

        // fg vert
        //if (uv.x > (0.498+uv.y/250.0) && uv.x < (0.503-uv.y/250.0) && mod(uv.y+mod(iTime/5.0, 0.4), 0.15) > 0.1) {
        if (uv.x > (0.498+uv.y/250.0) && uv.x < (0.503-uv.y/250.0)) {
			col = lineCol * roadMod;
        } else if (mod(fragCoord.x-w/2.0, h/1.89-fragCoord.y) < 2.25 && (uv.x < 0.25+uv.y/2.0 || uv.x > 0.79-uv.y/1.76)) {
            col = lineCol;
        }

		// fg hoz
        float c = 0.5*pow(0.5, (uv.y-0.1)*10.0);
        float s = 0.625*pow(2.0, (uv.y-0.1)*10.0);
        float d = min(0.02, c/8.0);
        if (mod(uv.y+mod(iTime/s, c), c) < d && (uv.x < 0.2+uv.y/1.75 || uv.x > 0.8-uv.y/1.76)) {
        	col = lineCol;
        }
        if (mod(uv.y+mod(iTime/s, c), c) < d && uv.x > 0.2+uv.y/1.75 && uv.x < 0.8-uv.y/1.76) {
            col = lineCol * roadMod;
        }

    }

    fragColor = vec4(col,a);
}

float makeBand(float d, float start, float end, float blur)
{
    float band1 = smoothstep(start - blur, start + blur, d);
    float band2 = smoothstep(end + blur, end - blur, d);
    return band1*band2;
}

float makeRect(vec2 uv, float left, float right, float top, float bottom, float blur)
{
    float verticalBand = makeBand(uv.x ,left,right,blur );
    float horizontalBand = makeBand(uv.y, bottom, top, blur);
    return verticalBand * horizontalBand;
}

float makeDiamond(vec2 uv, float left, float right, float top, float bottom, float blur)
{
    float verticalBand = makeBand(uv.x +uv.y ,left,right,blur );
    float horizontalBand = makeBand(uv.y - uv.x, bottom, top, blur);
    return verticalBand * horizontalBand;
}

float remap1(float a, float b, float t) //remap t to a - b in a range of 0-1 
{
 	return ((t-a) / (b - a));
}

float remap2(float a , float b, float c, float d , float t)
{
    return remap1(a,b,t) * (d-c) + c;
}

