/*javascript snippet for calculating plastic constant for ND-dimension

var ND = 4,
	ITER = 10000,//higher iterations solves number to higher decimal places
	n = 1;
ND = 1.0/(1+ND);
for (var i = 0; i < ITER; i++) n = Math.pow(1+n,ND);
console.log(n);
*/
#define R4P 1.1673039782614187

//r4 based hash  http://extremelearning.com.au/unreasonable-effectiveness-of-quasirandom-sequences/
vec4 r4(float n) {
    return fract(.5+vec4(1./R4P,1./R4P/R4P,1./R4P/R4P/R4P,1./R4P/R4P/R4P/R4P)*n);
}
vec4 hash4(vec4 a) {
    a = mod(abs(a),8273.97234);
    #define R4S(sw) floor(fract(.352347+dot(a,vec4(.001,.1,10.,100.).sw*2.23454))*20000.)
    return r4(R4S(xyzw)+R4S(yzwx)+R4S(wxyz)+R4S(zwxy));
}


