    }
}

// Fork of "Log warp" by ciric50. https://shadertoy.com/view/tdt3z8
// 2019-10-19 18:37:12
// Added smoothstep to the color calculation created a nice effect.
// ---------------------------------------------------------------


// Created by inigo quilez - iq/2013
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

// See here for a tutorial on how to make this:
//
// http://www.iquilezles.org/www/articles/warp/warp.htm

// Sep 6, 2019
// Willard Maier
// I added logarithmic warping to what inigo did.

//====================================================================

const mat2 m = mat2( 0.80,  0.60, -0.60,  0.80 );

float noise( in vec2 p )
{
	return sin(p.x)*sin(p.y);
}

float fbm4( vec2 p )
{
    float f = 0.0;
    f += 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p );
    return f/0.9375;
}

float fbm6( vec2 p )
{
    float f = 0.0;
    f += 0.500000*(0.5+0.5*noise( p )); p = m*p*2.02;
    f += 0.250000*(0.5+0.5*noise( p )); p = m*p*2.03;
    f += 0.125000*(0.5+0.5*noise( p )); p = m*p*2.01;
    f += 0.062500*(0.5+0.5*noise( p )); p = m*p*2.04;
    f += 0.031250*(0.5+0.5*noise( p )); p = m*p*2.01;
    f += 0.015625*(0.5+0.5*noise( p ));
    return f/0.96875;
}

vec2 fbm4_2( vec2 p )
{
    return vec2(fbm4(p), fbm4(p+vec2(7.8)));
}

vec2 fbm6_2( vec2 p )
{
    return vec2(fbm6(p+vec2(16.8)), fbm6(p+vec2(11.5)));
}

//====================================================================

float func( vec2 q, out vec4 ron )
{
    q += 0.3*sin( vec2(0.27,0.23)*2.*iTime + length(q)*vec2(4.1,4.3));

	vec2 o = fbm4_2( 0.9*q );

    o += 0.4*sin( vec2(0.12,0.14)*2.*iTime + length(o));

    vec2 n = fbm6_2( 3.0*o );

	ron = vec4( o, n );

    float f = 0.5 + 0.5*fbm4( 1.8*q + 6.0*n );

    return mix( f, f*f*f*3.5, f*abs(n.x) );
}

vec2 warp(in vec2 p)
{
#define R1  0.1
#define R2  4.0

    // Logarithm
    vec2 a = log(abs(p+0.01));

    // Rotate
    float alpha = atan(log(R2/R1)/6.283185307);
    float f = cos(alpha);
    float s = sin(alpha);
    vec2 b = f*vec2(a.x*f - a.y*s, a.x*s+a.y*f);

    // Exponential
    return vec2(exp(b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p1 = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 p = warp(p1);
    float e = 2.0/iResolution.y;

    vec4 on = vec4(0.0);
    float f = func(p, on);

	vec3 col = vec3(0.0);
    col = mix( vec3(0.2,0.1,0.4), vec3(0.3,0.05,0.05), f );
    col = mix( col, vec3(0.9,0.9,0.9), dot(on.zw,on.zw) );
    col = mix( col, vec3(0.4,0.3,0.3), 0.2 + 0.5*on.y*on.y );
    col = mix( col, vec3(0.0,0.2,0.4), 0.5*smoothstep(1.2,1.3,abs(on.z)+abs(on.w)) );
    col = clamp( col*f*2.0, 0.0, 1.0 );

#if 0
    // gpu derivatives - bad quality, but fast
	vec3 nor = normalize( vec3( dFdx(f)*iResolution.x, 6.0, dFdy(f)*iResolution.y ) );
#else    
    // manual derivatives - better quality, but slower
    vec4 kk;
 	vec3 nor = normalize( vec3( func(p+vec2(e,0.0),kk)-f, 
                                2.0*e,
                                func(p+vec2(0.0,e),kk)-f ) );
#endif    

    vec3 lig = normalize( vec3( 0.9, 0.2, -0.4 ) );
    float dif = clamp( 0.3+0.7*dot( nor, lig ), 0.0, 1.0 );
    vec3 lin = vec3(0.70,0.90,0.95)*(nor.y*0.5+0.5) + vec3(0.15,0.10,0.05)*dif;
    col *= 1.2*lin;
	col = 1.0 - col;
	col = 1.1*col*col;
    col=1.5*smoothstep(0.65,1., col);
    fragColor = vec4( col, 1.0 );
}

/*


	Double Simplex Truchet Weave
	----------------------------

	All too often, I'll be in the middle of coding something, then someone on 
    Shadertoy will post some interesting concept that sends me off on a tangent. 
	BigWIngs puts up a lot of examples along those lines.

    The other day, he posted a double quad Truchet that resulted in a really 
	intense looking weave pattern -- The link is below. The basic premise was to 
	double the usual amount of connecting points per side, then run	random 
	segments between them. It's such a simple idea, but opens up a heap of 
	possibilities.

	Anyway, BigWIngs also sketched up a very basic double sided simplex weave as 
	a proof of concept, so this is just an extension on it.

	I was pleasantly surprised by how quickly it came together. Most of the time,
	things that should take five minutes wind up taking much longer. The process
	was relatively quick because I was able to repurpose my quad version without
	too many incidents.

	In regard to the lighting, I basically hacked away with a few samples to get 
	the look I wanted. There's very little science in there, so I wouldn't take 
	any of it seriously... And I hope you like a monochrome palette -- It's the
	one I choose when I'm too lazy to make colors work. :D Either way, there's a
	"CENTER_STRIPE" define there for anyone who requires a splash of color.

    Like the quad variation, if you wanted to produce a 3D extruded version,
	you'd probably have to replace the Bezier curves with a mixture of arcs and 
	lines, as it would be much faster.

	At some point, I'd like to put together a proper 3D version, but I should 
	probably get back to what I'm supposed to be coding at the moment. :)



	Based On:

    Double Triangle Truchet Doodle- BigWIngs
	https://www.shadertoy.com/view/Ml2yzD

	Cube-mapped Double Quad Truchet - BigWIngs
	https://www.shadertoy.com/view/wlSGDD

    Double Sided Quad Truchet - Shane
	https://www.shadertoy.com/view/wl2GRG


*/

// I was undecided as to whether I wanted color, or not. Perhaps too busy?
// Anyway, I've included it as an option.
//#define CENTER_STRIPE

// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


// Standard vec2 to float hash - Based on IQ's original.
float hash21(vec2 p){ return fract(sin(dot(p, vec2(141.213, 289.197)))*43758.5453); }


// Unsigned distance to the segment joining "a" and "b".
float distLine(vec2 a, vec2 b){


	b = a - b;
	float h = clamp(dot(a, b)/dot(b, b), 0., 1.);
    return length(a - b*h);
}


// IQ's signed distance to a quadratic Bezier. Like all of IQ's code, it's
// quick and reliable. :)
//
// Quadratic Bezier - 2D Distance - IQ
// https://www.shadertoy.com/view/MlKcDD
float sdBezier(vec2 pos, vec2 A, vec2 B, vec2 C){

    // p(t)    = (1 - t)^2*p0 + 2(1 - t)t*p1 + t^2*p2
    // p'(t)   = 2*t*(p0 - 2*p1 + p2) + 2*(p1 - p0)
    // p'(0)   = 2*(p1 - p0)
    // p'(1)   = 2*(p2 - p1)
    // p'(1/2) = 2*(p2 - p0)

    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;

     // If I were to make one change to IQ's function, it'd be to cap off the value 
    // below, since I've noticed that the function will fail with straight lines.
    float kk = 1./max(dot(b,b), 1e-6); // 1./dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      

    float res = 0.0;

    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;

    if(h >= 0.0) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h, -h) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = uv.x + uv.y - kx;
        t = clamp( t, 0.0, 1.0 );

        // 1 root
        vec2 qos = d + (c + b*t)*t;
        res = length(qos);
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3 t = vec3(m + m, -n - m, n - m) * z - kx;
        t = clamp( t, 0.0, 1.0 );

        // 3 roots
        vec2 qos = d + (c + b*t.x)*t.x;
        float dis = dot(qos,qos);

        res = dis;

        qos = d + (c + b*t.y)*t.y;
        dis = dot(qos,qos);
        res = min(res,dis);

        qos = d + (c + b*t.z)*t.z;
        dis = dot(qos,qos);
        res = min(res,dis);

        res = sqrt( res );
    }

    return res;
}

// Rendering the smooth Bezier segment. The idea is to calculate the midpoint
// between "a.xy" and "b.xy," then offset it by the average of the combined normals
// at "a" and "b" multiplied by a factor based on the length between "a" and "b."
// At that stage, render a Bezier from "a" to the midpoint, then from the midpoint
// to "b." I hacked away to come up with this, which means there'd have to be a more
// robust method out there, so if anyone is familiar with one, I'd love to know.
float doSeg(vec2 p, vec4 a, vec4 b, float r){

    // Mid way point.
   vec2 mid = (a.xy + b.xy)/2.; // mix(a.xy, b.xy, .5);

    // The length between "a.xy" and "b.xy," multiplied by... a number that seemed
    // to work... Worst coding ever. :D
    float l = length(b.xy - a.xy)*1.732/6.; // (1.4142 - 1.)/1.4142;

    // Points on the same edge each have the same normal, and segments between them
    // require a larger arc. There was no science behind the decision. It's just 
    // something I noticed and hacked a solution for. Comment the line out, and you'll 
    // see why it's necessary. By the way, replacing this with a standard semicircular 
    // arc would be even better, but this is easier.
    if(abs(length(b.zw - a.zw))<.01) l = r; 

    // Offsetting the midpoint between the exit points "a" and "b"
    // by the average of their normals and the line length factor.
    mid += (a.zw + b.zw)/2.*l;

    // Piece together two quadratic Beziers to form the smooth Bezier curve from the
    // entry and exit points. The only reliable part of this method is the quadratic
    // Bezier function, since IQ wrote it. :
    float b1 = sdBezier(p, a.xy, a.xy + a.zw*l, mid);
    float b2 = sdBezier(p, mid, b.xy + b.zw*l, b.xy);

    // Return the minimum distance to the smooth Bezier arc.
    return min(b1, b2);
}


// vec4 swap.
void swap(inout vec4 a, inout vec4 b){ vec4 tmp = a; a = b; b = tmp; }


vec4 triPattern(vec2 p){


    // Scaling constant.
    const float gSc = 4.;
    p *= gSc;

    // Keeping a copy of the orginal position.
    vec2 oP = p;

    // Smoothing factor: This can do my head in, sometimes. If you don't take screen
    // resolution into account, the fullscree image can look too blurry. If you do, the
    // image can look too crisp and lose something in the translation... Then there's PPI
    // to consider... Damned if you do, damned if you don't. :D
    float sf = 4./450.*gSc;
    sf*=.3;
    //float sf = 4./min(750., iResolution.y)*gSc;



    // SIMPLEX GRID SETUP

    vec2 s = floor(p + (p.x + p.y)*.36602540378); // Skew the current point.

    p -= s - (s.x + s.y)*.211324865; // Use it to attain the vector to the base vertex (from p).

    // Determine which triangle we're in. Much easier to visualize than the 3D version.
    //float i = p.x < p.y? 1. : 0.; // Apparently, faster than: i = step(p.y, p.x);
    //vec2 ioffs = vec2(1. - i, i);

    // Amalgamating to the two lines above into this.
    vec2 ioffs = p.x < p.y? vec2(0, 1) : vec2(1, 0);


    // Vectors to the other two triangle vertices.
    vec2 ip0 = vec2(0), ip1 = ioffs - .2113248654, ip2 = vec2(.577350269);

    // Make the vertices match up by swapping two of the vertices on alternate triangles. 
    // Actually, it's not really necessary here, but if you want to check neighboring
    // properties, etc, it's a habit worth getting into.
    if(ioffs.x<.5) { vec2 tmp = ip0; ip0 = ip2; ip2 = tmp; }


    // Centralize everything, so that vec2(0) is in the center of the triangle.
    vec2 ctr = (ip0 + ip1 + ip2)/3.; // Centroid.
    //
    ip0 -= ctr; ip1 -= ctr; ip2 -= ctr; p -= ctr;




    // Displaying the 2D simplex grid. Basically, we're rendering lines between
    // each of the three triangular cell vertices to show the outline of the 
    // cell edges. There are faster ways to achieve this, but this will do.
    float tri = min(min(distLine(p - ip0, p - ip1), distLine(p - ip1, p - ip2)), 
                  distLine(p - ip2, p - ip0));

    // Connecting points around the triangles. Two for each side. I should probably
    // use a bit of trigonometry and hard code these, but I was feeling lazy. :)
    const float offs = .204124; // Approx: length(ip0 - ip1)/4., or 1./sqrt(24.);
    vec2 m01s = mix(ip0, ip1, .5 + offs);
    vec2 m01t = mix(ip0, ip1, .5 - offs);
    vec2 m12s = mix(ip1, ip2, .5 + offs);
    vec2 m12t = mix(ip1, ip2, .5 - offs);
    vec2 m20s = mix(ip2, ip0, .5 + offs);
    vec2 m20t = mix(ip2, ip0, .5 - offs);

    // The boundary normals for each point. I should probably hardcode these as well.
    vec2 n01 = -normalize(mix(ip0, ip1, .5));
    vec2 n12 = -normalize(mix(ip1, ip2, .5));
    vec2 n20 = -normalize(mix(ip2, ip0, .5));

    // Points, and their respective normals, to pass to the segment function.
    vec4[6] pnt = vec4[6](vec4(m01s, n01), vec4(m01t, n01), 
                          vec4(m12s, n12), vec4(m12t, n12),
                          vec4(m20s, n20), vec4(m20t, n20));



    // Shuffling the 6 array points and normals. I think this is the Fisher–Yates method, 
    // but don't quote me on it. It's been a while since I've used a shuffling algorithm, 
    // so if there are inconsistancies, etc, feel free to let me know.
    //
    // For various combinatorial reasons, some non overlapping tiles will probably be 
    // rendered more often, but generally speaking, the following should suffice.
    //
    for(int i = 5; i>0; i--){

        // Using the cell ID and shuffle number to generate a unique random number.
        float fi = float(i);

        // Random number for each triangle: The figure "s*3 + ioffs + 1" is unique for
        // each triangle... I can't remember why I felt it necessary to divide by 3,
        // but I'll leave it in there. :)
        float rs = hash21((s*3. + ioffs + 1.)/3. + fi/6.);

        // Other array point we're swapping with.
        //int j = int(floor(mod(rs*6e6, fi + 1.)));
        // I think this does something similar to the line above, but if not, let us know.
        int j = int(floor(rs*(fi + .9999)));
        swap(pnt[i], pnt[j]);

    }    


    vec3 d; // Distances for all three triangle cell segments.

    float dPnt = 1e5; // Distance for the edge points.

    for(int i = 0; i<3; i++){

        // The Bezier segments for each layer.
        d[i] =  doSeg(p,  pnt[i*2], pnt[i*2 + 1], offs);

        // The two edge points for each side.
        dPnt = min(dPnt, length(p - pnt[2*i].xy));
        dPnt = min(dPnt, length(p - pnt[2*i + 1].xy));
    }


    d -= .05; // Giving the segment some width.
    dPnt -= .0125; // Edge point size.


    // Overall cell color and bump value.
    vec3 col = vec3(1.);
    //float bump = -.5;

    // Concentric triangle background pattern.
    float pat = 1.;//clamp(cos(tri*96.)*.5 + .5, 0., 1.);

    // Background triangle borders.
    //col = mix(col, col*1.5, 1. - smoothstep(0., sf, tri - .01));
    //col = mix(col, col*.5, 1. - smoothstep(0., sf, tri - .0));

    // Extra base shadowing for the bump pass.
    //bump = mix(bump, 0., (1. - smoothstep(0., sf*3., min(min(d.x, d.y), d.z) - .03))*.5);


    // Rendering the layers. The bump value is similar to the color value, but differs slightly,
    // in places, so ultimately needs its own variable. Obviously, this means doubling up on 
    // calculations, but thankfully, this is a 2D... ish example.
    for(int i = 0; i<3; i++){

        // Shadows, stroke, color, etc.
        //col = mix(col, vec3(.0), (1. - smoothstep(0., sf*3., d[i] - .03))*.85);
        col = mix(col, vec3(.0), 1. - smoothstep(0., sf, d[i] - .02));
        col = mix(col, vec3(1), 1. - smoothstep(0., sf, d[i]));
//        col = mix(col, vec3(.03), 1. - smoothstep(0., sf, d[i] + .01));



    }

    // Return the color and bump value.
    return vec4(col, 1.);

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Aspect correct pixel coordinates.
    vec2 uv = (fragCoord - iResolution.xy*.5)/min(750., iResolution.y);

    // Scaling and translation.
	uv = rot2(3.14159/12.)*(uv + vec2(.1, .05)*iTime);

    // Three color samples.
    vec4 col = triPattern(uv);

    fragColor = (max(col, 0.));

}

const bool GO_CRAZY = false;

const float tau = 6.283185;

float hash1(float p) {
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float hash1(float p, float q) {
    vec3 p3  = fract(vec3(p, q, p) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash2(float p) {
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec2 hash2(float p, float q) {
    vec3 p3 = fract(vec3(p, q, p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec3 hash3(float p) {
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

vec3 hash3(vec3 p3) {
    p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

vec4 hash4(float p) {
    vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);    
}

float noise1(float p) {
    float i = floor(p);
    float f = fract(p);
    float u = f * f * (3.0 - 2.0 * f);
    return 1.0 - 2.0 * mix(hash1(i), hash1(i + 1.0), u);
}

vec3 noise3(vec3 p) {
    vec3 i = floor(p);
    vec3 f = fract(p);
    vec3 u = f * f * (3.0 - 2.0 * f);
    return 1.0 - 2.0 * mix(mix(mix(hash3(i + vec3(0.0, 0.0, 0.0)), 
                                   hash3(i + vec3(1.0, 0.0, 0.0)), u.x),
                               mix(hash3(i + vec3(0.0, 1.0, 0.0)), 
                                   hash3(i + vec3(1.0, 1.0, 0.0)), u.x), u.y),
                           mix(mix(hash3(i + vec3(0.0, 0.0, 1.0)), 
                                   hash3(i + vec3(1.0, 0.0, 1.0)), u.x),
                               mix(hash3(i + vec3(0.0, 1.0, 1.0)), 
                                   hash3(i + vec3(1.0, 1.0, 1.0)), u.x), u.y), u.z);
}

float fbm1(float p) {
    float f = noise1(p); p = 2.0 * p;
    f += 0.5 * noise1(p); p = 2.0 * p;
    f += 0.25 * noise1(p); p = 2.0 * p;
    f += 0.125 * noise1(p); p = 2.0 * p;
    f += 0.0625 * noise1(p);
    return f / 1.9375;
}

const mat3 m = mat3( 0.51162, -1.54702,  1.15972,
                    -1.70666, -0.92510, -0.48114,
                     0.90858, -0.86654, -1.55678);

vec3 fbm3(vec3 p) {
    vec3 f = noise3(p); p = m * p;
    f += 0.5 * noise3(p); p = m * p;
    f += 0.25 * noise3(p); p = m * p;
    f += 0.125 * noise3(p); p = m * p;
    f += 0.0625 * noise3(p);
    return f / 1.9375;
}

vec3 uniform3(float seed) {
    vec2 hash = hash2(seed);
    float x = 2.0 * hash.x - 1.0;
    float r = sqrt(1.0 - x * x);
    float t = tau * hash.y;
    return vec3(x, r * sin(t), r * cos(t));
}

vec3 hsv(float hue, float sat, float val) {
    return (val) * (vec3(1.0 - (sat)) + (sat) * (0.5 + 0.5 * cos(6.2831853 * (vec3(hue) + vec3(0.0, 0.33, 0.67)))));
}

vec4 newMoon(float moonCount, vec3 pole, float seed, float moon) {
    vec3 pos = uniform3(seed);
    pos -= 0.75 * pole * dot(pole, pos);
    pos = normalize(pos);
    pos *= 1.5 + 2.0 * hash1(seed + 0.001);
    pos *= step(moon, moonCount);
    float r = 0.02 + 0.1 * pow(hash1(seed + 0.002), 6.0);
    return vec4(pos, r);
}

#define hitMoon(moon, seed) { \
    vec3 v = eye - moon.xyz; \
    float b = dot(v, ray); \
    float c = dot(v, v) - moon.w * moon.w; \
    float h = b * b - c; \
    float mHit = step(0.0, h); \
    h = max(h, 0.0); \
    float mDepth = -b - sqrt(h); \
    vec3 p = eye + mDepth * ray; \
    vec3 mNorm = normalize(p - moon.xyz); \
    mHit *= step(mDepth, moonDepth); \
    moonDepth = mix(moonDepth, mDepth, mHit); \
    moonNorm = mix(moonNorm, mNorm, mHit); \
    moonSeed = mix(moonSeed, seed, mHit); \
}

float moonShadow(vec3 pos, vec4 moon, vec3 dir) {
    vec3 p = pos - moon.xyz;
	float m = dot(p, dir);
    float n = dot(p, p);
    return step(step(m, 0.0) * moon.w * moon.w, n - m * m);
}

float ring(vec3 pos, vec3 dir, vec3 pole, float seed, out float d, out float r, out vec3 q) {
    vec2 bounds = 1.4 * hash2(seed + 0.9) + vec2(1.1, 1.6);

    d = dot(-pos, pole) / dot(dir, pole);
    q = pos + d * dir;
    r = length(q);

    float a = smoothstep(bounds.x, bounds.x + 0.1, r) * smoothstep(bounds.y, bounds.y - 0.1, r);
    a *= smoothstep(-0.5, 1.0, fbm1(6.0 * (r + 3.0 * seed)));
    return clamp(a, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float t = 0.25 * iTime;
    float seed = floor(t) + 1.0;

    vec3 center = vec3(0.0);
    float s = tau * (0.4 * (fract(t) - 0.5));
    center += s * pow(tan(s) / s - 1.0, 10.0) * vec3(1.0, 3.0, 0.0);
    vec3 eye = center + vec3(0.0, -5.0, 0.0) + fract(t) * vec3(1.0, 1.0, 0.0);
    float zoom = 3.0;

    vec3 forward = normalize(center - eye);
    vec3 right = normalize(cross(forward, vec3(0.0, 0.0, 1.0)));
    vec3 up = cross(right, forward);
    vec2 xy = 2.0 * fragCoord - iResolution.xy;
    vec3 ray = normalize(xy.x * right + xy.y * up + zoom * forward * iResolution.y);

    vec3 light = uniform3(seed);
    vec3 pole = uniform3(seed + 0.1);
    if (GO_CRAZY || iMouse.z > 0.0)
        seed += 0.123 * floor(3.0 * iTime);
    float dist = 1.0 + 1.2 * hash1(seed);
    eye *= dist;

    vec2 bandHash = hash2(seed + 0.2);
    float bandScale = 1.0 + 6.0 * bandHash.x;
    float bandTurbulence = 0.1 * pow(bandHash.y, 2.0);

    vec4 planetHash = hash4(seed + 0.3);
    vec3 planetColor = hsv(planetHash.x, 0.5, 0.5 + 0.2 * planetHash.y);
    vec3 planetMinorX = hsv(planetHash.x, 0.3, 0.5 + 0.2 * planetHash.y + 0.3 * planetHash.w) - planetColor;
    vec3 planetMinorY = hsv(planetHash.x + 0.4 * planetHash.z, 0.5, 0.5 + 0.2 * planetHash.y) - planetColor;
    vec3 planetMinorZ = hsv(planetHash.x + 0.4 * planetHash.z, 0.3, 0.5 + 0.2 * planetHash.y - 0.4 * planetHash.w) - planetColor;

    vec3 ringHash = hash3(seed + 0.4);
    vec3 ringColor = hsv(ringHash.x, 0.2, 0.5 + 0.2 * ringHash.y);
    vec3 ringMinor = hsv(ringHash.x + 0.3 * ringHash.z, 0.2, 0.7 + 0.2 * ringHash.y) - ringColor;

    float moonCount = hash1(seed + 0.5) * 8.0 - 2.0;    

    vec4 moon1 = newMoon(moonCount, pole, seed + 0.01, 0.0);
    vec4 moon2 = newMoon(moonCount, pole, seed + 0.02, 1.0);
    vec4 moon3 = newMoon(moonCount, pole, seed + 0.03, 2.0);
    vec4 moon4 = newMoon(moonCount, pole, seed + 0.04, 3.0);
    vec4 moon5 = newMoon(moonCount, pole, seed + 0.05, 4.0);
    vec4 moon6 = newMoon(moonCount, pole, seed + 0.06, 5.0);

    float b = dot(eye, ray);
    float c = dot(eye, eye) - 1.0;
    float h = b * b - c;
    float hit = step(0.0, h);
    h = max(h, 0.0);
    float depth = -b - sqrt(h);
    vec3 pos = eye + depth * ray;

    vec3 poleX = normalize(cross(pole, vec3(0.0, 0.0, 1.0)));
    vec3 poleY = cross(pole, poleX);
    mat3 rot = inverse(mat3(pole, poleX, poleY));

    vec3 p = rot * pos;
    //p.x = asin(p.x); // Latitude bands
    p += bandTurbulence * fbm3(10.0 * p);

    vec3 bands = fbm3(bandScale * vec3(1.0, 0.05, 0.05) * p + seed);
    vec3 color = planetColor;
    color += planetMinorX * bands.x;
    color += planetMinorY * bands.y;
    color += planetMinorZ * bands.z;

    float moonDepth = 2.0 * depth;
    vec3 moonNorm = vec3(0.0);
    float moonSeed = 0.0;
    hitMoon(moon1, 1.0);
    hitMoon(moon2, 2.0);
    hitMoon(moon3, 3.0);
    hitMoon(moon4, 4.0);
    hitMoon(moon5, 5.0);
    hitMoon(moon6, 6.0);
    float moonHit = 1.0 - step(depth, moonDepth) * hit;

    vec3 moonPos = eye + moonDepth * ray;

    vec3 moonColor = fbm3(10.0 * moonPos);
    float moonSat = 0.4 * hash1(seed + 0.123 * moonSeed) + 0.2 * moonColor.x;
    moonColor = hsv(hash1(seed + 0.321 * moonSeed) + 0.2 * moonColor.y, moonSat, 0.4 + 0.7 * moonColor.z);

    pos = mix(pos, moonPos, moonHit);
    color = mix(color, moonColor, moonHit);
    depth = mix(depth, moonDepth, moonHit);
    vec3 norm = mix(pos, moonNorm, moonHit);
    hit = 1.0 - (1.0 - hit) * (1.0 - moonHit);

    float illumination = max(dot(norm, light), 0.0);

    float m = dot(pos, light);
    float n = dot(pos, pos);
    illumination *= step(step(m, 0.0), n - m * m);

    illumination *= moonShadow(pos, moon1, light);
    illumination *= moonShadow(pos, moon2, light);
    illumination *= moonShadow(pos, moon3, light);
    illumination *= moonShadow(pos, moon4, light);
    illumination *= moonShadow(pos, moon5, light);
    illumination *= moonShadow(pos, moon6, light);

    float d, r;
    vec3 q;
    float ringShadow = ring(pos, light, pole, seed, d, r, q);
    illumination *= 1.0 - ringShadow * step(0.0, d);

    color *= hit * illumination;

    float ringAlpha = ring(eye, ray, pole, seed, d, r, q);
    float ringLight = 0.5 + 0.5 * abs(dot(pole, light));

    m = dot(q, light);
    n = dot(q, q);
    ringLight *= step(step(m, 0.0), n - m * m);

    ringLight *= moonShadow(q, moon1, light);
    ringLight *= moonShadow(q, moon2, light);
    ringLight *= moonShadow(q, moon3, light);
    ringLight *= moonShadow(q, moon4, light);
    ringLight *= moonShadow(q, moon5, light);
    ringLight *= moonShadow(q, moon6, light);

    ringColor += ringMinor * fbm1(15.0 * (r + 3.0 * seed));
    ringColor *= ringLight;

    color = mix(color, ringColor, step(hit * d, depth) * ringAlpha);

    color *= 2.0;
    float flare = max(0.0, dot(ray, light));
    color += pow(flare, 10.0);

    fragColor = vec4(color, 1.0);
}

// should be a square for best results
#define NUM 25

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y * .25;
    // warm up the clock a bit
    float dt = iTime + 10.;

    // init points
    vec2 points[NUM];
    int stride = 5;
    for(int i = 0; i < NUM; i++) {
        float row = float(i / stride);
        float col = float(i % stride);
        points[i] = (-2. + vec2(col, row)) / float(stride);

        // todo this could probably use some more interesting movement
        points[i] += vec2(
             sin(dt*.123*float(i)) * .1,
             sin(19. + dt*.256*float(i)) * .1
            );
    }

    // debug origin draw
    #if 0
    if (uv.x > -.01 && uv.x < .01) { fragColor = vec4(1.,0.,0.,1.); return;}
    if (uv.y > -.01 && uv.y < .01) { fragColor = vec4(1.,0.,0.,1.); return;}
    #endif

    // this loop calculates the closest and second closest distances
    // it will be used for the classic f2-f1 voronoi border
    vec2 dist = vec2(.5);
    for(int i = 0; i < NUM; i++) {
        float d = distance(uv, points[i]);
        if (d < dist.x) {
            // store the new closest value and record
            // what the previous one was in dist.y
            dist.y = dist.x;
            dist.x = d;
        } else if (d < dist.y) {
            // sometimes the closest value doesn't 
            // change but the second closest does
            dist.y = d;
        }
    }

    // colors are based on leopard print patterns
    vec3 base = vec3(1.,.8,.5);
    vec3 outerspot = vec3(0.1, .05, .02);
    vec3 innerspot = vec3(.81, .37, .01);

    // easy f2-f1 voronoi. consider incorporating iq's perfect border
    // if you want something more predictable
    vec3 voro = vec3(dist.y - dist.x);
    vec3 col = mix(base, outerspot, step(.02, voro));
    col = mix(col, innerspot, step(.06, voro));

    fragColor = vec4(col, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/min(iResolution.x, iResolution.y);
	uv *= 5.;
    float f = smoothstep(-.4,.4,sin(atan(uv.y, uv.x)*4.-length(uv)*5. +iTime) + sin(length(uv)+iTime));
    f = 1.- abs(f-1.)*10.0;
    fragColor = vec4(f,f,f,1);
}

