float sdBox( vec3 p, vec3 b , float r)
{
  r = min(r,max(b.x,max(b.y,b.z)));
  vec3 q = abs(p) - (b-r);
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float map(in vec3 pos) {
 	float d = length(pos) - 0.2; //Distance function for a sphere
    float d2 = sdBox(pos - vec3(0,0,0),vec3(0.2,0.2,0.2),(sin(iTime)*0.5+0.5)*0.2);
	d = d2;
    d = min(pos.y + 0.2,d);
    return d;
}
vec3 calcNormal( in vec3 pos) {
    vec2 e = vec2(0.0001,0.0);
 	return normalize( vec3(map(pos+e.xyy)-map(pos-e.xyy),
                           map(pos+e.yxy)-map(pos-e.yxy),
                           map(pos+e.yyx)-map(pos-e.yyx)));   
}

float castRay(in vec3 ro, in vec3 rd, in float renderDistance) {
 	float t = 0.0;
    for(int i = 0; i < 100; i++) {
     	vec3 pos = ro + t*rd;

        float h = map(pos);
        if(h < 0.001) break;

        t += h;
        if(t > renderDistance) break;
    }
    if(t > renderDistance) t = -1.0;
    return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float an = iMouse.x / iResolution.x*6.245;
    float en = -iMouse.y / iResolution.y + 3.1415 / 5.0;
    //float an = iTime;
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec3 ro = vec3(cos(an),sin(en),sin(an));
    vec3 ta = vec3(0.0);

    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww,vec3(0,1,0)));
    vec3 vv = normalize(cross(uu,ww));

    vec3 rd = normalize(p.x * uu + p.y * vv + 1.5 * ww);

    float renderDistance = 20.0;
	float t = 0.0;
    t = castRay(ro,rd,renderDistance);
    vec3 col = pow(vec3(0.6,0.8,0.9), vec3(rd.y*7.0));


    vec3 sunVector = normalize(vec3(0.8,0.4,0.2));
    col += vec3(pow(max(dot(sunVector,rd),0.0), 50.0)) * normalize(vec3(7,4.5,3));



    if(t > 0.0) {
        vec3 pos = ro + t*rd;
        vec3 norm = calcNormal(pos);
        vec3 mat = vec3(0.18);


        vec3 skyVector = normalize(vec3(0.0,1.0,0.0));

        float sunDiffuse = clamp(dot(norm,sunVector),0.0,1.0);
        float skyDiffuse = clamp(0.5 + 0.5*dot(norm,skyVector),0.0,1.0);
        float bounceDiffuse = clamp(0.5 + 0.5*dot(norm,-skyVector),0.0,1.0);

        float sunShadow = step(castRay(pos + norm * 0.001,sunVector, renderDistance),0.0);



    	col = mat * vec3(7,4.5,3) * sunDiffuse * sunShadow;
        col += mat * vec3(0.6,0.8,0.9) * skyDiffuse;
        col += mat * vec3(0.7,0.3,0.2) * bounceDiffuse;
    }
	col = pow(col, vec3(0.4545));
    // Output to screen
    fragColor = vec4(col,1.0);
}

