float otherSDBox (vec3 p, vec3 c, vec3 s)
{
    float x = max
    (   p.x - c.x - s.x / 2.,
        c.x - p.x - s.x / 2.
    );

    float y = max
    (   p.y - c.y - s.y / 2.,
        c.y - p.y - s.y / 2.
    );

    float z = max
    (   p.z - c.z - s.z / 2.,
        c.z - p.z - s.z / 2.
    );

    float d = x;
    d = max(d,y);
    d = max(d,z);
    return d;
}

