    float sph = sdSphere(p + vec3(0.,-jumpster -.8,0.));
    float wiggliwoo = (sin(iTime) + 1.) * .3 + .1;
    float bx = otherSDBox(opCheapBend(p, -sin((4.*iTime + 2.1)) * wiggliwoo), vec3(0.,sin(iTime)*-.2,0.), vec3(5.,.1,3.));
    //float bx = otherSDBox(opCheapBend(p, .4), vec3(0.), vec3(3.,.1,3.));
    float d = min(bx, sph);

    return min(d, sdGround(p, -1.4));
}

// https://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow(vec3 ro, vec3 rd, float k) {
    // start the ray a little bit away from
    // the surface to avoid self intersection
    float t = .2;
    float result = 1.;
    for (int i = 0; i < 10; i++) {
        vec3 p = ro + rd * t;
        float d = map(p);
        result = min(result, k * d / t);
        if (result < 0.0001) {
            return 0.0;
        }
        t += d;
    }
    return result;
}

vec3 grad(vec3 p) {
    vec2 eps = vec2(TINY, 0.);
    return normalize(map(p) - vec3(map(p - eps.xyy), map(p - eps.yxy), map(p - eps.yyx)));
}

float march(vec3 ro, vec3 rd) {
    float t = 0.;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + rd * t;
        float d = map(p);
        if (d < TINY) { return t; }
        t += d;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    uv += texture(iChannel1, uv + iTime).rg * .04; // a touch of distortion

    // Time varying pixel color
    vec3 col = texture(iChannel0, fragCoord/iResolution.xy).rgb * (1. - length(uv) * .5);//vec3(length(uv)*vec3(sin(iTime),1.-length(uv),.8));

    vec3 ro = vec3(0., 1., 3.);
    // old style look straight ahead
    // vec3 rd = normalize(vec3(uv.xy, -1.));

    // --- camera look-at ---

    // bad code ignore sleepy copied from other thing for lookat
    float fr = fract(iTime);
    float sigx = (fr > .5 ? 1. - fr : fr) * 2.;
    float jumpster = doubleEllipticSigmoid(sigx, .2, .51);
    vec3 sphc = vec3(0.,jumpster -.6 * 2.,-2.);

    vec3 look = normalize(vec3(.0,-.1,-1.));//normalize(sphc);
    vec3 right = cross(vec3(0., 1., 0.), look);
    vec3 up = cross(look, right);
    vec3 rd = normalize(uv.x * right + uv.y * up + look);

    float dist = march(ro, rd);
    if (dist < 10.) { 
        vec3 p = ro + rd * dist;
        vec3 norm = grad(p);
        vec3 ld = vec3(0.,1.,0.);
        vec3 lc = vec3(1.,.97,.92);
        float incd = dot(norm, ld);
        float shad = shadow(p, ld, 12.);
        col = incd * lc * shad;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

