    for(int i=0;i<iter;i++)
	{
    	float id = 0.5 + 0.5*cos(iTime + sin(dot(floor(px+0.5),vec3(113.1,17.81,1.)))*43758.545);
    	vec3 co = 0.5 + 0.5*cos(iTime + 3.5*id + vec3(0.0,1.57,3.14) );
