const float PI = acos(-1.);

mat2 rotate(in float r) { float c=cos(r), s=sin(r); return mat2(c,-s,s,c); }
vec2 rotate(in vec2 p, in float r) { return p * rotate(r); }
vec3 rotate(in vec3 p, in vec3 r) { p.xy = rotate(p.xy, r.z); p.yz = rotate(p.yz, r.x); p.zx = rotate(p.zx, r.y); return p; }
vec3 rotate(in vec3 p, in float r) { return rotate(p, vec3(r)); }

float sphere(in vec3 p, in float r) { return length(p)-r; }
float box(in vec3 p, in vec3 b) { vec3 d = abs(p)-b; return length(max(d, 0.)) + min(max(d.x, max(d.y, d.z)), 0.); }
float box(in vec3 p, in float b) { return box(p, vec3(b));}

float map(in vec3 p) {
    float r = 1.5;
    vec3 q = p;
    vec3 c = vec3(4.0);
    q = mod(q-0.5*c, c) - 0.5*c;

    float d = sphere(q, .5);
	for(int i=0;i<6;i++) {
    	float fi = float(i);
        q = abs(q) - r;
        q.xyz = q.zxy;
        q = rotate(q, PI/4.);
        r *= mix(0.55, 0.6, 0.5+0.5*sin(iTime*0.04));
    }
    d = max(-d, box(q,r));
	return d;
}

#define MARCH_THR 5e-4
float trace(in vec3 ro, in vec3 rd, in vec2 tmm) {
	float t = tmm.x;
    for(int i=0;i<100;i++) {
    	float tmp = map(ro + rd*t);
        if(tmp<MARCH_THR || tmm.y<t) break;
        t += tmp;
    }
    return t;
}


vec3 normal(in vec3 p) {
	vec2 e = vec2(1., -1.)*1e-4;
    return normalize(
    	e.xyy*map(p+e.xyy) +
    	e.yxy*map(p+e.yxy) +
    	e.yyx*map(p+e.yyx) +
    	e.xxx*map(p+e.xxx)
    );
}


#define MATERIAL_COLOR vec3(0.8,0.9,1.0)
#define MATERIAL_BRIGHTNESS 2.0
#define BACKGROUND_COLOR vec3(0.)
#define REFLECTION 0.5+0.3*sin(iTime*0.25*PI)
vec3 render(in vec3 ro, in vec3 rd, in vec2 tmm) {
    vec3 res = vec3(0.);

	float t = trace(ro, rd, tmm);
    if(tmm.y<t) return BACKGROUND_COLOR;

	vec3 pos = ro + rd*t;
	vec3 nor = normal(pos);
    vec3 ref = reflect(rd, nor);

    vec3 lp = vec3(-1.);
    //lp = ro;
    vec3 ld = normalize(lp-pos);

    vec3 col = MATERIAL_COLOR * MATERIAL_BRIGHTNESS;
    col *= clamp(dot(nor, ld)-exp(-1.*length(lp-pos)), 0., 1.);
    col += pow(clamp(dot(ref, ld), 0., 1.), 30.);

    res += col;


    float theta = 0.5*PI - acos(dot(ref, nor));
    float tmmx = MARCH_THR / sin(theta);

    t = trace(pos, ref, vec2(tmmx, tmm.y));
    if(tmm.y<t) {
    	col = BACKGROUND_COLOR;
    } else {
        pos = pos + nor*t;
        nor = normal(pos);
        ref = reflect(rd, nor);

        col =  MATERIAL_COLOR * MATERIAL_BRIGHTNESS;
        col *= clamp(dot(nor, ld), 0., 1.);
        col += pow(clamp(dot(ref, ld), 0., 1.), 50.);
    }

    res = mix(res, col, REFLECTION);

	return res;
}

