//improved thanks to FabriceNeyret!
float sq(vec2 p, float d)
{
    p=smoothstep(3./iResolution.y,.0,abs(p)-d-.1);
    return  p.x*p.y;
}
void mainImage( out vec4 c, in vec2 f )
{
    vec2  p  = (2.*f-iResolution.xy)/iResolution.y;
    float pr = fract(iTime/5.)*4.,
    	  z  = clamp(4.-(pr),0.0,1.0);
    vec2  pp = vec2(0.,.4)+p+p*z/2.65-vec2(0.,-.27+z*.92);    

    float t1 = clamp(1.-(pr),0.0,1.0),
    	  t2 = clamp(2.-(pr),0.0,1.0),
    	  t3 = clamp(3.-(pr),0.0,1.0),
    	  d = .375,
		  t = sq(pp+vec2(0.,-2.)*t2,d) +
        	  sq(pp+vec2(.99,0.)+vec2(0.,-2.)*t1,d)+
        	  sq(pp+vec2(-.99,0.)+vec2(0.,-2.)*t3,d),    
   		  m = 1.479, v = 1.1723,h = 1.3636,
	 	  b = sq(pp+vec2(0.,v),d*m)+
    	  	  sq(pp+vec2(h,v),d*m) +
    	  	  sq(pp+vec2(-h,v),d*m);
    	  c = vec4(vec3(t+b),1.0);
}

// This is a combination of a semi-random noise gnerator 
// from Art of Code on Youtube - https://www.youtube.com/channel/UCcAlTqd9zID6aNX3TzwxJXg/videos
// a step/HSV effect I got from https://www.shadertoy.com/view/XdyXz3
// and some stuff I did myself (constantly changing noise and the squares)
// it was basically me learning how to work with masks
// it's a mess, but it works

#define S(a,b,t) smoothstep(a,b,t)
const float LEVELS = 12.;

vec4 N14(float t) {
    return fract(sin(t*vec4(123., 1024., 3456., 9564.))*vec4(6547., 345., 7799., 1564.));
}

vec4 NN14(float t) {
    return fract(sin(t*vec4(1243., 10224., 13456., 69564.))*vec4(61547., 4345., 27799., 15564.));
}

float N11(float p) {
    float a = fract(p*345.65);
	a += dot(a, a+34.45);
    return fract(a);
}

float N21 (vec2 p) {
    return fract(sin(p.x*122.+p.y*52453.)*244156.); // returns a single randomish number based on a coord input
}

vec3 hsv2rgb(vec3 c){  // taken from https://www.shadertoy.com/view/XdyXz3
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float SmoothNoise(vec2 uv) {
 	vec2 lv = fract(uv);
    vec2 id = floor(uv);

    lv = lv*lv*(3.-2.*lv);

    float bl = N21(id);
    float br = N21(id+vec2(1,0));
    float b = mix(bl, br, lv.x);
    float tl = N21(id+vec2(0,1));
    float tr = N21(id+vec2(1,1));
    float t = mix(tl, tr, lv.x);

    return mix(b,t,lv.y);
}

float SmoothNoise2(vec2 uv,float d) {
    float c;
    float uvscale = 4.;
    float detail = 1.;
    for (float i=1.;i<=d;i++) {
    	c += SmoothNoise(uv*uvscale)*detail;
        detail *= 0.5;
        uvscale *= 2.;
    }
    return c/1.3;;
}

float GetClouds(vec2 uv, float t, float d) {
    float speed = sin(t)*.5+.5;
    float pi = 3.141592653;
    // put a thing in here so that whenever the mix is fully one way or another, the 'hidden' one changes
    float pitime = mod(floor((t - (pi*.5)) / (pi*2.)),100.); // Had to mod this, as after a while it starts drifting
    float pitime2 = mod(floor((t - (pi*1.5)) / (pi*2.)),100.); // And at high numbers, the cloud function get a little janky
    vec2 offs = vec2(-2.,-2.);
    vec2 target1 = uv+(offs*pitime);
    vec2 target2 = uv+vec2(11.,22.)+(offs*pitime2);

    // pull two noise maps
    float c1 = SmoothNoise2(target1, d);
    float c2 = SmoothNoise2(target2, d);
    float c = mix(c1,c2,speed);                 

    return floor(c*LEVELS)/LEVELS;
}

vec3 DoClouds (vec2 uv) {
    uv *= .2;
	float t = iTime*1.;
    //vec2 offset = vec2(sin(t)*0.006,cos(t)*0.006);  // rotating lightsource. doesn't look so good without a visual light cue.
    vec2 offset = vec2(0.006,0.006);

    uv += iTime*0.02;  // slow diagonal move
	float detail = 4.; // 1-6 working range

    float c = GetClouds(uv,t,detail);//
    float edge = GetClouds(uv+offset,t,detail);

    //calc HSV color (taken from https://www.shadertoy.com/view/XdyXz3)
    float h = c*.9 - .2; //rainbow hue
    float s = 0.5;
    float v = 1. - ( c*(detail*.05) ); //deeper is darker

    v -= c-edge;

    return hsv2rgb(vec3(-h,s,v));
}

vec4 RoundedBox( in vec2 p, in vec2 b, in float r, in vec4 rgba ) 
{
    vec2 q = abs(p) - b;
    float d = min(max(q.x,q.y),0.0) + length(max(q,0.0));
    d *= 10.;
    return S(.8,.6,d) * rgba;
}

float RoundedBoxInnerMask( in vec2 p, in vec2 b, in float r) 
{
    vec2 q = abs(p) - b;
    float d = min(max(q.x,q.y),0.0) + length(max(q,0.0));
    d *= 10.;
    return S(.8,.6,d);
}

vec4 RoundedBoxRing( in vec2 p, in vec2 b, in float r) 
{
    vec2 q = abs(p) - b;
    float d = (min(max(q.x,q.y),0.0) + length(max(q,0.0)))*4.;
    return S(1.,0.85,d)-S(.4,.25,d)*vec4(0.,0.,0.,1.);
}

vec4 dots(vec2 uv,float t, vec4 rgba, float seed) {
    vec4 b = vec4(0.); // Box
    float bm = 0.; // Box Mask
    vec4 br = vec4(0.); // Box Ring
    float brm = 0.; // Box Ring Mask
    vec2 p = vec2(0.); // Box coords
    float nn = 20.; // Number of boxes
    float pi = 3.141592653;

    for (float n=1.;n<nn;n++) {
        float ti = (t*2.2)+.22*n; // change move time offset for each box so they don't all move at the same time
        // Put a thing in here to change the seed each cycle
        float pitimes = floor((ti - (pi*.5)) / (pi*2.))*nn;
        float pitimet = floor((ti - (pi*1.5)) / (pi*2.))*nn;
        float s1 = seed*n+pitimes;
        float s2 = seed*n+pitimet;
        vec4 rands = (N14(s1)-.5)*5.;
        vec4 randt = (NN14(s2)-.5)*5.;
        float locx = rands.x+(S(0.,1.,sin(ti)*.5+.5)*(randt.x-rands.x));
        float locy = rands.y+(S(0.,1.,sin(ti)*.5+.5)*(randt.y-rands.y));

        p.x = uv.x+(locx*1.8); // Needs to be wider.  should fix to aspect ratio, but cba right now.
        p.y = uv.y+locy;

        b += RoundedBox(vec2(p),vec2(0.5,0.5),0.1, rgba);
        bm += RoundedBoxInnerMask(vec2(p.x,p.y),vec2(0.5,0.5),0.1);
        br += RoundedBoxRing(vec2(p.x,p.y),vec2(0.5,0.5),0.1);
    }
	float maskring = clamp(1.-bm,0.,1.);
    return (b)-(maskring*br);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 m = iMouse.xy/iResolution.xy;
    float  t = iTime*.6+m.x+6.;
	uv *= 5.;
	vec4 col = vec4(.0);

    col += vec4(DoClouds(uv),1.);

    col += dots(uv,t,vec4(.1,.1,.35,.15),N11(54.)); // Add some boxes

    fragColor = vec4(col);
}

#define VOLUME 0.001
#define PI 3.14159
#define MAXSTEP 64

float Vignette(vec2 uv, float force)
{
    return 1.0 - pow(length(uv), force);
}

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }

float uSDF(float s1, float s2) { return min(s1, s2);}

float sSDF( float d1, float d2 ) { return max(-d1,d2); }

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); 
}

float sdScaledSphere(vec3 p, vec3 s, float r)
{
    return float(length(p/s)*s)-r;
}

float sdSphere(vec3 p, float r)
{
    return length(p)-r;
}

float sdPlane(vec3 p)
{
    return p.y;
}

float sdRoundCone( in vec3 p, in float r1, float r2, float h )
{
    vec2 q = vec2( length(p.xz), p.y );

    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(q,vec2(-b,a));

    if( k < 0.0 ) return length(q) - r1;
    if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

    return dot(q, vec2(a,b) ) - r1;
}

float sdCheese( vec3 p, vec2 h )
{
    p.zy *= rot(PI/2.0);
    p.xy *= rot(PI/2.0);
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// Scene setup

float map(vec3 pos)
{
    float t = 0.0;

    t = sdPlane(pos);

    // mouse
    vec3 mP = pos + vec3(-.3, 0.0, 2.0);
    float body = sdScaledSphere(mP, vec3(.5, .5, 1.0), 0.2);

    vec3 hP = mP + vec3(0.0, -0.15, 0.3);
    hP.zx *= rot(-PI/4.0+sin(iTime*10.)*.01);
    float head = sdSphere(hP, .15);
    vec3 eyeP = hP + vec3(0.05, -0.1, 0.1);
    head = sSDF(sdSphere(eyeP, .01), head);
    vec3 ore1P = hP + vec3(0.1, -0.1,  0.0);
    float ore1 = sdSphere(ore1P, .05);
    vec3 ore2P = hP + vec3(-0.1, -0.1,  0.0);
    float ore2 = sdSphere(ore2P, .05);
    vec3 musP = hP + vec3(0.0, -0.03, 0.15);
    float mus = sdSphere(musP, .05);
    vec3 stachP = hP + vec3(0.0, -0.05, 0.15);
    float stch = sdBox(stachP, vec3(.1, 0.002, 0.002));
    stachP = hP + vec3(0.0, -0.05, 0.15);
    stachP.xy *= rot(PI/8.0);
    stch = uSDF(stch, sdBox(stachP, vec3(.1, 0.002, 0.002)));
    stachP.xy *= rot(PI/-4.0);
    stch = uSDF(stch, sdBox(stachP, vec3(.1, 0.002, 0.002)));
    vec3 tailP = mP + vec3(0.2, 0.09, -0.3);
    tailP.zx *= rot(PI/16.0+sin(iTime)*0.1);
    float tail = sdCappedCylinder(tailP, 0.2, 0.1);
    tailP += vec3(0.03, 0.0, 0.1);
    tail = sSDF(sdCappedCylinder(tailP, 0.2, 0.2), tail);

    float ores = uSDF(ore1, ore2);
    float mouse = uSDF(body, head);
    mouse = uSDF(mouse, ores);
    mouse = uSDF(mouse, mus);
    mouse = uSDF(mouse, stch);
    mouse = uSDF(mouse, tail);

    // wall
    vec3 wallP = pos + vec3(0.0, -1.0, -2.0);
    float wallBase = sdBox(wallP, vec3(20.0, 30.0, 0.1));
    vec3 doorP = pos + vec3(2.0, 0.0, -2.0);
    float door = sdBox(doorP, vec3(1.0, 3.5, 1.0));
    float wall = sSDF(door, wallBase);

    // legs
    vec3 leg1P = pos + vec3(2.0, 1.0, -2.0);
    float leg1 = sdBox(leg1P, vec3(.15, 3.0, 0.15));
    vec3 leg2P = pos + vec3(2.6, 1.0, -2.0);
    float leg2 = sdBox(leg2P, vec3(.15, 3.0, 0.15));
    float legs = uSDF(leg1, leg2);
    vec3 pat1p = pos + vec3(2.0, -0.1, -1.8);
    float pat1 = sdBox(pat1p, vec3(.1, .1, .4));
    vec3 pat2p = pos + vec3(2.6, -0.1, -1.8);
    float pat2 = sdBox(pat2p, vec3(.1, .1, .4));
    float pats = uSDF(pat1, pat2);
    legs = uSDF(legs, pats);

    // cheese
    vec3 cP = pos + vec3(0.05, 0.0, 2.3);
    float cheese = sdCheese(cP, vec2(0.15, 0.1));
    cheese = sSDF(sdSphere(cP+vec3(0.1, -0.1, 0.0), .05), cheese);
    cheese = sSDF(sdSphere(cP+vec3(-0.1, -0.1, 0.0), .03), cheese);
    cheese = sSDF(sdSphere(cP+vec3(0.0, -0.1, -0.1), .05), cheese);
    cheese = sSDF(sdSphere(cP+vec3(0.0, -0.1, 0.1), .05), cheese);

    t = uSDF(t, mouse);
    t = uSDF(t, cheese);
    t = uSDF(t, wall);
    t = uSDF(t, legs);

    return t;
}

float CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<MAXSTEP ; i++)
    {
        float ray = map(ro + rd * t);

        if(ray < (0.0001*t))
        {
            float ao = float(i)/64.;
            return t;
        }

        t += ray;
    }

    return -1.0;
}

float GetShadow (vec3 pos, vec3 at, float k) {
    vec3 dir = normalize(at - pos);
    float maxt = length(at - pos);
    float f = 01.;
    float t = VOLUME*50.;
    for (float i = 0.; i <= 10.0; i += .1) {
        float dist = map(pos + dir * t);
        if (dist < VOLUME) return 0.;
        f = min(f, k * dist / t);
        t += dist;
        if (t >= maxt) break;
    }
    return f;
}

vec3 GetNormal (vec3 p) 
{ 
    vec2 e = vec2(0.01, 0.0); 
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
        )); 
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    float t = CastRay(ro, rd);
    vec3 pos = vec3(ro + rd * t);

    vec3 col = vec3(0.0);

    if(t == -1.0)
    {

        col = vec3(1.0+rd.y)*(1.0-step(rd.y, -0.2));
        col *= 1.0-step(uv.x, 0.5); // hide background color from left wall

    }
    else
    {
        vec3 N = GetNormal(pos);
        vec3 L = vec3(-4.0, 1.0, 8.0);
        float light = dot(N,L);
        float shade = GetShadow(pos, L, 8.0);
        float rim = smoothstep(1. - .9, 1.0, 1.0-dot(N, rd));

        col = vec3(1.0);
        col *= shade;
        col *= light;

    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 1.0 * forward);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

