const float PI = 2.*acos(0.);
const float doublePI = 2.*PI;
const float waveLength = 10.;
const float waveSpeed = 20.0;

float waveAngleOffset() {
    return iTime*waveSpeed; // loose precision arround 800sec	(because of big number in sin/cos ???)
    return mod(iTime*waveSpeed, doublePI);	
}    

float psin(in float angle) { return .5 * (1. + sin(angle)); }

vec2 polar(in vec2 p)
{
    float dist = length(p);
    float teta = dist > .0 ? asin(p.y/dist) : .0;	// -Pi/2 -> Pi/2
    if(p.x < .0)
    	teta = PI - teta;

    return vec2(dist, teta);
}    

float spiralPol(in vec2 pol)
{
	return psin((pol.x*doublePI * waveLength) + pol.y - waveAngleOffset());
}

float spiralXY(in vec2 xy) { return spiralPol(polar(xy)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;

    vec2 pol = polar(uv);

    float spiral = spiralPol(pol);
    // smooth a bit the center
    spiral = min(1., (pol.x*waveLength*2.)) * spiral;

	vec3 col = spiral * (.4+ .5*vec3(uv.xyy) * psin(pol.y - waveAngleOffset()));
    // Output to screen
    fragColor = vec4(col,1.0);
}

