float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float noise(float p){
	float fl = floor(p);
  float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}


float capsule( vec3 p, vec3 a, vec3 b, float r , float nmod)
{
    p.x += sin(p.y*2. + iTime*3.)*.2;
    p.z += cos(p.y*2. + iTime*3.)*.2;

    float shaping = .05*abs(sin(iTime*3. + p.x + p.z + p.y*4. + p.z))*nmod;
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r + shaping;
}

float sphere(vec3 p)
{
    //p.y *= 1.25;
    float shaping = .1*abs(sin(3.36 + p.x + p.z + p.y*4.));
    return length(p + bump(p)*.006) - .6+shaping;
}

float torus( vec3 p, vec2 t )
{
   	p.y *= .4;
  	vec2 q = vec2(length(p.xz)-t.x,p.y);
  	return length(q)-t.y;
}

float model(vec3 p)
{
    //p = opRep(p, vec3(10.));
    p*=.45;
    p.y -= 1.25;

    p.y += sin(iTime*3. + p.y*3.)*.075;
    p.x += sin(iTime*3. + p.y*3.)*.05;
    p.z += sin(iTime*3. + p.y*3.)*.05;

    // head
    float head = sphere(p);
    float torus = torus(p + vec3(0., .1 + sin(iTime*3.)*.05, 0.), vec2(.65, .05));

    head = opSmoothUnion(head, torus, .2);

    // tentacles
    vec3 cp = p + vec3(0., -.0, 0.);

    float tent = capsule(cp + vec3(-.2, .15, .2), vec3(0.), vec3(0., -3., 0.), .2, 2.);
    tent = min(tent, capsule(cp + vec3(.2, .15, .2), vec3(0.), vec3(0., -3., 0.), .2, 2.));
    tent = min(tent, capsule(cp + vec3(.2, .15, -.2), vec3(0.), vec3(0., -3., 0.), .2, 2.));

    for(float i = 0.; i < 15.; i++)
    {
    	tent = min(tent, capsule(cp, vec3(0.), vec3(sin(i + rand(i*10.)), -2., cos(i + rand(i))), .075, 1.));
    }

    return opSmoothUnion(tent*.9, head, .1);
}

float raymarch(in vec3 ro, in vec3 rd)
{
    float dist = 0.;
    for(int i = 0; i < 90; i++)
    {
		float m = model(ro+rd*dist);
        dist += m;

        if(m < .01) return dist;
        else if(dist > 20.) break;
    }
    return -1.;
}

vec3 normal(vec3 pos)
{
    vec3 eps = vec3(.01, -.01, 0.);

    return normalize(vec3(
        model(pos + eps.xzz) - model(pos + eps.yzz),
        model(pos + eps.zxz) - model(pos + eps.zyz),
        model(pos + eps.zzx) - model(pos + eps.zzy)));
}

float shadow(in vec3 pos, in vec3 ld)
{
    float spread = 3.;
    float res = 1.0;
	for(float t = .2; t < .4;)
    {
        float dist = model(pos+ld*t);
        if(dist<.001) return 0.;
        res = min(res, spread*dist/t);
        t += dist;
    }
    return res;
}

vec3 background()
{
    return vec3(.15, .05, .3);
}

vec3 shade(vec3 pos, vec3 nor, vec3 rd, float dist, vec3 ro)
{
    if(dist < 0.) return background();

    //vec3 ld = .6 * vec3(sin(iTime*.5 + .75), 1., cos(iTime*.5 + .75));
    vec3 ld = normalize(ro + vec3(1.));

    float dif = max(dot(nor,ld), 0.)*.6;
    float sha = 0.;
    if(dif > .01) sha = shadow(pos, ld);
    vec3 lin = vec3(dif*sha);

    // add color
    vec3 col1 = vec3(.2, .9, .8);
    vec3 col2 = vec3(.0, 0.3, .6);
    vec3 col = mix(col1, col2, lin)*.75;

    // bump & shift
    //float bump = bump(pos);
    //col = hueShift(col, bump*.25 + .75);
    //col.bg *= 2. + pow(1., 2.0)*.2;

    //col = hueShift(col, 4.25);
    //col = min(col*1.5, vec3(1.));
    //col *= pow(exp(-.02*dist*dist), .2);
    //col = pow(col, vec3(1.5));
    //col.rgb = mix(background(), col, exp(-.003*dist*dist)*1.0).rgb;

    //col *= 1.2;

    contrast(col, 1.5);

    col*=1.5;

    //col.rb *= .9;


    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - .5*iResolution.xy)/iResolution.y;
    p += noise(vec3(p.x*5., p.y*5., iTime))*.05;

    //vec3 ro = vec3(sin(iTime*.5), 1., cos(iTime*.5))*7.;
    //vec3 ro = vec3(6.);
    vec3 ta = vec3(0., 0., 0.);

    vec3 ro = vec3(cos(iTime/2.), 1., sin(iTime/2.))*7.;

    vec3 w = normalize (ta-ro);
    vec3 u = normalize (cross (w, vec3(0., 1., 0.)));
    vec3 v = normalize (cross (u, w));
    mat3 mat = mat3(u, v, w);
    vec3 rd = normalize (mat*vec3(p.xy,1.));

    float dist = raymarch(ro, rd);
    vec3 pos = ro+rd*dist;
    vec3 nor = normal(pos);

    vec3 col = shade(pos, nor, rd, dist, ro);
    col = vec3(dot(col.rgb, vec3(0.299, 0.587, 0.114)));

    // Output to screen
    fragColor = vec4(col,1.0);
}

/*
	Simple shader to test stereoscopic rendering
	Made for the 3rd issue of Cookie Fanzine that will be released soon
	https://fanzine.cookie.paris/

	Use glasses with red on the left eye and cyan on the right eye
*/

// put to 2 or more to get antialiasing
#define AA 1
// put to 1 to get less artifacts but more noise
#define DITHER 0

float time = 16.06;
// put to 0 to get a pretty fixed image
#if 1
	#define time2 (iTime*0.2)
	#define factor 1.0
#else
	#define time2 0.0
	#define factor 0.0
#endif


mat2 rot(float a) {
    float ca=cos(a);
    float sa=sin(a);
    return mat2(ca,sa,-sa,ca);    
}

float box(vec3 p, float s) {
    p=abs(p)-s;
    return max(p.x, max(p.y,p.z));
}

float box(vec2 p, float s) {
    p=abs(p)-s;
    return max(p.x, p.y);
}


float map(vec3 p) {

    float t3=time*2.0;
    t3=pow(smoothstep(0.0,1.0,fract(t3)),10.0)+floor(t3);
    t3*=2.0;
    t3 += sin(time2*0.5)+0.5*factor;

    vec3 bp=p;

    float m=10000.0;
    for(int i=0;i<4; ++i) {
        float t=0.7+float(i) + time*0.5;
        p.xz*=rot(t + sin(p.y * 0.075 - t3)*0.2);
        p.xy*=rot(t*0.7+sin(p.z*0.043 - t3)*0.3);
        p=abs(p);
        m=min(m, min(p.x,min(p.y,p.z)));
        p-=0.7 + sin(p.yzx*0.8 - time2*2.0)*0.4;
    }

    float d=m-0.0;
    d=abs(d-0.2)-0.1;

    float f=abs(length(p)-5.0)-0.1;
    f=min(f, abs(box(p.xz,0.5))-0.02);
    f=min(f, abs(box(p.xy,0.5))-0.02);
    f=min(f, abs(box(p.yz,0.5))-0.02);
    f=min(f, abs(box(bp,4.0))-0.1);
    d=max(d, f)-0.0;

 	return d;   
}

float raymarch(vec3 s, vec3 r, float off) {
    float val=0.0;
    vec3 p=s;
    for(int i=0; i<80; ++i) {
     	float d=abs(map(p))*off;
        if(d<0.01) {
            d=0.1;
            break;
        }
        val+=0.01/(0.8+d);
        p+=r*d;
    }
    return val;
}

#define ZERO (min(iFrame,0))

float rnd(vec2 uv) {
    return fract(dot(sin(uv*752.352+uv.yx*364.588), vec2(127.842)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 sum=vec3(0);

#if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
		vec2 uv = fragCoord-iResolution.xy*0.5 + o;
#else    
        vec2 uv = fragCoord-iResolution.xy*0.5;

#endif

        float dither  =0.6;
        #if DITHER
            dither = 0.55+rnd(uv)*0.1;
        #endif

        //uv /= iResolution.y;
        uv /= iResolution.x*0.56;

        vec3 s=vec3(0,0,-25.0 + sin(time*0.8)*0.0);
        vec3 t=vec3(0);

        float t2=time*0.3;
        s.xz *= rot(t2+sin(time2)*0.2+0.2*factor);
        s.xy *= rot(-t2*1.2+sin(time2*0.7)*0.3);

        vec3 cz=normalize(t-s);
        vec3 cx=normalize(cross(cz, vec3(0,1,0)));
        vec3 cy=normalize(cross(cz, cx));

        float eyeoff=0.5; // distance between eyes

        float fov = 1.0;
        float dist = 20.0; // distance of focus
        vec3 rbase = uv.x*cx+uv.y*cy+fov*cz;
        vec3 r1=normalize(vec3(rbase + cx * eyeoff / dist));
        vec3 r2=normalize(vec3(rbase - cx * eyeoff / dist));

        vec3 col=vec3(0);
        col.x=raymarch(s - eyeoff * cx, r1, dither);
        col.y=raymarch(s + eyeoff * cx, r2, dither);
        col.z=col.y;

        //col *= pow(1.2-length(uv),1.0);

        col = smoothstep(0.0,1.0,col);
        col = pow(col, vec3(0.4545));

        sum += col;
#if AA>1
    }
    sum /= float(AA*AA);
#endif



    // Output to screen
    fragColor = vec4(sum,1.0);
}

vec2 hash( vec2 x ) 
{
    const vec2 k = vec2( 0.9183099, 0.2678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    float n =  mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
    return 0.5 + 0.5*n;
}

float noise2(vec2 uv)
{
    float color;
    color += smoothstep(.1,.5,noise(uv)); // Black splatter
    color -= smoothstep(.1,.8,noise(uv)); // Holes on splatter
    return color/2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) + vec2(-iTime/200.,0);

    float time = iTime*5.;
    vec2 uv1 = uv*8.;
    float rand = noise(uv1+vec2(time));
    rand += noise(uv1+vec2(-time));
    rand += noise(uv1+vec2(time, -time));
    rand += noise(uv1+vec2(-time, time));
    rand += noise(uv1+vec2(time, 0.));
    rand += noise(uv1+vec2(-time, 0.));
    rand += noise(uv1+vec2(0., -time));
    rand += noise(uv1+vec2(0., time));

    rand /= 16.;  

    float colR = smoothstep(clamp(0.25+rand,0.,1.), clamp(0.7, 0., 1.), noise(uv*32.)+noise2(uv*8.));
    rand /= 4.;
    float colG = smoothstep(clamp(0.5+rand,0.,1.), clamp(1., 0., 1.), noise(uv*32.)+noise2(uv*8.));
    vec3 color = vec3(colR,colG,(colG+colR)/10.); 
    //vec3(noise2(uv*8.));
    fragColor = vec4(color,1.0);
}

