const float PI = 3.14159265359;

mat2 rotate(float a)
{
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

vec2 getPath(vec2 p)
{
    p.xy = rotate(cos(p.x+cos(p.x*0.4))*.0005)*p.xy;
    p.xy = rotate((-cos((p.x+.8)*.3)*.0004))*p.xy;
    p.xy = rotate((+cos((p.x+.5)*3.)*.00005))*p.xy;
	return p;
}

////////////////////////////////////////////////////////////////////////////////
//http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}
////////////////////////////////////////////////////////////////////////////////

void mapArm(inout float d, vec2 pf, float ti ) {
    float tt1 = abs(fract(pow(ti+.5,2.)*0.05)*2.-1.);
    float t1 = (tt1*2.-1.);

    float tt2 = abs(fract(pow(ti+.4,2.)*0.05)*2.-1.);
    float t2 = (tt2*2.-1.);

    vec2 pl = pf + vec2(.0,+.03 );
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pl,vec2(-.0,-0.0),vec2(0.025*t1,-.05))));  // arm up  
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pl,vec2(-.0,-0.0),vec2(-0.025*t2,-.05)))); // arm up

    vec2 pl1 = pl + vec2(-.025*t1,+.05);
    vec2 pl2 = pl + vec2( .025*t2,+.05);
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pl1,vec2(-.0,-0.0),vec2(0.025,-.02)))); // arm down 
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pl2,vec2(-.0,-0.0),vec2(0.025,-.02)))); // arm down
}

void mapLeg(inout float d, vec2 pf, float ti ){
    float tt1 = abs(fract(pow(ti*2.,2.)*0.05)*2.-1.);
    float t1 = (tt1*2.-1.);

    float tt2 = abs(fract(pow(ti*2.+.01,2.)*0.05)*2.-1.);
    float t2 = (tt2*2.-1.);

    vec2 pl = pf + vec2(.0,+.1 );
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pl,vec2(-.0,-0.0),vec2(0.025*t1,-.05))));  // leg up  
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pl,vec2(-.0,-0.0),vec2(-0.025*t2,-.05)))); // leg up

    vec2 pl1 = pl + vec2(-.025*t1,+.05);
    vec2 pl2 = pl + vec2( .025*t2,+.05);
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pl1,vec2(-.0,-0.0),vec2(-0.025+t1*.03,-.06)))); // leg down 
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pl2,vec2(-.0,-0.0),vec2(-0.025-t2*.03,-.06)))); // leg down
}

void mapBody(inout float d, vec2 pf){
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(pf,vec2(0.),vec2(.0,-.1)))); // body
}

void mapHead(inout float d, vec2 pf, float ti){
	d = min(d, 1.-smoothstep(0.,.2, .22-length(pf-vec2(0.,cos(pow(ti*1.+.01,2.))*.003)))); // head
}

void mapTheFlash(inout float d, vec2 pf, float ti){
    mapHead(d, pf, ti); // head
 	mapBody(d, pf);     // body
   	mapArm(d, pf, ti);  // arm
    mapLeg(d, pf, ti);  // leg
}

void drawWind(inout vec3 c, vec2 ph, vec2 pb , float ti){
    float ty = min( 0.01,  floor((pb.y+.05)*20.)/20.-.025) ;
    float tx =  -ti*ti*0.001;
    float d = min(1., 1.-smoothstep(0.,.2, .2-sdLine(pb+cos(vec2(0.,ti*ti*1.+pb.x*50.))*.01*pb.x,vec2(-0.01+ty,ty),vec2(tx+ty,ty)))); // wind
    c = mix(c,vec3(0), clamp(ti*.01,0.,.5)*clamp(clamp(pb.x*.15, -0.2, 0.) + .2-smoothstep(0.,.03,d), 0., 1.));
}

void drawWheelStar(inout vec3 c, vec2 pb, float ti){
        float timeStar = clamp(ti-3., 0., 1.);
        vec2 pf = pb-vec2(0.+cos(ti*.3)*1.+.8,-0.25);
        pf -= vec2(.5,0.1 +abs(cos(ti*2.))*.2);
        float aStar = cos(atan(pf.x,pf.y)*5.-ti*ti)*.1;
        c = mix(c,vec3(0), clamp(smoothstep(0.,.01, aStar*timeStar-length(pf)), 0., 1.) );
        c = mix(c,vec3(0), clamp(smoothstep(0.,.01, .01-abs(.1-length(pf))), 0., 1.) );
}

void drawSonic(inout vec3 c, vec2 pb, float ti) {
    float t1 = clamp(ti-37., 0., 1.); 
    float t2 = clamp(ti-63., 0., 1.); 
    float t3 = clamp(ti-62.5, 0., 1.); 
    float t1inv = 1.-t1; 
    float t2inv = 1.-t2; 
    vec2 pf = pb-vec2(t3/3.+cos(ti*.3)*1.-1.8-.3*(((((t1inv*t1inv*t1inv))))),-0.25 + 1.5*t1inv*cos(ti*1.3));
    pf -= vec2(.5,0.1 +abs(cos(ti*2.))*.12);
    float aStar = mod(atan(pf.x,pf.y)*5.-ti*ti,PI*1.)*.1;
    float ts = sin(t3*t3*PI*10.);
    c = mix(c,vec3(0),  clamp(smoothstep(0.,.01,  (1.-ts)*t2inv*t2inv*t1*.1- (.7+aStar)*length(pf)), 0., 1.) );
    c = mix(c,vec3(1), ts);
}

void drawGoku(inout vec3 c, vec2 p, float ti) {
    float t1 = clamp((ti-80.)/10., 0., 1.); 
    float t2= clamp((ti-90.)/1., 0., 1.); 
    vec2 pf = p;
    vec2 pg = vec2(4.*t1-2.,.4);
    float ft = mod(atan(pf.x,pf.y)*200.,1.)+.1;
    float d = ( smoothstep(0.,.25, .015-sdLine(pf,vec2(-2.,0.4),pg)));
    c = mix(c,vec3(0), (1.-t2)*clamp(smoothstep(0.,.01, d), 0., 1.) );
    c = mix(c,vec3(0), (1.-t2)*clamp(smoothstep(0.,.01, ft*.03-length((pf-pg)*vec2(.5,1.))), 0., 1.) );
}

void drawBoom(inout vec3 c, vec2 ph,  vec2 pb,  vec2 pt, float ti) {
    float t1 = clamp((ti-121.8)*5., 0., 1.);
    float t2 = clamp((ti-121.98)*5., 0., 1.); 

    float tx1 = pow(abs((122.)-1.)*.2,2.)/1.; 
    c = mix(c,vec3(0), clamp(smoothstep(0.,.01, .1-length(ph-vec2(tx1,1.-t1))), 0., 1.) );
    c = mix(c,vec3(0), clamp(smoothstep(0.,.01, t2-length(ph-vec2(tx1,1.-t1))), 0., 1.) );

    float d = 1.;
    d = min(d, 1.-smoothstep(0.,.2, .2-sdLine(ph,vec2(tx1,10.),vec2(tx1, 1.-t1))));
    c = mix(c,vec3(0), (1.-pow(t1,8.))*clamp( 1.-smoothstep(0.,.15,d), 0., 1.));
}
void mapGrass(inout float d, vec2 ph, vec2 pt, float ti) {
    vec2 rep = vec2(+floor(pt.x*.2)/.2+2.3,0.0);
	float tb = 0.;
    vec2 pbuild = vec2(mod(ph.x/10., .01)*10.-0.05, ph.y);
    d = min(d, 1.-smoothstep(0.,.1, .1-sdLine(pbuild,vec2(tb,0.),vec2(tb, abs(cos(ph.x*1.)+.5)*0.03)))); // bar grass
}

void mapStartBar(inout float d, vec2 ph, float ti) {
	d = min(d, 1.-smoothstep(0.,.2, .22-sdLine(ph,vec2(0.,0.0),vec2(0.,.2)))); // bar start
}

void mapCloudsBar(inout float d,  vec2 ph, vec2 pt, float ti) {
    d = min(d, 1.-smoothstep(0.,.2, .22-sdLine(ph,vec2(+floor(pt.x*.2)/.2+2.1,0.5),vec2(+floor(pt.x*.2)/.2+2.3,.5)))); // bar clouds
}

void drawTheFlash(inout vec3 c, vec2 pf, vec2 ph, vec2 pb, float ti) {
    float d = 1.;
    mapTheFlash(d, pf, ti);
    c = mix(c,vec3(0), 1.-smoothstep(0.,.01,d));
}
void drawBgScene(inout vec3 c, vec2 ph, vec2 pt, float ti) {
    float d = 1.;
    d = min(d, ph.y); 					// floor
 	mapStartBar( d,  ph, ti);			// start bar
    mapCloudsBar( d, ph, pt, ti);		// "cloud"
    mapGrass(d, ph, pt, ti);			// grass
    c = mix(c,vec3(0), 1.-smoothstep(0.,.01,d));
}
void drawFadeOutEnd(inout vec3 c, float tEnd, float tDuration, float ti){
    c = mix(c,vec3(0), clamp((ti-161.)/2., 0., 1.)); // fade out
}

float getTime(float totalTime) {
    return 1.*(fract((iTime+ 0.)/totalTime))*totalTime;
}

void setSlowMotion(inout vec2 p, inout float ti, float tStart, float tsDuration, float tEnd, float tsEnd){
    float tspeed = clamp((ti-tStart)/tsDuration, 0., 1.);
    float tspeed2 = 1.-clamp((ti-tEnd)/tsEnd, 0., 1.); // tsEnd... speed time to normal...
    float tsp = abs(tspeed*2.-1.);
    float speed = (max(.0,(pow( tsp *1.1, 3.)))*.5+.5);
    ti = ti - tspeed2*tspeed*6.7 ; 
    float timeBounce = clamp(ti*.3, 0., 1.);
