vec3 opRepLim( in vec3 p, in float c, in vec3 l)
{
    return p-c*clamp(round(p/c),-l,l);
}

// Scene setup
vec2 map(vec3 pos)
{
    vec2 scene = vec2(0.0, 0.0);

    vec3 bp = pos + vec3(0.0);
    bp = opRepLim(bp, 1.0, vec3(1.0));
    float box = sdBox(bp, vec3(.3));

    vec3 hp = pos + vec3(0.0);
    float heart = sdSphere(hp, 1.1+abs(sin(iTime*5.0)*.1));


    // materials
    scene = vec2(box, 0.0);
    scene = opU2(scene, vec2(heart, 1.0));

    return scene;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<128 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(float(i)/128., ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = vec3(ro + rd * t.x);

    vec3 col = vec3(0.0);
    vec3 polyCol = palette(t.x+fract(iTime), vec3(.5), vec3(.5), vec3(1.0), vec3(0.0, 0.33, 0.67));

    if(t.x == -1.0)
    {

        col = vec3(.1)*(length(uv)-.4);

    }
    else
    {
        float depth = 1.0 - t.x;

        if(t.y == 0.0)
        {
            col = vec3(depth)*.4;
            col.r -= sin(rd.y*5.0+iTime)*.5;
        }
        else if(t.y == 1.0)
        {
            col = polyCol;
        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    vec2 screenUV = fragCoord.xy / iResolution.xy;

    vec3 cp = vec3(sin(iTime), 1.0, cos(iTime))*5.0;
    vec3 ct = vec3(0.0, 0.0, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = Render(cp, vd, uv);

    fragColor = vec4(sqrt(clamp(col, 0.0, 1.0)),1.0);
}

#define MAX_MARCHING_STEPS 24

float distScene(vec3 position){
    position = fract(position)*2.-1.;
    float sphere = length(position) - .15;
    return sphere;
}
float trace(vec3 ro, vec3 rd){
	float depth = 0.;
    for(int i = 0;i < MAX_MARCHING_STEPS;i++){
    	vec3 current = ro + depth * rd;
        float t = distScene(current);
        depth += t*.5;
    }
    return depth;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    vec3 ro = vec3( iTime*.2, iTime*.2, iTime*0.1);

    float angle = iTime*.001;
    ro.xz *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec3 rd = normalize(vec3(uv,1.));
    float t = trace(ro, rd);
    float fog = 1./(1.+t*t*.1);
    fragColor = vec4((vec3(uv.x+1.5,uv.y-1.,1.)*fog),1.0);
}

float rand(vec2 id)
