 //   i.color = m < m2 ? kColor : cColor;
}

vec3 normal(vec3 p, float s){

    vec2 e = vec2(0.0001, 0);
    float d = dfm(p, s);
    vec3 n = d - vec3(
        dfm(p - e.xyy, s),
        dfm(p - e. yxy, s),
        dfm(p - e. yyx, s));
    return normalize(n);
}

