void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 cameraPos = vec3(0, 0, -10);
    float screenZ = 2.5;
    vec3 rayDir = normalize(vec3(uv, screenZ));
    vec3 lightDir = normalize(vec3(1.0, 1.0, -10.));

    float size = 1.0;
    float depth = 0.0;
    float dist = 0.0;
    vec3 rayPos = vec3(0.0);
    vec3 col = vec3(0.0);

    for(int i = 0; i < 99; i++){

      	rayPos = cameraPos + (rayDir * depth);
        dist = dfm(rayPos, size);

        if(dist < 0.0001) break;

        depth += dist;
    }


    if(dist < 0.0001){

      	vec3 n = normal(rayPos, 1.0);
        float diff = dot(n ,lightDir);
        float im = image(uv);
        vec3 c1 = vec3(0.5, 0.4, 0.9);
        vec3 c2 = vec3(0.);
        col = mix(c1, c2, diff); 
        col += image2(uv);
        col /= image(uv);
    }else{
        float d = image(uv);
        vec3 c1 = vec3(0.0, 0.2, 0.9);
        c1 -= vec3(0.5);
        vec3 c2 = vec3(0.9, 0.9, 0.2);
        col = mix(c2, c1, d);  
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

