float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdOctahedron( in vec3 p, in float s)
{
    p = abs(p);
    float m = p.x+p.y+p.z-s;
    vec3 q;
         if( 3.0*p.x < m ) q = p.xyz;
    else if( 3.0*p.y < m ) q = p.yzx;
    else if( 3.0*p.z < m ) q = p.zxy;
    else return m*0.57735027;

    float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
    return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

// Scene setup
vec2 map(vec3 pos)
{
    vec2 scene = vec2(0.0, 0.0);

    float time = iTime*4.0;

    pos.z += time;
    pos.y += sin(pos.z)*.5;

    vec3 fwp = pos+vec3(0.0);
    fwp = opRep(fwp, vec3(0.0, 0.0, 1.0));
    float fw = sdBox(fwp, vec3(1.5));

    vec3 sfwp = pos+vec3(0.0);
    sfwp.zy *= rot(PI/2.0);
    sfwp.x += sin(pos.z*5.0)*.1;
    sfwp = opRep(sfwp, vec3(0.0, 1.0, 0.0));
    float sfw = sdCappedCylinder(sfwp, 1.0, 2.0);

    fw = max(fw, -sfw);

    // heart
    vec3 hp = pos + vec3(0.0, 0.0, -time);
    hp.xz *= rot(iTime*.9);
    hp.yx *= rot(iTime*.74);
    hp.x += sin(iTime)*.3;
    hp.z += sin(iTime)*.4;
    float heart = sdOctahedron(hp, .4+abs(sin(iTime*10.)*.1));


    // materials
    scene = vec2(fw, 0.0);
    scene = opU2(scene, vec2(heart, 1.0));

    return scene;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<128 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(float(i)/128., ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = vec3(ro + rd * t.x);

    vec3 col = vec3(0.0);
    vec3 polyCol = palette(t.x+fract(iTime), vec3(.5), vec3(.5), vec3(1.0), vec3(0.0, 0.33, 0.67));

    if(t.x == -1.0)
    {

        col = vec3(.1)*(length(uv)-.4);

    }
    else
    {
        float depth = 1.0 - t.x;

        if(t.y == 0.0)
        {
            col = vec3(depth)*.4;
        }
        else if(t.y == 1.0)
        {
            col = polyCol - depth*.5;
        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    vec2 screenUV = fragCoord.xy / iResolution.xy;

    vec3 cp = vec3(0.0, sin(iTime*2.0)*.25, -5.0);
    vec3 ct = vec3(0.0, 0.0, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = Render(cp, vd, uv);

    col.r -= uv.y*.5;

    col -= ThirdRules(screenUV)*.1;

    col -= clamp((length(uv))*.3, 0.0, 1.0);

    fragColor = vec4(sqrt(clamp(col, 0.0, 1.0)),1.0);
}

