	for(int i=1;i<45;i++)
	{
		vec2 newp=p;
		newp.x+=(0.5/(1.5*float(i)))*cos(float(i)*p.y+iTime*11.0/37.0+0.03*float(i))+1.3;		
		newp.y+=(0.5/(1.5*float(i)))*cos(float(i)*p.x+iTime*17.0/41.0+0.03*float(i+10))+1.9;
