    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);

    vec2 iPos = floor(uv);


    for(float i =0.; i < 12.; i++){
    	vec2 pos = vec2(sin(sin(iTime * i * 0.2) + i + iTime * 0.5), rand(vec2(0.5 * i, i)) + sin(iTime + i) * 0.5) + vec2(0., 0.5);
        float ball = 1.3 - distance(pos, uv);

        ball = smoothstep(1., .0, ball) * 0.05;

        //ball = floor(ball + 0.2);
        col += vec3(ball * rand(vec2(i * 20., i * 2.)) * 20., 
                                ball * rand(vec2(sin(i) * 1281., i * 2.)), 
                                ball * rand(vec2((i + 0.5) * 100., i * 120.)) * 20.) * 3.;

    }

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/vec2(iResolution.y);
	float freq = 30.0;
    float Fade = 20.0;
    float i = 0.07;
    //float i = 0.0;
    float AddBri = 1.0;
    vec2 CirCen0 = vec2(0.9-(cos((iTime+5.0)*0.27)*0.9),0.5+(0.5*cos(iTime*.33)));
    vec2 CirCen1 = vec2(0.9-(cos(iTime*0.59)*0.9),0.5+(0.5*cos((iTime+5.0)*.41)));
    float MyDist0 = distance(CirCen0,uv);   
    float MyDist1 = distance(CirCen1,uv);

    float DarknessR = 0.0;
    float DarknessY = 0.0;
    float DarknessG = 0.0;
    float DarknessB = 0.0;
    float DarknessV = 0.0;

    DarknessR += cos(MyDist0*freq)/((MyDist0*MyDist0*Fade)+1.0);
    MyDist0 += i;
    DarknessY += cos(MyDist0*freq)/((MyDist0*MyDist0*Fade)+1.0);
    MyDist0 += i;
    DarknessG += cos(MyDist0*freq)/((MyDist0*MyDist0*Fade)+1.0);
    MyDist0 += i;
    DarknessB += cos(MyDist0*freq)/((MyDist0*MyDist0*Fade)+1.0);
    MyDist0 += i;
    DarknessV += cos(MyDist0*freq)/((MyDist0*MyDist0*Fade)+1.0);    

    DarknessR += cos(MyDist1*freq)/((MyDist1*MyDist1*Fade)+1.0);
	MyDist1 += i;
    DarknessY += cos(MyDist1*freq)/((MyDist1*MyDist1*Fade)+1.0);
    MyDist1 += i;
    DarknessG += cos(MyDist1*freq)/((MyDist1*MyDist1*Fade)+1.0);
    MyDist1 += i;
    DarknessB += cos(MyDist1*freq)/((MyDist1*MyDist1*Fade)+1.0);
    MyDist1 += i;
    DarknessV += cos(MyDist1*freq)/((MyDist1*MyDist1*Fade)+1.0);    

    DarknessY *= 2.0;
    DarknessB *= 2.0;

    DarknessR = DarknessR + DarknessY + DarknessV;
    DarknessG = DarknessG + DarknessY;
    DarknessB = DarknessB + DarknessV;

	vec3 col = vec3(DarknessR,DarknessG,DarknessB);

    fragColor = vec4(col,1.0);
}

// Andi Smithers 
// button shader for smooth border edges
// includes shadow both internal and drop shadow
// 9 patch is basically preserving the edge with rectangle and doesnt stretch the image

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float progress = mod(iTime, 1.0);
    vec4 barCol = vec4(1,1,0,1);
    vec4 backCol = vec4(1,0,0,1);
    // uniforms
    float borderSize = 0.05;
    float antiAliasSize = 0.03;
    vec4 borderColor = vec4(1.0,1.0,0.95, 1);
    vec4 centreColor = vec4(0.1,0.5,0.9,1);
    float wobbly = iTime;
	float rounding = sin(wobbly)*3.0+4.0;
    vec2 shadowVec = vec2(-0.01, 0.05);
    float scaleBox = 1.2;

    // alias thickness
    float aascalar = 1.0 / antiAliasSize;
    float outerAlias = 1.0 - antiAliasSize;

    // convert UV into normalized coord
    vec2 iuv = fragCoord.xy / iResolution.xy;
	vec2 uv = fragCoord.xy / iResolution.y;

    // compute 9patch centre - xaxis
    float centre = (iResolution.x - iResolution.y)/iResolution.x * 0.5 ;
    if (abs(iuv.x-0.5)<centre) uv.x=0.5;
    if(iuv.x>centre+0.5) uv.x-=0.5 + (iResolution.y/iResolution.x) * centre*2.0;

    vec2 uvUnit = (uv*2.0-1.0) * scaleBox;
    vec2 shadowUnit = uvUnit + shadowVec;

    // rounded edge - have to abs the value incase shader doesnt support negative powers
    uvUnit = pow(abs(uvUnit), vec2(rounding));
    shadowUnit = pow(abs(shadowUnit), vec2(rounding));

    // compute border
    float borderEdge = pow(1.0-borderSize, rounding);
    float borderAlias = borderEdge - antiAliasSize;

    // edge
    float edge = length(uvUnit);
    float outer = 1.0-(clamp(edge, outerAlias, 1.0)-outerAlias) * aascalar;
    float inner = 1.0-(clamp(edge, borderAlias, borderEdge)-borderAlias) * aascalar;
    float shadowInner = 1.0-(clamp(length(shadowUnit), borderAlias, borderEdge)-borderAlias) * aascalar;
    float shadowOuter = 1.0-(clamp(length(shadowUnit), outerAlias, 1.0)-outerAlias) * aascalar;

    // blow out outer then chose minimum this ensures a mask around the alpha output
    float outermask = min(outer*1000.0, 1.0);

    // fetch image and poor mans chromakey alpha
    float blend = ((iuv.x - progress) <= 0.0 ? 1.0 : 0.0);
    vec4 image = blend*barCol + backCol*(1.0-blend);
    //image*= texture(iChannel0, iuv); if you want it textured
    float alpha = length(image.rgb-vec3(0,1,0));
    alpha = alpha> 0.5 ? 1.0 : 0.0;

    // shadow
    float shade = shadowInner<0.9 ? 0.8 + shadowInner*0.2: 1.0;
    float shadeOut = shadowOuter>0.5 ? 0.0 + shadowOuter*0.2: 0.0;	

    // blend inner/outer and image with image alpha
    vec4 color = (centreColor*inner*(1.0-alpha)+image*alpha*inner)*shade + borderColor*(1.0-inner)*outermask;
    color.a =  max(outer, shadeOut);

    // blend against a background texture - because shadertoy
    vec4 background = texture(iChannel1, iuv);

    // and set the final color 
    fragColor = (color*color.a) + background*(1.0-color.a);
}

#define max_marches 100
#define max_distance 100.
#define surface_distance .001
#define max_recursions 3
#define light_pos vec3(cos(iTime * .125) * 3., 5, sin(iTime * -.241) * 2. - 1.)

float sdf_sphere(vec3 p) {
	return length(p);
}

float sdf_cube(vec3 p) {
	p = abs(p);
    return max(p.x, max(p.y, p.z));
}

float sdf_plane(vec3 p) {
	return p.y;
}

float sdf_scene(vec3 p) {
    float plane0 = sdf_plane(p - vec3(0, -.5, 0));
	float sphere0 = sdf_sphere(p - vec3(-1.5, .5, 0)) - 1.;
    float sphere1 = sdf_sphere(p - vec3(-3.2, 2, -1)) - .5;
    float cube0 = sdf_cube(p - vec3(1., 0, 0)) - .5;
    float cube1 = sdf_cube(p - vec3(.8, 1, .3)) - .5;
    float cube2 = sdf_cube(p - vec3(-1, .1, 3.)) - .6;
    float cube3 = sdf_cube(p - vec3(-1, -.3, -2.)) - .2;
    float cube4 = sdf_cube(p - vec3(-4, .5, -1)) - 1.;
    float cube5 = sdf_cube(p - vec3(-3.5, -.3, -2.5)) - .2;

    return min(min(min(plane0, sphere0), sphere1), min(min(min(cube0, cube1), min(cube2, cube3)), min(cube4, cube5)));
}

struct material_t {
    vec3 diffuse;
    float fresnelity;
    float reflectivity;
    float refractivity;
    float refract_eta;
    float density;
};

material_t material(vec3 p) {
    float plane0 = sdf_plane(p - vec3(0, -.5, 0));
	float sphere0 = sdf_sphere(p - vec3(-1.5, .5, 0)) - 1.;
    float sphere1 = sdf_sphere(p - vec3(-3.2, 2, -1)) - .5;
    float cube0 = sdf_cube(p - vec3(1., 0, 0)) - .5;
    float cube1 = sdf_cube(p - vec3(.8, 1, .3)) - .5;
    float cube2 = sdf_cube(p - vec3(-1, .1, 3.)) - .6;
    float cube3 = sdf_cube(p - vec3(-1, -.3, -2.)) - .2;
    float cube4 = sdf_cube(p - vec3(-4, .5, -1.)) - 1.;
    float cube5 = sdf_cube(p - vec3(-3.5, -.3, -2.5)) - .2;
    float d = min(min(min(plane0, sphere0), sphere1), min(min(min(cube0, cube1), min(cube2, cube3)), min(cube4, cube5)));
   	material_t m;

    m.diffuse = vec3(1, 1, 1);
    m.fresnelity = .2;
    m.reflectivity = .3;
    m.refractivity = .0;
    m.refract_eta = .95;
    m.density = 0.;

   	if (sphere0 == d || cube3 == d || cube5 == d) {
        m.diffuse = vec3(1, 1, 1);
        m.fresnelity = .6;
        m.reflectivity = .1;
        m.refractivity = .8;
    } else if (cube0 == d || cube1 == d || cube2 == d) {
    	m.diffuse = vec3(1, 0, 0);
        m.fresnelity = .4;
        m.reflectivity = .1;
    } else if (cube4 == d || sphere1 == d) {
    	m.diffuse = vec3(1, 1, 1);
        m.reflectivity = .8;
        //m.refractivity = ;
    }

    return m;
}

bool march(in vec3 ro, in vec3 rd, out vec3 p) {
    p = ro;
    float lO = 0.f;

    for (int i = 0; i < max_marches; i++) {
    	float l = sdf_scene(p);
        lO += l;
        p += l * rd;

        if (l < surface_distance)
            return true;
        if (lO > max_distance)
            return false;
    }

	return false;
}

bool inner_march(in vec3 ro, in vec3 rd, out vec3 p) {
    p = ro;
    float lO = 0.f;

    for (int i = 0; i < max_marches; i++) {
    	float l = -sdf_scene(p);
        lO += l;
        p += l * rd;

        if (l < surface_distance)
            return true;
        if (lO > max_distance)
            return false;
    }

	return false;
}

// internal_reflection with thickness

vec3 normal(vec3 p) {
	vec2 e = vec2(0, .001);
    float l = sdf_scene(p);

    vec3 n = l - vec3(
    	sdf_scene(p - e.yxx),
        sdf_scene(p - e.xyx),
        sdf_scene(p - e.xxy)
    );

    return normalize(n);
}

vec3 inner_normal(vec3 p) {
	vec2 e = vec2(0, .001);
    float l = sdf_scene(p);

    vec3 n = l - vec3(
    	sdf_scene(p - e.yxx),
        sdf_scene(p - e.xyx),
        sdf_scene(p - e.xxy)
    );

    return normalize(n);
}


struct ray_t {
    vec3 ro;
    vec3 rd;
    float dist;
    float lum;
    int depth;
};

void mainImage(out vec4 frag_color, in vec2 frag_coord)
{
    vec2 uv = (frag_coord - iResolution.xy * .5) / iResolution.y;
    vec2 m = (iMouse.xy - iResolution.xy * .5) / iResolution.y;
    vec3 c = vec3(0.);

    vec3 ro = vec3(vec2(-1, 2) + m * 5., -5);
    vec3 rd = normalize(vec3(uv - vec2(0, .2), 1));

    vec3 diff_r = .8 * vec3(1, 0, 0);
    vec3 spec_r = .2 * vec3(0, 1, 0);

    ray_t rays[int(pow(2., float(max_recursions)))]; // TOOD validate size
    int rays_size = 0;
    rays[0].ro = ro;
    rays[0].rd = rd;
    rays[0].dist = 1.;
    rays[0].lum = 1.;
    rays[0].depth = 0;

    while (rays_size > -1) {
        vec3 ro = rays[rays_size].ro;
        vec3 rd = rays[rays_size].rd;
        float dist = rays[rays_size].dist;
        float lum = rays[rays_size].lum;
        int depth = rays[rays_size].depth;
        --rays_size;

        vec3 p0;
        if (march(ro, rd, p0)) {
            material_t m0 = material(p0);
        	vec3 n0 = normal(p0);

            vec3 surface = n0 * surface_distance * 2.;
            vec3 ro_outside = p0 + surface;
            vec3 rd_light = normalize(light_pos - ro_outside);
            float d0 = length(ro_outside - ro);
            float dl = length(light_pos - ro_outside);
            float da = dist + d0 + dl;
            float attenuation = da * da * .001 + 1.;
            float reflectivity = 1. - max(0., dot(rd, -n0)); // fresnel
            reflectivity = min(1., min(1., pow(reflectivity, 8. - m0.fresnelity * 6.)) * m0.fresnelity + m0.reflectivity);
            float refractivity = (1. - reflectivity) * m0.refractivity;
            float diffusity = 1. - reflectivity - refractivity;

            vec3 p_light;
            if (!march(ro_outside, rd_light, p_light) || dl < length(p_light - ro_outside) + .01) {
            	c += lum * diffusity * m0.diffuse * max(0., dot(n0, rd_light)) / attenuation;
            }

            if (depth < max_recursions) {
            	++rays_size;
                rays[rays_size].ro = ro_outside;
                rays[rays_size].rd = reflect(rd, n0);
                rays[rays_size].dist = dist + d0;
                rays[rays_size].lum = lum * reflectivity;
                rays[rays_size].depth = depth + 1;

                vec3 rd_refract = refract(rd, n0, m0.refract_eta);
                vec3 ro_inside = p0 - surface;

                vec3 p_inside;
                if (inner_march(ro_inside, rd_refract, p_inside)) {
                    vec3 n_inside = normal(p_inside);
                    vec3 surface_inside = n_inside * surface_distance * 2.;
                    vec3 ro_refract_outside = p_inside + surface_inside;
                    float di = length(p_inside - ro_inside);

                    ++rays_size;
                    rays[rays_size].ro = ro_refract_outside;
                    rays[rays_size].rd = refract(rd_refract, -n_inside, 1. / m0.refract_eta);
                    rays[rays_size].dist = dist + d0 + di;
                    rays[rays_size].lum = lum * refractivity;
                    rays[rays_size].depth = depth + 1;
                }
            }
        }

        // follor ray -> color
        // extra rays
        // light, bears law, reflection, refraction, inner reflection
    }


    frag_color = vec4(c, 1.);
}

