Triangle T1 = Triangle(vec3(5.0,5.0,-1.0), vec3(5.0,-5.0,-1.0), vec3(-5.0,5.0,1.0), vec3(0.7,0.7,0.7));
Triangle T2 = Triangle(vec3(5.0,-5.0,-1.0), vec3(-5.0,-5.0,1.0), vec3(-5.0,5.0,1.0), vec3(0.8,0.8,0.8));
Triangle T3 = Triangle(vec3(-5.0,5.0,1.0), vec3(5.0,-5.0,-1.0), vec3(5.0,5.0,-1.0), vec3(0.7,0.7,0.7));
Triangle T4 = Triangle(vec3(-5.0,5.0,1.0), vec3(-5.0,-5.0,1.0), vec3(5.0,-5.0,-1.0), vec3(0.8,0.8,0.8));

struct SceneObjects{
    Sphere[5] spheres;
    Triangle[4] triangles;
};
SceneObjects objects;

vec3 checkCollide(vec3 Origin, vec3 rayDir){
    float closestP = -1.0;
    int objectNum = -1;
    int SphereOrTriangle = -1;
    for(int i = 0; i<objects.spheres.length(); i++){
        Sphere tempS = objects.spheres[i];

        vec3 L = (tempS.pos-Origin);
    	float tca = dot(L,rayDir);


    	if(tca<=0.0){
    	   	continue;
    	}

    	float d = sqrt(dot(L,L)-(tca*tca));

    	if(d>=tempS.r && d!=0.0){
    	    continue;
    	}

    	float thc = sqrt((tempS.r*tempS.r)-(d*d));
    	float distToPoint= tca-thc;

        if(i==0||closestP==-1.0){
            closestP = distToPoint;
            SphereOrTriangle = 0;
        	objectNum = i;
        }
        else if(distToPoint<closestP){
            closestP = distToPoint;
            SphereOrTriangle = 0;
        	objectNum = i;
        }

    }

    for(int i = 0; i<objects.triangles.length(); i++){
        Triangle tempT = objects.triangles[i];

        vec3 normal = cross((tempT.Vert1-tempT.Vert2),(tempT.Vert1-tempT.Vert3));

        //Test if ray is parallel to plane 
        if(dot(rayDir,normal)<0.001){
            continue;
        }

        vec3 p0l0 = tempT.Vert1 - Origin;

        //Dist to Intersection Point on plane
        float distToPoint = dot(p0l0,normal) / dot(rayDir,normal);
        if(distToPoint<0.0){
            continue;
        }

        vec3 P = Origin + rayDir*distToPoint; //Point of intersection

        //Check to see if ray is within the triangle
        vec3 C;
        //edge 1
        vec3 edge1 = tempT.Vert2 - tempT.Vert1;
		vec3 vp1 = P - tempT.Vert1;
        C = cross(edge1,vp1);
        if(dot(normal,C)<0.0){
            continue;
        }
        //edge 2
        vec3 edge2 = tempT.Vert3 - tempT.Vert2;
		vec3 vp2 = P - tempT.Vert2;
        C = cross(edge2,vp2);
        if(dot(normal,C)<0.0){
            continue;
        }
        //edge 3
        vec3 edge3 = tempT.Vert1 - tempT.Vert3;
		vec3 vp3 = P - tempT.Vert3;
        C = cross(edge3,vp3);
        if(dot(normal,C)<0.0){
            continue;
        }



        if(closestP==-1.0){
            closestP = distToPoint;
            SphereOrTriangle = 1;
        	objectNum = i;
        }
        else if(distToPoint<closestP){
            closestP = distToPoint;
            SphereOrTriangle = 1;
        	objectNum = i;
        }

    }

    return vec3(closestP,SphereOrTriangle,objectNum);


}

bool checkShadow(vec3 P){
    vec3 ray = light.pos-P;
    vec3 rayDir = ray/length(ray);
    //P = P-(0.00001*rayDir);


    float distToPoint = checkCollide(P,rayDir).x; //Checks collison to light

    //If the ray has collided, and it is closer than the light source
    if(distToPoint!=-1.0 && length(ray)>distToPoint){ 
        return true;
    }
    return false;
}

void castRay(vec2 xy){
    float ratio = Cam.dir.z/length(Cam.dir.xy);
    vec3 VertPerp = vec3(-Cam.dir.x*ratio, -Cam.dir.y*ratio, length(Cam.dir.xy));
    vec3 yComp = VertPerp * xy.y;

    vec3 HoriPerp = vec3(Cam.dir.y,-Cam.dir.x,0.0)/length(vec3(Cam.dir.y,-Cam.dir.x,0.0));
    vec3 xComp = HoriPerp * xy.x;

    vec3 ray = (Cam.dir*3.0)+xComp+yComp;
    vec3 rayDir = (ray/length(ray));

    vec3 Collision = checkCollide(Cam.pos,rayDir);
    if(Collision.x>0.0){
        if(int(Collision.y)==0){ //Sphere
        	pixCol = objects.spheres[int(Collision.z)].col;
            vec3 P1 = Cam.pos + (rayDir*Collision.x);
        	if(checkShadow(P1)){ //Checks if in shadow
            	pixCol = objects.spheres[int(Collision.z)].col/3.0;
        	}
        }
        if(int(Collision.y)==1){ //Triangle
            Triangle tempT = objects.triangles[int(Collision.z)];
            pixCol = tempT.col;
            vec3 P1 = Cam.pos + (rayDir*Collision.x);
            vec3 normal = cross((tempT.Vert1-tempT.Vert2),(tempT.Vert1-tempT.Vert3));
            P1 = P1 + normal*(-0.00001);
        	if(checkShadow(P1)){ //Checks if in shadow
           		pixCol = objects.triangles[int(Collision.z)].col/3.0;
        	}
        }
    }
}

