    return texelFetch( NOISE_TEXTURE_3D, ivec3(vec3(HF,HG,HH)*st)&31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(vec2(HF,HG)*st)&255,0).x;
    # else
    return fract(sin(st)*HE); 
    #endif
}

float hash21(vec2 st) {
    #if defined(NOISE_TEXTURE_3D)
    return texelFetch( NOISE_TEXTURE_3D, ivec3(st*HE,(st.x*st.y)*HE) &31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(st*HE)&255,0).x;
    # else
    return fract(sin(dot(sin(st.xy),vec2(HE,HF)))*HG);
    #endif
}

float hash31(vec3 st) {
    #if defined(NOISE_TEXTURE_3D)
    return texelFetch( NOISE_TEXTURE_3D, ivec3(st*HE)&31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(st.x*HE+st.z*HF,st.y*HG+st.z*HH)&255,0).x;
    # else
    return fract(sin(dot(sin(st),vec3(HE,HF,HG)))*HH);
    #endif
}


//FIXME: find alternativs with texture lookup
vec3 hash13(float st) {
    return fract(sin(st*vec3(HE,HF,HG))*HH);
}

vec3 hash23(vec2 st) {
    return fract(sin(st.xyx*vec3(HE,HF,HG))*HH);
}

vec3 hash33(vec3 st) {
    return fract(sin(st*vec3(HE,HF,HG))*HH);
}

// ========== ==========

// ========== Coherent Noise ==========

//Quintic interpolation curve
vec2 quinticInterpolation(vec2 x) {
    return x*x*x*(x*(x*6.-15.)+10.);
}

float quinticInterpolation(float x) {
    return x*x*x*(x*(x*6.-15.)+10.);
}

// - Value Noise, 2d, Unsigned
float valueNoise2du(vec2 samplePoint) {
    vec2 pointI = floor(samplePoint);
    vec2 pointF = fract(samplePoint);
    float bl = hash21(pointI + vec2(0.0,0.0) );
    float br = hash21(pointI + vec2(1.0,0.0) );
    float fl = hash21(pointI + vec2(0.0,1.0) );
    float fr = hash21(pointI + vec2(1.0,1.0) );
    //vec2 u = smoothstep(0.0,1.0,pointF);
    vec2 u = quinticInterpolation(pointF);
    float b = mix(bl,br,u.x);
    float f = mix(fl,fr,u.x);

    return mix(b,f,u.y);
}

// - Value Noise, 1d, Unsigned
float valueNoise1du(float samplePoint) {
    float pointI = floor(samplePoint);
    float pointF = fract(samplePoint);
    float b = hash11(pointI);
    float f = hash11(pointI + 1.0 );
    float u = quinticInterpolation(pointF);
    return mix(b,f,pointF);
}

// - Value Noise, 1d, Unsigned, Wraped
float valueNoise1duw(float samplePoint, float wrap) {
    float pointI = floor(samplePoint);
    float pointF = fract(samplePoint);
    float b = hash11(mod(pointI, wrap));
    float f = hash11(mod(pointI + 1.0 ,wrap));
    float u = quinticInterpolation(pointF);
    return mix(b,f,pointF);
}

// ========== ==========

// ========== Distance Feilds ==========

vec3 ClosestRayPoint(ray ray, vec3 point) {
    return ray.origin + max(0.0,dot(point - ray.origin, ray.direction)) * ray.direction;
}

float DistanceRayPoint(ray ray, vec3 point) {
    return (distance(point,ClosestRayPoint(ray, point) )) ;   
}

float DistancePointSphere(sphere sphere, vec3 point) {
    float sphereDistance = distance(point, sphere.origin) - sphere.radius;
    return sphereDistance; 
}

// ========== ==========

// ========== Clouds ==========

float getCloudNoise(vec2 origin, float time) {
    origin.x-= time*0.1;
    origin.y-= time*0.5;

    float r = valueNoise2du(origin*0.3);
    r+= valueNoise2du(origin*1.3)*0.66;
    r+= valueNoise2du(origin*2.3)*0.33;
    r*= valueNoise2du(origin*0.2);
    r*=0.5;
    return clamp(r,0.0,1.0);
}

float getClouds(vec3 direction, float time) {
    //Dont draw clouds very low on the horizon or ground.
    if (direction.y<=0.04) {
        return 0.0;
    }

    vec2 s = direction.xz;
    s *= (1000.0)/direction.y;
    s *= 0.001;
    //slowly fade out the clouds at distance, to get rid of aliasing issues.
    return getCloudNoise(s,time)*(clamp(0.0,0.1,(direction.y-0.04))*10.0);
}

// ========== ==========

// ========== Terrain ==========

float getTerrainHeight(vec2 point) {
    const int octaves = 3;
    float lacunarity = 2.0;
    float gain = 0.5;
    float amplitude = 1.5;
    float frequency = 0.2;

    float height = 0.0;

    for (int i = 0; i < octaves; i++) {
        height += amplitude * (sin(frequency*point.x*0.5)*cos(frequency*point.y+point.x)*0.5+0.5);
        frequency *= lacunarity;
        amplitude *= gain;
    }
    //Add surface ripples in the dips, moved to shading to reduce overhead, adds errors at small scale but its not notisable
    //height += ((sin(30.0*point.x)*sin(1.5*point.y+10.0*point.x)*0.5+0.5)*0.05)*(smoothstep(0.3,2.0,1.7-height));
    return height;
}

vec3 getTerrainNormal(vec2 point, float hc) {

    #if defined(DEBUG_SAND_3POINT_NORMAL)
    float hl = getTerrainHeight(point + vec2(-0.01,0.0));
    float hf = getTerrainHeight(point + vec2(0.0,-0.01));
    return normalize(vec3(
        hl - hc,
        0.01,
        hf - hc
    ));
    #else
    float hl = getTerrainHeight(point + vec2(-0.01,0.0));
    float hr = getTerrainHeight(point + vec2(0.01,0.00));
    float hf = getTerrainHeight(point + vec2(0.0,-0.01));
    float hb = getTerrainHeight(point + vec2(0.0,0.01));
    return normalize(vec3(
        hl - hr,
        0.02,
        hf - hb
    ));
    #endif    

}

void getTerrainHit(vec3 currentPoint, inout rayMarchHit hit, float minSurfaceDistance) {
    float heightCenter = getTerrainHeight(currentPoint.xz);

    float newDistance = (currentPoint.y - heightCenter);

    if (newDistance < hit.distance) {
        hit.distance = newDistance;        
        //if (newDistance <= minSurfaceDistance) {
        hit.origin = currentPoint;
        hit.surfaceNormal = getTerrainNormal(currentPoint.xz, heightCenter);
        hit.materialId = MAT_GROUND;
        //}
    }
}

// ========== ==========

// ========== Ray Marcher ==========

rayMarchHit GetRayMarchHit(vec3 currentPoint, float minSurfaceDistance, float time) {
    rayMarchHit hit;
    hit.distance = RAY_MAX_DISTANCE;
    #if defined(DEBUG_SAND_RENDER)
    getTerrainHit(currentPoint, hit, minSurfaceDistance);
    #endif
    return hit;
}
float map(float value, float min1, float max1, float min2, float max2) {

    return (value - min1) / (max1 - min1) * (max2 - min2) + min2;
}

rayMarchHit RayMarch(ray ray, float maxDistance, float minSurfaceDistance, int maxSteps, float time)
{
    float currentDistance = 0.0;
    rayMarchHit hit;
    float accumulatedDust = 0.0;
    float lastDistance = 0.0;
    float maxDustDistance = maxDistance/2.0;
    float dustMaxHeight = 4.0;

    for(int i=0; i<maxSteps; i++) {

        vec3 currentPoint = ray.origin + ray.direction*currentDistance;
        hit = GetRayMarchHit(currentPoint, minSurfaceDistance, time);

        #if defined (DEBUG_DUST_RENDER)
        float tHeight = getTerrainHeight(currentPoint.xz);
        float dHeight = tHeight+dustMaxHeight;

        if (currentDistance <= maxDustDistance && currentPoint.y < dustMaxHeight) {
            float remapedY =  map(currentPoint.y, tHeight, dHeight, 0.0, 2.0*PI);
            //we add the remapped y value ot the time for the lookup so the sand closer to the ground runs slower, not sure its that notisable.
            float dust = lastDistance * (getCloudNoise(currentPoint.xz*3.0, time*(20.0-(remapedY*0.002))));
            //we remap the ray height into the 0.0 to 2PI range, then pass it though sin to get a nice smooth falloff
            dust *= sin(remapedY)*0.5+0.5;			
            dust *= 1.0-currentDistance/maxDustDistance;
            accumulatedDust+= dust;
        }
        #endif

        //had to add this to remove some of the dust artifacts., we jsut check if the ray is going into the dust and limit the distance
        if (hit.distance > 1.0)
        {
            #if defined (DEBUG_DUST_RENDER)
            if (currentPoint.y + (ray.direction.y * hit.distance) <= dHeight)
            {
                #endif
                hit.distance = min(hit.distance,max((hit.distance*(currentDistance/maxDustDistance)),1.0));
                #if defined (DEBUG_DUST_RENDER)
            }
            #endif
        }
        hit.iteration = i;

        #if defined (DEBUG_RAY_DITHER)
	        if(hit.distance > 0.0) {
    	        hit.distance *= mix(0.9,1.0, hash31(fract(currentPoint+time)));
        	}
        #endif

        lastDistance = hit.distance;
        currentDistance += hit.distance;

        if(currentDistance >= maxDistance || (currentPoint.y > 5.0 && ray.direction.y > 0.0) || (currentPoint.y < 0.0 && ray.direction.y < 0.0)) {
            hit.surfaceNormal = ray.direction;
            hit.materialId=MAT_SKY;
            break;
        } 
        //terminate if we are very close or bounce back out if we end up within an object. 
        else if(hit.distance >= 0.0 && hit.distance <= minSurfaceDistance) {
            break;
        }
    }
    hit.distance = currentDistance;
	//This should match the calculation in the getLight function to ensure they line up.
    hit.dust = clamp(0.2 * accumulatedDust * ((cos(time*0.5) + cos(time*0.3))*0.25+0.5),0.0,1.0);
    return hit;
}

// ========== ==========

// ========== Lighting ==========

float getShadow(vec3 origin, vec3 destination, float soft, float time) {
    float currentDistance = OBJECT_MIN_SURFACE_DISTANCE_SHADOW;
    float maxDistance = distance(destination, origin);
    vec3 direction = normalize(destination-origin);
    rayMarchHit rayMarchHit;
    float light = 1.0;

    for(int i=0; i<RAY_MAX_STEPS_SHADOW; i++) {
        vec3 currentPoint = origin + direction*currentDistance;
        rayMarchHit = GetRayMarchHit(currentPoint, 99999.0, time);
        //Soft Shadows!
        light = min(light,soft * rayMarchHit.distance/currentDistance);
        currentDistance += rayMarchHit.distance;
        if(currentDistance > maxDistance) {
            break;
        } else if(rayMarchHit.distance < OBJECT_MIN_SURFACE_DISTANCE_SHADOW) {
            light = 0.0;
            break;
        }
    }

    return light;
}

float getOcclusion( in vec3 origin, in vec3 normal, float time)
{
    float occlusion = 0.0;
    float scale = 0.7;
    rayMarchHit hit;

    for( int i=0; i<6; i++ )
    {
        float distance = OBJECT_MIN_SURFACE_DISTANCE_SHADOW + 0.025*float(i);
        vec3  currentPoint = origin + distance*normal;
        hit = GetRayMarchHit(currentPoint, OBJECT_MIN_SURFACE_DISTANCE_SHADOW, time);
        occlusion += (distance-hit.distance)*scale;
        scale *= 0.95;
    }
    return clamp( 1.0 - 2.0*occlusion, 0.0, 1.0 );
}

void getLight(vec3 point, vec3 normal, vec3 rayDirection, out vec3 diffuseLight, out vec3 specularLight, float time) {

    //FIXME: could bake the light brightness into the colour
    //FIXME: get material shinynes for power

    diffuseLight = vec3(0.0);
    specularLight = vec3(0.0);

    vec3 direction;
    vec3 colour;
    float brightness;
    float shadow;
    vec3 specDirection;
    float dotProduct;
    float maxDustDistance = RAY_MAX_DISTANCE/6.0;

    #if defined (DEBUG_DUST_RENDER)
    float dust = (getCloudNoise(point.xz*3.0, time*20.0))*0.25;
    //This should match the calculation in the RayMarch function to ensure they line up.
    dust = 1.0-clamp(1.2*dust * ((cos(time*0.5) + cos(time*0.3))*0.25+0.5),0.0,1.0);
    #endif

    #if defined(LIGHT_SUN)
    colour=vec3(0.8,0.6,0.4);
    direction = sunDir;
    brightness = clamp(dot(direction,normal),0.0,1.0);
    shadow = getShadow(point +  normal*OBJECT_MIN_SURFACE_DISTANCE_SHADOW_OFFSET, point+direction*RAY_MAX_DISTANCE*0.5, 10.0, time);
    #if defined (DEBUG_CLOUD_RENDER)
    float cloudCover = (1.0-getCloudNoise(point.xz*0.2, time));
    brightness *= cloudCover;
    #endif
    #if defined (DEBUG_DUST_RENDER)
    brightness *= dust;
    #endif
    brightness *= shadow;

    #if defined(DEBUG_SPECULAR)

    float spec = hash21(floor(point.xz*100.0)/100.0);

    if(spec > 0.98) {
        spec = (spec-0.98) * 50.0;
        specDirection = reflect(direction, normal);
        dotProduct = dot(specDirection, rayDirection);
        specularLight += colour * 5.0 * spec * pow(max(dotProduct,0.0), 5.0) * shadow;
    }
        #endif
        diffuseLight += colour * 5.0 * brightness;
    #endif

    #if defined(LIGHT_SKY)
    colour=vec3(0.4, 0.85, 1.0);
    direction = normalize(vec3(0.0, 1.0, 0.0));
    brightness = clamp(dot(direction,normal),0.0,1.0);
    #if defined (DEBUG_DUST_RENDER)
    	brightness *= dust;
    #endif

        #if defined(DEBUG_SPECULAR)
        spec = hash21(floor(point.xz*150.0)/150.0);

        if(spec > 0.99) {
            spec = (spec-0.99) * 100.0;
            specDirection = normalize(reflect(direction, normal));
            dotProduct = dot(specDirection, rayDirection);
            specularLight += colour * 2.0 * pow(max(dotProduct,0.0), 5.0);
        }
        #endif

    diffuseLight += colour * 2.0 * brightness;
    #endif

    //#if defined(LIGHT_GROUND)
    //colour=SAND_COLOUR;
    //direction = normalize(vec3(0.0, -1.0, 0.0));
    //brightness = clamp(dot(direction,normal),0.0,1.0);
    //#if defined(DEBUG_SPECULAR)
    //specDirection = normalize(reflect(direction, normal));
    //dotProduct = dot(specDirection, rayDirection);
    //specularLight += pow(max(dotProduct,0.0), 2.0) * colour;
    //#endif
    //diffuseLight += colour * 1.0 * brightness;
    //#endif
}

// ========== ==========

// ========== Camera Motion ==========

vec3 getCameraPath(float time) {
    //Force a loop in time .. large values of time cuase judder
    //FIXME: need to figure out a way to make the loop smooth, remove the glitch jump.
    //time=mod(time,300.0);

    vec3 r;
    //r.y = 6.0;
    //r.x=sin(time/10.0)*40.0;
    //r.z=cos(time/10.0)*40.0;
    r.y = (sin(time/2.0)*0.5+0.5) + (sin(time/6.0)+1.0) + (sin(time/15.0)+1.0) + 3.0;
    r.x = ( sin(time/10.0) * 40.0 ) + sin(time/6.0) * 5.0 + sin(time/2.0) * 3.0;
   r.z = ( cos(time/10.0) * 40.0 ) + sin(time/5.0) * 9.0 + sin(time/3.0) * 2.0;
    return r;

}

camera getCamera(vec3 origin, float roll, float pitch, float yaw, float zoom) {
    camera camera;
    camera.origin = origin;
    mat3x3 rotationMatrix = eulerToMatrix( roll, pitch, yaw);

    camera.forward = vec3(0.0,0.0,1.0) * rotationMatrix;
    camera.right = vec3(1.0,0.0,0.0) * rotationMatrix;
    camera.up = vec3(0.0,1.0,0.0) * rotationMatrix;

    camera.rotationMatrix = rotationMatrix;
    camera.roll = roll;
    camera.pitch = pitch;
    camera.yaw = yaw;

    camera.zoom = zoom;
    return camera;
}

camera getCamera(vec3 origin, vec3 forward, float roll, float zoom) {
    float yaw = atan(forward.x,forward.z);
    float pitch = asin(forward.y);
    return getCamera(origin, roll, pitch, yaw, zoom);
}

camera getCamera(vec2 mouse, float time) {
    #if defined(DEBUG_CAMERA_FLY)
    vec3 cameraPos1 = getCameraPath(time);
    if (iMouse.z<0.5) {
		vec3 cameraPos2 = getCameraPath(time+0.5);
        vec3 cameraPos3 = getCameraPath(time-0.5);

        vec3 cameraForward = normalize(cameraPos2 - cameraPos1);

        vec3 cameraForward2 = normalize(cameraPos1 - cameraPos3);

        //Take atan and nivt to -1 to +1 range
        float yaw1 = atan(cameraForward.x,cameraForward.z)/PI;
        float yaw2 = atan(cameraForward2.x,cameraForward2.z)/PI;

        // get the differance and renormalize using fract.
        float roll = -(fract((yaw1-yaw2)*0.5+0.5)*2.0-1.0)*5.0;
        roll = clamp(roll,-0.5,0.5);
        return getCamera(cameraPos1, cameraForward, roll,  0.5);
    } else  {
        return getCamera(cameraPos1, 0.0, mouse.y*PI, mouse.x*2.0*PI,  0.5);
    }
    #else
    return getCamera(vec3(0.0,5.0,0.0), 0.0, mouse.y*PI, mouse.x*2.0*PI,  0.5);    
    #endif
}

// ========== ==========

// ========== Screen Space Effects ==========

vec3 getLensFlare(vec2 uv, vec2 sunUv)
{
    // Render the 'godray'?
    vec2 delta = uv-sunUv;
    float distance = clamp(dot(delta,delta),0.0,1.0);
    float angle = atan(delta.x,delta.y);
    float ray =sin(valueNoise1du(sin(angle*3.0+sunUv.x*2.0)*7.0-cos(angle*5.0+sunUv.y*2.0)))*0.2;
    ray*=1.0-distance;
    ray+=pow(1.0-distance,10.0);

    //Render the 'circles'

    //further away from the center the bigger they gets
    vec2 uvSpread =  uv * length(uv) ;
    //Offset the RGB to fake Chromatic Aberration
    vec3 circles = vec3(0.0);
    if (sunUv.x > -1.5 && sunUv.y > -1.0 && sunUv.x < 1.5 && sunUv.y < 1.0) {
        for (float i=0.0;i<5.0;i++) {
            vec2 circlePosition = sunUv * (0.5 - 0.4*i);
            float size = 40.0/(i+1.0);
            float blur = 2.0*(5.0-i);
            circles += vec3(	max(	0.0,	1.0-pow(length(			circlePosition-delta)*size,blur)),
                            max(	0.0,	1.0-pow(length(1.05*	circlePosition-delta)*size,blur)),
                            max(	0.0,	1.0-pow(length(1.1*		circlePosition-delta)*size,blur)));    
        }
    }
    // Odd edge glows ...
    circles += vec3(
        max(0.0, 5.0/(1.0+25.0*pow(length(uvSpread-0.7*-sunUv),1.5))),
        max(0.0, 5.0/(1.0+30.0*pow(length(uvSpread-0.8*-sunUv),1.4))),
        max(0.0, 5.0/(1.0+35.0*pow(length(uvSpread-0.9*-sunUv),1.3)))
    );

    circles += vec3(
        max(0.0, 1.0-(1.0*pow(length(uvSpread-0.7*-sunUv)*5.5,2.5))),
        max(0.0, 1.0-(2.0*pow(length(uvSpread-0.8*-sunUv)*5.5,2.4))),
        max(0.0, 1.0-(2.0*pow(length(uvSpread-0.9*-sunUv)*5.5,2.3)))
    );

    return circles*0.1*length(uv-sunUv) + ray;
}

// ========== ==========

// ========== Entry Point ==========

void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    float time = iTime;
    //vec3 debugOverlay = vec3(0.0);

    // Normalized Pixel coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    // Normalized Mouse coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 mouse = ((iMouse.xy-0.5*iResolution.xy)/iResolution.y);// - vec2(0.5,0.5)*(iMouse.w*0.1);

    //Snap to center on startup.
    if((iMouse.x <= 1.0 && iMouse.y <= 1.0)) {
        mouse = vec2(0.0,0.0);
    }

    camera camera = getCamera(mouse, time);

    // --
    ray ray = getRay(uv, camera);
    rayMarchHit hit = RayMarch(ray, RAY_MAX_DISTANCE, OBJECT_MIN_SURFACE_DISTANCE, RAY_MAX_STEPS, time);

    //float yaw3 = atan(ray.direction.x,ray.direction.z)/PI;
    //debugOverlay = vec3(abs(fract( (yaw3-yaw1)*0.5+0.5)*2.0-1.0));

    vec3 colour;

    // ---- Render Sky Box

    //Get the distance from the sun so we can tint the sky, This is also used for the fog colour later.
    float distanceFromSun = 1.0-(dot(sunDir,ray.direction)*0.5+0.5);

    vec3 skyColour;    

    if(ray.direction.y < 0.0) {
        // Floor (not the sand!)
        skyColour = mix(SAND_COLOUR*0.75,SAND_COLOUR*0.5,distanceFromSun);
    } else {
        // Sky
        skyColour = mix(vec3(0.4, 0.85, 1.0),vec3(0.1, 0.2, 0.25),distanceFromSun) - max(ray.direction.y,0.0)*0.3;
    }

    skyColour *= 5.0; //Make it very bright!

    // ---- Render Mountains
    #if defined(DEBUG_MOUNTAIN_RENDER)
    if (ray.direction.y > 0.0 && ray.direction.y < 0.1875) {
        float rayYaw = atan(ray.direction.x,ray.direction.z)/PI*0.5+0.5;
        float mountainDepth = 0.0;
        float maxMountainHeight = 1.0;
        vec3 mountainColour;
        for (float i=0.0;i<6.0;i++) {
            float 	mountainHeight =	valueNoise1duw( i*1234.543 + rayYaw*(30.0),		30.0) * 2.0;
            mountainHeight+= 			valueNoise1duw( i*7832.856 + rayYaw*(60.0),		60.0) * 1.0;
            mountainHeight+= 			valueNoise1duw( i*5892.473 + rayYaw*(120.0),	120.0) * 0.5;
            mountainHeight+= 			valueNoise1duw( i*2857.673 + rayYaw*(180.0),	180.0) * 0.25;
            mountainHeight*= 0.07-i*0.01;

            if(ray.direction.y < mountainHeight) {
                mountainDepth=(i+1.0)/7.0;
                //FIXME: should move this out somwhere or remap the mountain height to 0.0 to 1.0
                //add a fade from the top to the bottom of this mountain
                float maxHeight = (2.0+1.0+0.5+0.25)*(0.07-i*0.01);
                float ratio = mountainHeight/maxHeight;
                mountainDepth*= 1.0-pow(1.0-ratio,3.6);
                mountainColour = mix(vec3(1.0),SAND_COLOUR*2.0,(i+1.0)/5.0);
            }
        }

        float pointHeight = map(ray.direction.y,0.0,0.1875,0.0,1.0);
        skyColour = mix(skyColour, mountainColour, mountainDepth);
    }
    #endif
    //Haze
    skyColour = mix(skyColour,mix(SAND_COLOUR*1.5,SAND_COLOUR,distanceFromSun)*3.0,pow(1.0-abs(ray.direction.y),20.0));

    // ---- Render Clouds and Sun
    if(hit.materialId == MAT_SKY) {
        vec3 samplePoint = ray.direction;
        float clouds = 0.0;
        #if defined(DEBUG_CLOUD_RENDER)
        clouds = getClouds(ray.direction, time);
        #endif
        vec3 suncolour = vec3(0.8,0.6,0.6);
        float sunBright = 1.0-distanceFromSun;

        sunBright = pow(smoothstep(0.99,1.0,sunBright),20.0) * 10.0; //blow it right out

        skyColour += suncolour * sunBright;

        skyColour = mix(skyColour, vec3(3.0), clouds );        

        colour = skyColour;

        // ---- Render Ground/Sand
    } else if(hit.materialId == MAT_GROUND) {

        //base sand colour
        vec3 sandColour = SAND_COLOUR;//vec3(0.98,0.82,0.68);

        #if defined(DEBUG_SAND_RIPPLES)
        //Add riples by distorting the normal
        float offset = ((sin(35.0*hit.origin.x)*sin(2.0*hit.origin.y+5.0*hit.origin.x)*2.0-1.0)*0.4)*(smoothstep(0.3,2.0,1.7-hit.origin.y));
        hit.surfaceNormal.xy = normalize(hit.surfaceNormal.xy - offset);
        #endif

        #if defined(DEBUG_SAND_SHADING)
        //give the sand some interesting variations
        sandColour = mix(sandColour,vec3(1.0),smoothstep(0.0,1.0,dot(vec3(-1.0,0.0,0.0),hit.surfaceNormal)));
        sandColour = mix(sandColour, vec3(0.8,0.6,0.6),smoothstep(0.0,2.0,(sin((hit.origin.x+hit.origin.z)*0.2)*cos((hit.origin.z+hit.origin.y)*0.2)*cos(hit.origin.y*0.1)*0.5+0.5)));
        sandColour = mix(sandColour,vec3(0.6,0.4,0.3),smoothstep(0.0,1.0,1.5-hit.origin.y));
        #endif
        colour = sandColour;

        // ---- Render Other Objects
    } else if(hit.materialId == MAT_SPHERE_1) {
        colour = vec3(1.0,0.1,0.1);
    } else if(hit.materialId == MAT_SPHERE_2) {
        colour = vec3(hit.surfaceNormal)*0.5+0.5;
    } else {
        colour = vec3(1.0,0.0,1.0);//vec3(1.0-hit.distance/RAY_MAX_DISTANCE);    
    }

    // ---- Render Lighting, Occlusion and Fog for everything but the skybox
    if(hit.materialId != MAT_SKY) {
        // Apply diffuse and specular Light
        vec3 diffuseLight, specularLight;
        getLight(hit.origin, hit.surfaceNormal, ray.direction, diffuseLight, specularLight, time);

        colour = colour * diffuseLight + specularLight;

        #if defined(DEBUG_OCCLUSION)
        //Simple fake Occlusion
        float occlusion = getOcclusion(hit.origin, hit.surfaceNormal, time);
        colour *= occlusion;
        #endif

        #if defined(DEBUG_DUST_RENDER)
        colour = mix(colour,SAND_COLOUR*2.0,hit.dust);
        #endif

        #if defined(DEBUG_FOG_RENDER)
        //Fog - fade to sky colour in the distance
        colour = mix(colour,skyColour,pow((hit.distance/RAY_MAX_DISTANCE),3.0));
        #endif
    }

    // ---- Lense Flare
    #if defined(DEBUG_SUN_RAYS_RENDER)
    float sunDot = dot(camera.forward, sunDir);
    if (sunDot > 0.0) {
        //Project the sun to the screen to get its UV position
        mat3 invMat = rotateY(-camera.yaw) * rotateX(-camera.pitch) * rotateZ(-camera.roll);
        vec3 sunProjection = (sunDir*invMat);
        vec2 sunUv = sunProjection.xy/sunProjection.z*camera.zoom;
        colour += getLensFlare(uv, sunUv)*5.0;//*pow(clamp(dot(camera.forward, sunDir),0.0,1.0),1.5);
    }
    #endif

    // ---- Colour correction and Vignette
    float exposure = 0.35;

    #if defined(DEBUG_DYNAMIC_EXPOSURE)
    exposure *= smoothstep(-0.7,1.0,( 1.0-pow(clamp(dot(camera.forward, sunDir),0.0,1.0),4.0)));
    #endif

    // Gamma
    colour = pow( exposure * colour, vec3(1.0/2.2) );

    //Clamp
    colour = clamp(colour,0.0,1.0); 

    #if defined(DEBUG_GRAIN_VIGNETTE) 
    // Vignette
    uv = fragCoord.xy / iResolution.xy;
    uv *=  1.0 - uv; 
    float vignette = clamp(pow( uv.x*uv.y*5.0,0.5)+0.4,0.0,1.0);
    // Grain
    //vignette *= (1.0-hash31( (vec3(ray.direction)+(5.0+mod(vec3(time),vec3(10.0,137.0,75.3))  )) * 50.00 ) * 0.1);
    //Burn it in
    colour=vec3(1.0)-((vec3(1.0)-colour)/vignette);
    #endif

    //Clamp
    colour = clamp(colour,0.0,1.0); 
    // colour grading
    //colour = colour*vec3(1.0,0.8,1.0);
    // compress        
    colour = 1.7*colour/(1.0+colour);
    colour = colour*colour*(3.0-2.0*colour);

    fragColour = vec4(colour,1.0);
    //ragColour = vec4(mix(colour,debugOverlay,0.5),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv - vec2 (0.5,0.5);
    vec2 a = uv + mod(0.5,0.5);     
    vec3 col = vec3 ( 0.5, .7, 1. );
    float r = 0.02 + 0.9 * sin(atan(q.y,q.x)* iTime * 5.0 ); 
    float d = 0.2 * 6.0 * fract(atan(a.y,a.x) * iTime * a.x * 2.0 );
    col *= mix (r, r*0.0001, length( q / d*3.23) ); 
    fragColor = vec4(col,1.0);
}

#define S(a,b,t) smoothstep(a,b,t)
#define sat(x) clamp(x,0.,1.) 
//make sure that x is between 0 and 1

float remap01(float a, float b, float t){
	return sat((t-a) / (b-a));
}

float remap(float a, float b, float c, float d, float t){
	return sat(((t-a) / (b-a)) * (d-c) +c);
}

vec2 within(vec2 uv, vec4 rect){//Same as remap 01
	return (uv - rect.xy) / (rect.zw-rect.xy);
}


vec4 Brow(vec2 uv){
	float y = uv.y;
    uv.y +=uv.x *.8-.3; // play with the place & form
    uv.x -= .1;

    uv -=.5;

    vec4 col = vec4(0.);

    float blur = .1;

    float d1 = length(uv); 
    float s1 = S(.45, .45-blur, d1);//first circle
    float d2 = length(uv-vec2(.1, -.2)*.7); 
    float s2 = S(.5, .5-blur, d2); //2nd circle

	float browMask = sat(s1-s2); //substract first circle to second & don't go negative

     float colMask = remap01(.7, .8, y)*.75;
     colMask *= S(.6, .9, browMask);

    vec4 browCol = mix(vec4(.4,.2 ,.2, 1.),vec4(1., .75, .5, 1.), colMask);

    uv.y += .15;
    blur +=.1;

    d1 = length(uv);
    s1 = S(.45, .45-blur, d1);
    d2 = length(uv-vec2(.1, -.2)*.7);
    s2 = S(.5, .5-blur, d2);
    float shadowMask = sat(s1-s2);


    col = mix(col, vec4(0.,0.,0.,1.), S(.0, 1., shadowMask)*.5);

    col = mix(col, browCol, S(.2, .4, browMask));

    return col;
}

vec4 Eye(vec2 uv, float side, vec2 m, float smile) {
    uv -=.5;
    uv.x *= side;

    float d= length(uv);
    vec4 irisCol = vec4(.3,.5,1.,1.);
    vec4 col = mix(vec4(1.), irisCol, S(.1,.7,d)* .5);
    col.a = S(.5,.48,d);

    col.rgb *= 1. - S(.45, .5 , d) *.5 * sat(-uv.y - uv.x*side);
    d = length(uv-m*.5);
    irisCol.rgb *= 1. + S(.3,.05,d);

    col.rgb = mix(col.rgb, vec3(0.), S(.3,.28,d)); // iris outline

    d = length(uv-m*.6); 
    col.rgb = mix(col.rgb, irisCol.rgb, S(.28,.25,d));

    float pupilSize = mix(.2,.16, smile);
    col.rgb = mix(col.rgb, vec3(0.), S(pupilSize,pupilSize*.85,d)); //pupile

    float highlight = S(.1, .09, length(uv-vec2(-.15,.15)));
    highlight += S(.07, .05, length(uv+vec2(-.08,.08)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);


    return col;
}

vec4 Mouth(vec2 uv) {
    uv -= .5;
	vec4 col = vec4(.5, .18, .05, 1.);

    uv.y *= 1.5;
    uv.y -= uv.x * uv.x*2.;
    float d = length(uv);
    col.a = S(.5, .48, d);

    float td = length(uv-vec2(0., .6));
    vec3 toothCol = vec3(1.) * S(.6,.35 ,d);
    col.rgb = mix(col.rgb, toothCol, S(.4,.37, td));

    td = length(uv + vec2(0., .5)); //thong distance
    col.rgb = mix(col.rgb, vec3(1., .5 ,.5) , S(.5 , .2 ,td));
    return col;
}

vec4 Head(vec2 uv) {
	vec4 col = vec4(0.9,.65,0.1,1.);
    float d = length(uv);
    col.a = S(0.5,.49,d);
    float edgeShade = remap01(.35,.5,d);
    edgeShade *= edgeShade;
    col.rgb *= 1. - edgeShade *.5;
    col.rgb = mix(col.rgb, vec3(.6,.3,.1), S(.47,.48,d));

    float highlight = S(.41, .405,d);
    highlight *= remap(.41 , -.1, 0.75, 0.,uv.y);
    highlight *= S(.18, .19, length(uv - vec2(.21,.08)));
    col.rgb = mix(col.rgb , vec3(1.), highlight);

     d = length(uv - vec2(.25,-.2));
    float cheek = S(.2,.01,d)*.4; //edge
    cheek *= S(.18 ,  .16, d) ;

    col.rgb = mix(col.rgb, vec3(1., .1 , .1), cheek);

    return col;
}


vec4 Smiley(vec2 uv, vec2 m, float smile) {
	vec4 col = vec4(0.);

    float side = sign(uv.x); //+1 for right side -1 for the left side

    uv.x = abs(uv.x); //symetrie
    vec4 head = Head(uv);
    vec4 eye = Eye(within(uv, vec4(.03,-.1,.37, .25)),side, m, smile);
    vec4 mouth = Mouth(within(uv, vec4(-.3,-.43,.3, -.13)));
    vec4 brow = Brow(within(uv, vec4(.03,.2, .4,.45)));

    col = mix(col,head, head.a); //mix the color between col and head. if head.a = 0 return col/ if =1 return head
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);
    col = mix(col, brow, brow.a);

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
