  	m -= .5;

    float smile = cos(iTime)*.5+.5;
    // Output to screen
    fragColor = Smiley(uv, m, smile);
}

#define PI 3.14159265358979
#define BLACK vec4(0,0,0,1)
#define WHITE vec4(1,1,1,1)
#define RADIUS .8
#define BASE_THICKNESS .05
#define VAR_THICKNESS .08
#define INNER_THICKNESS_SCALE .2

float bell(float x)
{
    return exp(-((x-PI)*(x-PI)) * .5);
}

float go(vec2 pos, float time)
{
    time *= time;

    float lp = length(pos);
    float theta = mod(atan(pos.y, pos.x) + time, 2.*PI) ;
    float thickness = BASE_THICKNESS + VAR_THICKNESS*bell(theta);

    if (lp < RADIUS + thickness && lp > RADIUS - INNER_THICKNESS_SCALE*thickness) {
        return 1.;
    }
    return 0.;
}

vec3 samp(vec2 pos, vec2 o)
{
    pos += o / iResolution.xy;
    float a = go(pos, iTime-.02);
    float b = go(pos, iTime-.01);
    float c = go(pos, iTime);
    return vec3(a, b, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = 2. * fragCoord.xy / iResolution.xy - 1.;
    pos.x *= iResolution.x / iResolution.y;
    float time = iTime * iTime;

    vec3 sampleA = samp(pos, vec2(-.75, .25));
    vec3 sampleB = samp(pos, vec2(-.25,-.75));
    vec3 sampleC = samp(pos, vec2( .25, .75));
    vec3 sampleD = samp(pos, vec2( .75,-.25));

    fragColor = vec4((sampleA + sampleB + sampleC + sampleD) / 4., 1);
}

float Angle(vec3 v,vec3 u)
{
    return dot(v,u)/(length(v)*length(u));
}

float Delta(vec3 pos,vec3 spos)
{
    float a = Angle(pos,vec3(sin(iTime*0.15),cos(iTime*0.1),sin(iTime*0.1)));

	return  abs(length(pos-spos)
               - 1.0
               + 0.05*sin(8.0*pos.y + iTime)
               - 0.05*sin(16.0*pos.x*pos.y + iTime)
        	   + 0.05*sin(20.0*a));
}

vec3 Normal(vec3 pos,vec3 spos) {
    return normalize(pos-spos);
}

vec3 Col(vec3 pos,vec3 spos)
{
    vec3 norm = Normal(pos,spos);
    return norm*Angle(norm,vec3(1.0,0.5,0.5));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0*fragCoord/iResolution.xy - 1.0;

	vec3 pos = vec3(uv,0.0);
    vec3 dir = pos - vec3(0.0,0.0,-1.5);

    vec3 spos = vec3(sin(iTime)*0.1,cos(iTime)*0.1,1.0);

	vec3 color  = vec3(0.0);

    int steps = 150;
    for(int i = 0; i < steps; i++)
    {
        if(Delta(pos,spos) < 0.015)
        {
            color += Col(pos,spos)*(float(i)/float(steps));
        }
        pos += 0.01*dir;
    }

	color *= 0.35;

    fragColor = vec4(color,1.0);
}

const float levels = 6.; // number of quantisation levels, must be at least 2
const float animFPS = 120.; // max framerate for temporal dither, set 0 to disable
bool separateRGB = false; // use different random values for different channels?

uint hash(uint x)
{
    x ^= x >> 16; x *= 0x7feb352du; 
    x ^= x >> 15; x *= 0x846ca68bu; 
    x ^= x >> 16;
    return x;
}

// fetch random values for RGB channels
vec3 hash2v(uint x, uint y)
{
    // take a hash, split it into components
    uint h = hash(uint(x)+(uint(y)<<16));
    vec3 v;
    v.x = float(h&0xffu); if(separateRGB) h >>= 8;
    v.y = float(h&0xffu); if(separateRGB) h >>= 8;
    v.z = float(h&0xffu);

    return v / float(0xffu);
}

vec3 triCDF(vec3 x)
{
    vec3 y = min(x, 1.-x);
    y = .5 * sqrt(2.*y);
    return mix(y, 1.-y, round(x));
}

vec3 dither(vec3 c, uvec2 xy)
{

    // temporal dither
    xy.x += uint(floor(animFPS*iTime)*12345.);

    // compute noise
    vec3 h = hash2v(xy.x, xy.y);
    h -= .25 * hash2v(xy.x-1u, xy.y);
    h -= .25 * hash2v(xy.x+1u, xy.y);
    h -= .25 * hash2v(xy.x, xy.y-1u);
    h -= .25 * hash2v(xy.x, xy.y+1u);

    // scale, about 2 is enough to deal 
    h *= 2.;

    float g = levels-1.;

    // expand near black to counter compression
    vec3 f0 = max(.5-c*g, 0.); c -= .5*f0*f0;
    // expand near white to counter compression
    vec3 f1 = max(.5-(1.-c)*g, 0.); c += .5*f1*f1;    

    return round(c*g + h) / g;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 c = texture(iChannel0,uv.xy).xyz;

    if(abs(iMouse.x - fragCoord.x) < iResolution.x*.25) 
    {
        c = dither(c, uvec2(fragCoord.xy));
    }
	else if(iMouse.y < .5*iResolution.y) c = round(c*(levels-1.)) / (levels-1.);


    // Output to screen
    fragColor = vec4(c, 1.0);
}

#define noiselevel 0.4
#define rgbshiftlevel 0.01
#define ghostreflectionlevel 0.03
#define bypass false

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float randomValue = rand(vec2(uv.x+sin(iTime), uv.y+cos(iTime)));
    float rgbShift = sin(iTime+randomValue)*rgbshiftlevel;

    if(randomValue > 0.95-ghostreflectionlevel)
    	uv.x+=sin(iTime/5.0)*0.5;

    uv.y += (cos(iTime*randomValue)+0.5) * (randomValue*0.01);

    float colorr = texture(iChannel0, vec2(uv.x+rgbShift, uv.y)).r;
    float colorg = texture(iChannel0, vec2(uv.x, uv.y)).g;
    float colorb = texture(iChannel0, vec2(uv.x-rgbShift, uv.y)).b;

  	vec4 movieColor = vec4(colorr,colorg,colorb, 1.0);
    vec4 noiseColor = vec4(randomValue,randomValue,randomValue,1.0);

    if(randomValue > 0.55-ghostreflectionlevel)
    	noiseColor = abs(noiseColor - 0.2);

    if(bypass)
        fragColor = texture(iChannel0, fragCoord.xy / iResolution.xy); 
    else
      	fragColor = mix(movieColor, noiseColor, noiselevel);  


}

vec2 point(float i) {//random point maker
    vec3 a = fract(i*vec3(123.45, 456.78, 321.98));
    a += dot(a, a+35.79);
    return fract(a.xy + a.zz);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragCoord -= iResolution.xy/2.;//centered, normalized, uv
    vec2 uv = fragCoord/iResolution.y;
    uv += .5;

    vec2 p = vec2(-10.);
    for(float i = 0.; i < 50.; i++) {//voronoi
        vec2 v = .5 + .5*sin(6.2831*point(i) + .1*iTime);
        if (length(uv-p) > length(uv-v)) {
        	p = v;
        }
    }

    vec3 color = vec3(p.x+p.y);//based on og rainbow effect
    color += vec3(0, 2, 4) + iTime;
    color = .5 + .5*sin(color);

    fragColor = vec4(color, 1.);
    fragColor *= 1. - 2.*length(uv-p);//times voronoi

    fragColor.w = 1.;
}

