// Using code from Inigo Quilez

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float random (in vec2 st) { return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123); }

vec3 look (vec3 eye, vec3 target, vec2 anchor) {
    vec3 forward = normalize(target-eye);
    vec3 right = normalize(cross(forward, vec3(0,1,0)));
    vec3 up = normalize(cross(right, forward));
    return normalize(forward * .75 + right * anchor.x + up * anchor.y);
}

float map (vec3 pos) {
    vec3 p = pos;
    float scene = 10.0;
    float r = 1.0;
    const float count = 9.0;
    for (float index = count; index > 0.0; --index) {
        pos.xz = abs(pos.xz)-1.4*r;
        pos.yx *= rot(3.9/r + iTime * 0.3);
        pos.yz *= rot(0.5/r + iTime * 0.2);
        pos.xz *= rot(0.2/r + iTime * 0.1);
        scene = min(scene, abs(max(pos.x, max(pos.y, pos.z))));
        r /= 1.8;
    }
    scene = max(abs(p.z)-1., scene);
    scene = max(-length(p.xy)-p.z*.5, scene);
    return scene;
}

vec4 raymarch (vec3 eye, vec3 ray) {
    float dither = random(ray.xy+fract(iTime));
    vec4 result = vec4(eye, 0);
    float total = 0.1 * dither;
    float maxt = 20.0;
    const float count = 30.;
    for (float index = count; index > 0.0; --index) {
        result.xyz = eye + ray * total;
        float dist = map(result.xyz);
        if (dist < 0.001 + total * .001 || total > maxt) {
            result.w = index / count;
            break;
        }
        dist *= 0.7 + 0.1 * dither;
        total += dist;
    }
    result.w *= step(total, maxt);
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    vec3 eye = vec3(.2,.1,-2.);
    vec3 at = vec3(0);
    vec3 ray = look(eye, at, uv);
    vec3 eyeoffset = 0.02*normalize(cross(normalize(at-eye), vec3(0,1,0)));
    vec4 resultLeft = raymarch(eye-eyeoffset, ray);
    vec4 resultRight = raymarch(eye+eyeoffset, ray);
    fragColor = vec4(resultLeft.w,vec2(resultRight.w),1);
}

// SHADERTOBER 22 Ghost
// Poulet vert 22-10-2019
// thanks iq, leon

#define VOLUME 0.001
#define PI 3.14159

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); 
}

float sdSphere(vec3 p, float r)
{
    return length(p)-r;
}

vec3 opRep(vec3 p, vec3 c)
{
     return mod(p+0.5*c,c)-0.5*c;
}

vec2 opU2( vec2 d1, vec2 d2 )
{
    return (d1.x < d2.x) ? d1 : d2;
}


float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
    p.y -= clamp( p.y, 0.0, h );
    return length( p ) - r;
}

// Scene setup
vec2 map(vec3 pos)
{
    vec2 scene = vec2(0.0, 0.0);

    pos.y -= iTime*2.0;
    pos = opRep(pos, vec3(0.0, 8., 0.0));


    pos.y += sin(iTime)*.5;
    pos.x += sin(pos.y+iTime)*.5;
    pos.xy *= rot(sin(iTime)*.1);

    vec3 bodyP = pos + vec3(0.0);
    float body = sdVerticalCapsule(bodyP, 2.0, 1.0);
    float low = sdSphere(pos+vec3(0.0, 1.0, 0.0), .8);

    body = opSmoothUnion(body, low, 2.0);

    vec3 subP = pos + vec3(0.0, 1.8, 0.0);
    subP.y += sin(pos.x*10.+iTime)*.1;

    float sub1 = sdBox(subP, vec3(2.0, 1.0, 2.0));

    body = max(body, -sub1);

    vec3 eyeP = pos + vec3(0.15, -2.0, 1.0);
    float eyes = sdSphere(eyeP, .1);
    eyeP = pos + vec3(-0.15, -2.0, 1.0);
    eyes = min(eyes, sdSphere(eyeP, .1));

    // materials
    scene = vec2(body, 0.0);
    scene = opU2(scene, vec2(eyes, 1.0));

    return scene;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<128 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(t, ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

float GetShadow (vec3 pos, vec3 at, float k) {
    vec3 dir = normalize(at - pos);
    float maxt = length(at - pos);
    float f = 01.;
    float t = VOLUME*50.;
    for (float i = 0.; i <= 10.0; i += .1) {
        float dist = map(pos + dir * t).x;
        if (dist < VOLUME) return 0.;
        f = min(f, k * dist / t);
        t += dist;
        if (t >= maxt) break;
    }
    return f;
}

vec3 GetNormal (vec3 p)
{ 
    vec2 e = vec2(0.01, 0.0); 
    return normalize(vec3(
        map(p+e.xyy).x - map(p-e.xyy).x,
        map(p+e.yxy).x - map(p-e.yxy).x,
        map(p+e.yyx).x - map(p-e.yyx).x
        ));
}

float GetLight(vec3 N, vec3 lightPos)
{
    return max(dot(N, normalize(lightPos)), 0.0);
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = vec3(ro + rd * t.x);

    vec3 col = vec3(0.0);
    vec3 polyCol = palette(t.x+fract(iTime), vec3(.5), vec3(.5), vec3(1.0), vec3(0.0, 0.33, 0.67));

    if(t.x == -1.0)
    {

        col = vec3(0.0);

    }
    else
    {    
        vec3 N = GetNormal(pos);
        vec3 rimL = vec3(0.5, 0.5, .5);
        float rimlight = GetLight(N, rimL);
        vec3 mainL = vec3(1.0, 1.0, -2.0);
        float mainlight = GetLight(N, mainL);


        if(t.y == 0.0)
        {
            col = vec3(.0);
            col += vec3(1.0) * mainlight;
            col += vec3(1.0, 0.5, 0.0) * vec3(rimlight) * 2.0;
        }
        else if(t.y == 1.0)
        {
            col = polyCol;
        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(sin(iTime)*.5, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    vec2 screenUV = fragCoord.xy / iResolution.xy;

    vec3 cp = vec3(0.0, 4.0, -8.0);
    vec3 ct = vec3(0.0, 4.0, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = Render(cp, vd, uv);

    col.b -= (length(uv)-1.0)*.2;

    col *= clamp(3.0-length(uv), 0.0, 1.0);

    col = sqrt(clamp(col, 0.0, 1.0));

    fragColor = vec4(col,1.0);
}

