vec4 gPhs;
vec3 ltDir;
vec2 gId;
float tCur, dstFar, hgSize, obHt, bbHt;
int idObj;
const float pi = 3.14159, sqrt3 = 1.7320508;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

void SetGrObjConf ()
{
  float g, r;
  r = Hashfv2 (37.3 * gId + 11.1);
  obHt = 0.5 * r;
  bbHt = 0.1 + mod (3. * tCur + 0.5 * r * dstFar, 0.5 * dstFar);
  g = dot (gId, vec2 (1.));
  gPhs = vec4 (12. + floor (8. * r),
     -0.1 * sin (2. * pi * (0.1 * tCur + 0.2 * g)),
     sin (2. * pi * (-0.5 * tCur + 0.3 * g) + vec2 (0., 0.5 * pi)));
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, s, a, r, rs, l, w;
  dMin = dstFar;
  r = 2.;
  l = 0.5;
  w = 0.05;
  p.xz -= HexToPix (gId * hgSize);
  p.y -= obHt;
  q = p;
  a = (length (q.xz) > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
  s = smoothstep (0.05, 0.8, q.y / r);
  q.xy -= vec2 (gPhs.y * s, r);
  rs = r * (1. + (0.02 * cos (gPhs.x * 2. * pi * a) + 
     0.1 * cos (4. * pi * (a + 0.1 * tCur)) * gPhs.w) * s) * (1. +
     0.002 * dot (gPhs.zw, sin (3. * pi * q.y + vec2 (0.5 * pi, 0.))));
  d = max (PrSphAnDf (q, rs, 0.1 * (0.2 - 0.8 * q.y / r)), q.y - 0.1 * r);
  DMIN (1);
  q.xz = Rot2D (q.xz, 2. * pi * (floor (gPhs.x * a + 0.5) / gPhs.x));
  d = max (PrTorusDf (q, w, rs), q.y);
  DMIN (1);
  q.xy -= vec2 (- rs, l);
  d = PrCylDf (q.xzy, w, l);
  DMIN (1);
  q.y -= l;
  d = PrSphDf (q, 3. * w);
  DMIN (2);
  q = p;
  q.y -= bbHt;
  d = PrSphDf (q, 0.05);
  DMIN (3);
  return 0.7 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-99.);
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetGrObjConf ();
    }
    d = ObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float WatShd (vec3 rd)
{
  vec2 p;
  float t, h;
  if (rd.y == 0.) rd.y = 0.0001;
  p = 20. * rd.xz / rd.y;
  t = 2. * tCur;
  h = sin (2. * p.x + 0.77 * t + sin (0.73 * p.y - t)) + sin (0.81 * p.y - 0.89 * t +
     sin (0.33 * p.x + 0.34 * t)) + 0.5 * (sin (1.43 * p.x - t) + sin (0.63 * p.y + t));
  h *= 0.04 * smoothstep (0.5, 1., rd.y);
  return h;
}

float TurbLt (vec3 p, vec3 vn, float t)
{
  vec4 b;
  vec2 q, qq;
  float c, tt;
  q = 2. * pi * mod (vec2 (dot (p.yzx, vn), dot (p.zxy, vn)), 1.) - 256.;
  t += 11.;
  c = 0.;
  qq = q;
  for (float j = 1.; j <= 7.; j ++) {
    tt = t * (1. + 1. / j);
    b = sin (tt + vec4 (- qq + vec2 (0.5 * pi, 0.), qq + vec2 (0., 0.5 * pi)));
    qq = q + tt + b.xy + b.zw;
    c += 1. / length (q / sin (qq));
  }
  return clamp (pow (abs (1.25 - abs (0.167 + 40. * c)), 8.), 0., 1.);
}

vec3 BgCol (vec3 rd)
{
  float t, gd, b;
  t = 2. * tCur;
  b = dot (vec2 (atan (rd.x, rd.z), 0.5 * pi - acos (rd.y)), vec2 (2., sin (rd.x)));
  gd = clamp (sin (5. * b + t), 0., 1.) * clamp (sin (3.5 * b - t), 0., 1.) +
     clamp (sin (21. * b - t), 0., 1.) * clamp (sin (17. * b + t), 0., 1.);
  return mix (vec3 (0.1, 0.7, 0.6), vec3 (0.3, 0.5, 1.), 0.5 + 0.5 * rd.y) *
     (0.24 + 0.44 * (rd.y + 1.) * (rd.y + 1.)) * (1. + 0.1 * gd);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, bgCol, vn;
  float dstObj;
  rd = VaryNf (32. * rd, rd, 0.02 * (1. - rd.y * rd.y));
  bgCol = BgCol (rd);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj < 3) {
      vn = VaryNf (ro, vn, 0.2);
      col = HsvToRgb (vec3 (0.05 + 0.4 * Hashfv2 (gId), 0.9, 1.));
      if (idObj == 2) col *= 1.5;
    } else {
      col = mix (vec3 (1.2), BgCol (reflect (rd, vn)), 0.5);
    }
    col = col * (0.1 + 0.2 * bgCol + 0.7 * max (dot (vn, ltDir), 0.));
    col *= 1. + 0.2 * TurbLt (0.05 * ro, abs (vn), 0.25 * tCur) * smoothstep (-0.3, -0.1, vn.y);
    col = mix (col, bgCol, smoothstep (0.3, 1., dstObj / dstFar));
  } else col = (bgCol + WatShd (rd)) * (0.7 + 0.3 * smoothstep (-0.9, -0.5, rd.y));
  col = pow (clamp (col, 0., 1.), vec3 (1.1, 1.1, 0.9));
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  hgSize = 2.8;
  az = 0.;
  el = -0.12 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.3 * pi * mPtr.y;
  } else {
    az += 0.4 * pi * (2. * mod (floor (0.015 * tCur), 2.) - 1.) *
       SmoothBump (0.3, 0.7, 0.15, mod (0.015 * tCur, 1.));
    el -= 0.05 * pi * sin (0.017 * pi * tCur);
  }
  el = clamp (el, -0.45 * pi, 0.2 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vec3 (0., 20., 5. + 0.5 * tCur);
  zmFac = 3.;
  dstFar = 90.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrSphAnDf (vec3 p, float r, float w)
{
  return abs (length (p) - r) - w;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

float func(float x) {
    return smoothstep(iTime/5., 1.0, x);
}

float funcGrad(float x) {
    float o = 0.01;
    return (func(x-o) - func(x+o)) / (2.0 * o);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    uv -= (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;

    uv *= 2.0;
    uv -= 0.1;

    vec3 col = vec3(1);

    float gridW = 0.1;
    vec2 gridUv = fract(uv*10.);
    float grid = smoothstep(1.0-gridW, 1.0, gridUv.x);
    grid += smoothstep(gridW, 0.0, gridUv.x);
    grid += smoothstep(1.0-gridW, 1.0, gridUv.y);
    grid += smoothstep(gridW, 0.0, gridUv.y);

    col -= 0.1*grid;

    if (abs(uv.x) < 0.01 || abs(uv.y) < 0.01) col = vec3(1.0, 0.0, 0.0);

    float y = func(uv.x);
    // old method, uneven thickness
    //col -= 1.0 - smoothstep(0.01, 0.02, abs(uv.y - y));
    // new method based off http://www.iquilezles.org/www/articles/distance/distance.htm
    col -= smoothstep(0.02, 0.019, abs(uv.y - y) / sqrt(1.0 + pow(funcGrad(uv.x), 2.0)));

    fragColor = vec4(col,1.0);
}

precision highp float;

float getDistanceFromPoint(vec3 point);
vec3 draw_line(float d);
float draw_solid(float d);
vec3 draw_distance(float d, vec2 p);
mat3 calcLookAtMatrix(vec3 origin, vec3 target, float roll) {
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));
    return mat3(uu, vv, ww);
}
vec3 calcNormal(vec3 pos, float eps) {
    const vec3 v1 = vec3( 1.0, -1.0, -1.0);
    const vec3 v2 = vec3(-1.0, -1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0, -1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);
    return normalize( v1 * getDistanceFromPoint( pos + v1*eps ) +
    v2 * getDistanceFromPoint( pos + v2*eps ) +
    v3 * getDistanceFromPoint( pos + v3*eps ) +
    v4 * getDistanceFromPoint( pos + v4*eps ) );
}
vec3 getRay(vec3 origin, vec3 target, vec2 screenPos, float lensLength) {
    mat3 camMat = calcLookAtMatrix(origin, target, 0.0);
    return normalize(camMat * vec3(screenPos, lensLength));
}
float intersectPlane(vec3 ro, vec3 rd, vec3 nor, float dist) {
    float denom = dot(rd, nor);
    float t = -(dot(ro, nor) + dist) / denom;
    return t;
}
float getDistanceFromPoint(vec3 point) {
    vec3 offset = vec3(0.25, 0, 0);
    float radius = 0.3;
    float d1 = length(point - offset) - radius;
    float d2 = length(point + offset) - radius;
    float d3 = length(point - offset.zyx) - radius;
    float d4 = length(point + offset.zyx) - radius;
    return min(max(d1, d2), max(d3, d4));
}
float beckmannDistribution(float x, float roughness) {
    float NdotH = max(x, 0.0001);
    float cos2Alpha = NdotH * NdotH;
    float tan2Alpha = (cos2Alpha - 1.0) / cos2Alpha;
    float roughness2 = roughness * roughness;
    float denom = 3.141592653589793 * roughness2 * cos2Alpha * cos2Alpha;
    return exp(tan2Alpha / roughness2) / denom;
}
float cookTorranceSpecular(
vec3 lightDirection, vec3 viewDirection, vec3 surfaceNormal, float roughness, float fresnel) {
    float VdotN = max(dot(viewDirection, surfaceNormal), 0.0);
    float LdotN = max(dot(lightDirection, surfaceNormal), 0.0);

    //Half angle vector

    vec3 H = normalize(lightDirection + viewDirection);

    //Geometric term

    float NdotH = max(dot(surfaceNormal, H), 0.0);
    float VdotH = max(dot(viewDirection, H), 0.000001);
    float x = 2.0 * NdotH / VdotH;
    float G = min(1.0, min(x * VdotN, x * LdotN));

    //Distribution term

    float D = beckmannDistribution(NdotH, roughness);

    //Fresnel term

    float F = pow(1.0 - VdotN, fresnel);

    //Multiply terms and done

    return  G * F * D / max(3.14159265 * VdotN * LdotN, 0.000001);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    float time = iTime * 0.025;
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    vec3 ro = vec3(sin(time), 1.0, cos(time));
    vec3 ta = vec3(0);
    vec3 rd = getRay(ro, ta, uv, 2.0);
    float t = -1.0;
    float mind = 0.01;
    float maxd = 10.0;
    float latest = 1.0;
    for (int i = 0; i < 30; i++) {
        if (latest < mind || t > maxd) break;
        t += (latest = getDistanceFromPoint(ro + rd * t));
    }
    float tPlane = intersectPlane(ro, rd, vec3(0, 1, 0), 0.0);
    if (tPlane > -0.5 && tPlane < t) {
        vec3 pos = ro + rd * tPlane;
        fragColor = vec4(draw_distance(getDistanceFromPoint(pos) - 0.0125, pos.xz), 1);
    }
    else
    if (t > maxd) {
        fragColor = vec4(0, 0, 0, 1);
    }
    else {
        vec3 pos = ro + rd * t;
        vec3 normal = calcNormal(pos, 0.002);
        vec3 ldir = normalize(vec3(0, 1, 0.2));
        float mag = max(0.2, dot(normal, ldir));
        mag = pow(mag, 0.3545);
        mag *= 1.75;
        //mag = 0.0;


        fragColor = vec4(mag * vec3(0.95, 0.45, 0.15), 1);
        fragColor.rgb += cookTorranceSpecular(ldir, -rd, normal, 1.0, 3.25) * 1.5;
    }

}
vec3 draw_line(float d) {
    const float aa = 1.0;
    const float thickness = 0.0025;
    return vec3(smoothstep(0.0, aa / iResolution.y, max(0.0, abs(d) - thickness)));
}
float draw_solid(float d) {
    return smoothstep(0.0, 3.0 / iResolution.y, max(0.0, d));
}
vec3 draw_distance(float d, vec2 p) {
    float t = clamp(d * 0.85, 0.0, 1.0);
    vec3 grad = mix(vec3(1, 0.8, 0.5), vec3(0.3, 0.8, 1), t);
    float d0 = abs(1.0 - draw_line(mod(d + 0.1, 0.2) - 0.1).x);
    float d1 = abs(1.0 - draw_line(mod(d + 0.025, 0.05) - 0.025).x);
    float d2 = abs(1.0 - draw_line(d).x);
    vec3 rim = vec3(max(d2 * 0.85, max(d0 * 0.25, d1 * 0.06125)));
    grad -= rim * clamp(1.25 - d, 0.0, 1.0);
    grad -= 1.0 - clamp(1.25 - d * 0.25, 0.0, 1.0);
    grad -= mix(vec3(0.05, 0.35, 0.35), vec3(0.0), draw_solid(d));
    return grad;
}

#define THETA 2.399963229728653 //THETA is the golden angle in radians: 2 * PI * ( 1 - 1 / PHI )
vec2 spiralPosition(float t)
{
    float angle = t * THETA - iTime * .001; 
    float radius = ( t + .5 ) * .5;
    return vec2( radius * cos( angle ) + .5, radius * sin( angle ) + .5 );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = ( fragCoord - .5 * iResolution.xy ) / iResolution.y * 1024.;
    float a = 0.;
    float d = 50.;
    for(int i = 0; i < 256; i++)
    {
        vec2 pointDist = uv - spiralPosition( float(i) ) * 6.66;
        a += atan( pointDist.x, pointDist.y );
        d = min( dot( pointDist, pointDist ), d );
    }
    d = sqrt( d ) * .02;
    d = 1. - pow( 1. - d, 32. );
    a += sin( length( uv ) * .01 + iTime * .5 ) * 2.75;
    vec3 col  = d * (.5 + .5 * sin( a + iTime + vec3( 2.9, 1.7, 0 ) ) );
    //col   = d * smoothstep( .75, 1.0, vec3( .5 + .5 * sin( a + iTime * -1. ) ) );
    fragColor = vec4( col, 1. );
}

