vec2 scale_center(vec2 st, float a){
    st -= .5;
    st = mat2(a, 0, 0, a) * st;
    st += .5;
    return st;
}

vec2 rotate(vec2 st, float a){
    return mat2(cos(a), -sin(a), sin(a), cos(a)) * st;
}

float poli_field(vec2 st, float n){
    float a = atan(st.x, st.y)+3.141592;
    float r = (3.141592 * 2.)/n;

    return cos(floor(.5+a/r)*r-a) * length(st);
}

vec2 hash22(vec2 p){
    p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)));
    return -1.0 + 2.0*fract(sin(p) * 43758.5453123);
}

float hash21(vec2 st){
    return fract(sin(dot(st, vec2(12.9898,78.233))) * 43758.5453123);
}

float noise(vec2 st){
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = hash21(i);
    float b = hash21(i + vec2(1.0, 0.0));
    float c = hash21(i + vec2(0.0, 1.0));
    float d = hash21(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u.x) + (c - a)* u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

float fbm(vec2 st){
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.);
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < 5; ++i) {
        v += a * noise(st);
        st = rot * st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

vec2 fbm2(vec2 uv, float freq, float amp){
    float rx = (fbm(uv * freq) - .5);
    float ry = (fbm(uv * freq + 313.666999) - .5);
    return normalize(vec2(rx, ry)) * amp;
}

float voronoi(vec2 x ) {
    vec2 n = floor(x);
    vec2 f = fract(x);

    float md = 8.0;
    float val;
    for (int j= -1; j <= 1; j++) {
        for (int i= -1; i <= 1; i++) {
            vec2 g = vec2(float(i),float(j));
            vec2 o = hash22( n + g );
            o = 0.5 + 0.5*sin( 6.2831*o );

            vec2 r = g + o - f;
            float d = dot(r,r);

            if( d<md ) {
                md = d;
                val = clamp(dot(o,vec2(.3,.6)), 0., 1.);
            }
        }
    }

    return val;
}

float voronoise(vec2 uv, float rfreq, float ramp, float vfreq){
    float rx = (fbm(uv* rfreq) - .5);
    float ry = (fbm(uv * rfreq + 313.15843) - .5);
    vec2 dir = normalize(vec2(rx, ry)) * ramp;

    return voronoi(uv * vfreq + dir);
}

vec3 DiscreteLerp(float t){
    return _Color0 * step(t, .33) +
        _Color1 * step(.33, t) * step(t, .66) +
        _Color2 * step(.66, t);
}

float triang(vec2 uv, float a){
    vec2 d = rotate(vec2(0., .25), a);
    vec2 _uv = rotate(uv - d, -a);
    float f = poli_field(_uv * vec2(12., 6.), 3.);
    return smoothstep(.2, .19, f);
}

vec2 rand_dir(vec2 uv, float freq){
    float rx = (fbm(uv* freq) - .5);
    float ry = (fbm(uv * freq + 313.15843) - .5);
    return normalize(vec2(rx, ry));
}

float tooth(float x, float y){
    float yr = fract(y * 4.);
    y = abs(yr * 2. - 1.);
    y = fract(y);
    x += y * .15 + .005;
    return step(.5, x);
}

vec3 background(vec2 uv, vec2 f0, vec2 f1, vec2 f2){
    uv = (uv - .5) * 2.;

    float f0d = length(uv - f0);
    float ford = (1. - smoothstep(0., .4, f0d));

    f0d = length(uv - f1);
    ford += (1. - smoothstep(0., .4, f0d));

    f0d = length(uv - f2);
    ford += (1. - smoothstep(0., .4, f0d));                

    uv = rotate(uv, 3.14 * ford);
    //scroll
    uv.y += iTime * .1;

    vec3 col = vec3(0.);
    float rs = noise(uv * 6.28) * 0.098125;

    uv.x += sin((uv.y + rs) * 6.28) * 0.098125;
    uv *= vec2(12, 1.76);

    float rx = (fbm(uv* 64.) - .5);
    float ry = (fbm(uv * 64. + 696.666999) - .5);
    vec2 dir = normalize(vec2(rx, ry)) * 0.1;
    col.rgb = DiscreteLerp(voronoi(uv + dir));
    col.rgb *= pow(DiscreteLerp(voronoi(uv * 4. + dir + 313.313)), vec3(.2));

    return col;
}

vec4 rage(vec2 uv, vec2 c){
    vec2 dir = rand_dir(uv, 16.) * .0015;
    vec2 suv = scale_center(uv, .8);
    uv = (suv - .5 + dir) * 2. - c * 2.;

    float cir = smoothstep(.2, .19, length(uv));

    float tf1s = 0.;
    float a = 0.;

    for(int k=0; k<4; k++){
        tf1s += triang(uv, a);
        tf1s += triang(uv, a + 3.14 * .25) * 1.5;
        a += 3.14 * .5;
    }

    float v = voronoise(uv, 64., .5, 64.);
    v = mix(.4, .9, v);

    vec4 col = vec4((cir + tf1s) * v);
    col.rgb *= _Color0;
    col.a = step(.1, col.r);
    return col;
}            

vec4 sorrow(vec2 uv, vec2 c){
    vec2 dir = rand_dir(uv, 16.) * .0015;

    vec2 suv = scale_center(uv + dir, 3.14);
    uv = (suv - .5) * 2. - c * 3.14 * 2.;
    float cir = smoothstep(.9, .89, length(uv));

    uv = suv - c * 3.14;

    float t1 = tooth(uv.x, uv.y+ 0.125);
    float t2 = tooth(1.-uv.x, uv.y);
    float brk = 1. - t1 * t2;

    float v = voronoise(uv, 64., .5, 64.);
    v = mix(.4, .9, v);

    vec4 col = vec4(cir * brk * v);
    col.rgb *= _Color2;
    col.a = step(.01, col.r);
    return col;
}

vec4 reason(vec2 uv, vec2 c){
    vec2 dir = rand_dir(uv, 16.) * .0015;
    uv = scale_center(uv + dir- c, 3.5);
    uv = uv * 2. - 1.;
    float cir = smoothstep(.99, .98, length(uv));          
    float cir2 = smoothstep(.1, .09, length(uv - vec2(0, .5)));
    float cir3 = smoothstep(.1, .09, length(uv + vec2(0, .5)));

    uv *= 1.02;
    float x = uv.x + .5;
    float s = sin(uv.y * 3.14);
    float sig = sign(s);
    float cur = abs(s) * sig;

    x += cur * .33;
    float a = clamp(step(.5, x) + .5, 0., 1.);

    float re = cir * a + cir3 - cir2 * .5;

    float v = voronoise(uv, 32., .5, 32.);
    v = mix(.66, .9, v);

    vec4 col = vec4(re * v);
    col.rgb *= _Color1;
    col.a = step(.01, re);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    uv *= 1.5;
    uv = uv * .5 + .5;

    vec2 c = vec2(0, -.1);

    vec2 pn = vec2(.0, .25);
    float t = iTime * -.2;

    vec2 p0 = rotate(pn, t)+c;
    vec2 p1 = rotate(pn,  t + 4.71238898038)+c;
    vec2 p2 = rotate(pn,  t + 4.71238898038 * 3.)+c;

    vec4 ra = rage(uv, p0);
    vec4 re = reason(uv, p1);
    vec4 so = sorrow(uv, p2);

    vec4 fo = ra + re + so;
    fo.a = clamp(fo.a, .0, 1.);

    vec3 bg = background(uv, p0 * 2., p1 * 2., p2 * 2.);

    vec4 col = fo;
    col.rgb += bg * (1.-fo.a) * .66;

    fragColor.rgb = col.rgb;
}

