    //float d8 = segment_check(uv, vec2(-0.5,  0.28), vec2(-0.5, -0.28), 10.0);

    float mn = min(d0, d1);
          mn = min(mn, d2);
          mn = min(mn, d3);
          mn = min(mn, d22);
