const float two_Pi =6.28;
const float A=2.;
vec2 wind= vec2(1.,0.);
const float V=1000.;
const float g=9.8;
float L=V*V/g;

vec2 complex_multiply(vec2 c1,vec2 c2){
    float x = c1.x;
    float y = c1.y;
    float a = c2.x;
    float b = c2.y;
    return vec2(a * x - b * y,a * y + b * x);
}

float random(vec2 v)
{
    float seed_x=1113.;
    float seed_y=11.5;
    return fract(sin(dot(v,vec2(seed_x,seed_y)))*43758.54534 );
}

float random_clamp(vec2 v){
    // need clamp beacuse 
    // https://www.geogebra.org/m/dpvqeczu
	return clamp(random(v),0.001,1.);
}

// https://www.geogebra.org/m/dpvqeczu
vec2 gaussian_distribution(vec2 u){
    float u1=u.x;
    float u2=u.y;

    // Box-Muller
    // https://zhuanlan.zhihu.com/p/67776340
    float r =sqrt(-2.*log(u1));
    return vec2(r*cos(two_Pi*u2),r*sin(two_Pi*u2));
}

// https://zhuanlan.zhihu.com/p/64414956
float Pn(vec2 k,float K){
    float K2 =K*K;
    float KL =K*L;
    float dot_k_wind=abs(dot(k,wind));
    return  A/(K2*K2) * exp(-1./(KL*KL)) * dot_k_wind * dot_k_wind;
}

vec2 h0(vec2 k,vec2 E,float K){
    float S =sqrt(Pn(k,K)/2.);
    //return vec2(S);
	return S*E;
}

vec2 h0_conjugate(vec2 k,vec2 E,float K){
    vec2 c_h0=h0(k,E,K);
    return vec2(c_h0.x,-c_h0.y);
}

float w(float k){
	return sqrt(g*k);
}

vec2 e_i(float x){
	return vec2(cos(x),sin(x));
}

