//  Noise - value - 3D by iq (https://www.shadertoy.com/view/4sfGzS)
float hash(vec3 p)  // replace this by something better
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

//  Noise - value - 3D by iq (https://www.shadertoy.com/view/4sfGzS)
float noise(in vec3 x)
{
    vec3 i = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash(i+vec3(0,0,0)), 
                        hash(i+vec3(1,0,0)),f.x),
                   mix( hash(i+vec3(0,1,0)), 
                        hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(i+vec3(0,0,1)), 
                        hash(i+vec3(1,0,1)),f.x),
                   mix( hash(i+vec3(0,1,1)), 
                        hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

#define pi 3.1415926
#define tau (2.0 * pi)

#define N 5.0

float STEP(float x, float y)
{
    float o = 1.0 / iResolution.y;
    return smoothstep(x - o, x + o, y);
}

float water(in vec2 v)
{
    float r = 1.5 / N;
    float s = 1.0;

    for(float i = 0.0; i < N; i++)
    {
        vec2 c = vec2(iResolution.x / iResolution.y * (i + 0.5) / N, 0.5);
        float phase = tau * noise(vec3(i, 0.0, 0.0));
        float amp = 1.0;
        vec2 o = vec2(cos(iTime + phase), sin(iTime + phase)) / N;
        c += 0.1 * o;

        s = min(s, STEP(r, length(v - c)));
        s = min(s, max(STEP(r, abs(v.x - c.x)), STEP(v.y, c.y)));
    }

    return s;
}

