const float nrcircles = 7.;
const float pi = 3.141592;
const vec3 color = vec3(236.0 / 255.0, 71.0 / 255.0, 140.0 / 255.0);
const float innerradius = 0.5;
const float outerradius = 0.02;

vec2 shake(vec2 uv) {
	return vec2(uv.x + .1*sin(1. * pow(3., pi - mod(iTime, pi))), uv.y - .1*sin(1. * pow(2.5, pi - mod(iTime, pi))));
}

float edge(vec2 pixel, vec2 offset, float outerradius, float innerradius) {
    return clamp(outerradius / abs(length(pixel + offset) - innerradius), 0.0, 3.0);
}

float fade(float radius) {
    return 2. - radius;
}

float sonicboom(vec2 uv, float radius) {
    return edge(uv, vec2(.0), outerradius + (1. - .5*radius), radius) * fade(radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;	//centered, unstretched pixel coordinates: (0; 0) is the center of the viewport
    pixel = shake(pixel);
    float s;
    float c;
    float f;
    for(float i = 0.0; i < nrcircles; i++){
        s = cos(iTime + i * 2.0 * pi / nrcircles) * abs(sin(iTime)) * 0.4;
        c = sin(iTime + i * 2.0 * pi / nrcircles) * abs(sin(iTime)) * 0.4;
        f += edge(pixel, vec2(c, s), outerradius, innerradius);
    }

    //if(f < 1.0)	//Clamp the color so that it stays the same value, not brightened up
    vec3 destColor = color * f;
    float radius = 1.*mod(iTime + .01, pi) + innerradius;
    destColor += color * sonicboom(pixel, radius);

    // Output to screen
    fragColor = vec4(destColor, 1.0);
}

float box(vec2 p, vec2 b){vec2 q = abs(p) - b;return length(max(q,0.0)) + min(max(q.x,q.y),0.0);}
mat2 rot(float r){float c = cos(r), s = sin(r);return mat2(c,s,-s,c);}

float pinera_ql(vec2 uv)
{
    const float w = 0.01;
    const float h = 0.3;
    float d = 100.0;    
    vec2 pinera_ql_centro = vec2(0.15, 0.0);
    vec2 p = pinera_ql_centro + vec2(1.0, 0.0);
    d = min(d, box(uv + p - vec2(0.0, 0.0), vec2(w, h)));
    d = min(d, box(uv + p - vec2(0.1, 0.29), vec2(h / 3.0, w)));
    d = min(d, box(uv + p - vec2(0.1, -0.0), vec2(h / 3.0, w)));
    d = min(d, box(uv + p - vec2(0.2, 0.15), vec2(w, h / 2.0)));    
    vec2 i = pinera_ql_centro + vec2(0.7, 0.0);
    d = min(d, box(uv + i - vec2(0.0, 0.0), vec2(w, h)));    
    vec2 n = pinera_ql_centro + vec2(0.6, 0.0);
    d = min(d, box(uv + n - vec2(0.0, 0.0), vec2(w, h)));
    d = min(d, box((uv + n - vec2(0.09, 0.0)) * rot(0.3), vec2(w, h)));
    d = min(d, box(uv + n - vec2(0.18, 0.0), vec2(w, h)));
    d = max(d, -box(uv + n - vec2(0.1, 0.29), vec2(0.17, w*10.0)));
    d = min(d, box(uv + n - vec2(0.09, 0.29), vec2(h/3., w)));    
    vec2 e = pinera_ql_centro + vec2(0.32, 0.0);
    d = min(d, box(uv + e - vec2(0.0, 0.0), vec2(w, h)));
    d = min(d, box(uv + e - vec2(0.1, 0.29), vec2(h / 3.0, w)));
    d = min(d, box(uv + e - vec2(0.1, -0.0), vec2(h / 3.0, w)));
    d = min(d, box(uv + e - vec2(0.1, -0.29), vec2(h / 3.0, w)));    
    vec2 r = pinera_ql_centro + vec2(0.03, 0.0);
    d = min(d, box(uv + r - vec2(0.0, 0.0), vec2(w, h)));
    d = min(d, box(uv + r - vec2(0.1, 0.29), vec2(h / 3.0, w)));
    d = min(d, box(uv + r - vec2(0.1, -0.0), vec2(h / 3.0, w)));
    d = min(d, box(uv + r - vec2(0.2, 0.15), vec2(w, h / 2.0)));
	d = min(d, box((uv + r - vec2(0.155, -0.145)) * rot(0.3), vec2(w, h / 1.9)));    
    vec2 a = pinera_ql_centro + vec2(-0.27, 0.0);
    d = min(d, box(uv + a - vec2(0.0, 0.0), vec2(w, h)));
    d = min(d, box(uv + a - vec2(0.1, 0.29), vec2(h / 3.0, w)));
    d = min(d, box(uv + a - vec2(0.1, -0.0), vec2(h / 3.0, w)));
    d = min(d, box(uv + a - vec2(0.2, 0.0), vec2(w, h)));    
    vec2 _ = pinera_ql_centro + vec2(-0.55, 0.29);
    d = min(d, box(uv + _ - vec2(0.1, -0.0), vec2(h / 4.0, w)));    
    vec2 q = pinera_ql_centro + vec2(-0.83, 0.0);
    d = min(d, box(uv + q - vec2(0.0, 0.0), vec2(w, h)));
    d = min(d, box(uv + q - vec2(0.1, 0.29), vec2(h / 3.0, w)));
    d = min(d, box(uv + q - vec2(0.1, -0.29), vec2(h / 3.0, w)));
    d = min(d, box(uv + q - vec2(0.2, 0.0), vec2(w, h)));
    d = min(d, box((uv + q - vec2(0.18, -0.26)) * rot(0.6), vec2(w, h / 4.0)));    
    vec2 l = pinera_ql_centro + vec2(-1.134, 0.0);
    d = min(d, box(uv + l - vec2(0.0, 0.0), vec2(w, h)));
    d = min(d, box(uv + l - vec2(0.1, -0.29), vec2(h / 3.0, w)));
    return d;
}

