    vec3 color = vec3(.2,0,0);

    for(int i = 0 ; i < 10 ; i ++) {
        if( length(uv) > 1.) {
            break;
        }

        uv *= rotate(angle+float(i)*.5);
        uv*=2.;
        uv.x -=sign(uv.x); 

        color = vec3(sign(uv.y));
    }

