#define MAX_DIST 30.0
#define PI 3.1415927
#define T mod((iTime*2.),12.55)

vec2 rotate(vec2 a, float d){
    float s  = sin(d);
    float c = cos(d);

    return vec2(
        a.x*c-a.y*s,
        a.x*s+a.y*c);
}

float box(vec3 p, vec3 b)
{
    vec3 d = abs(p)-b;
    return length(max(d,0.0));
}

float husk(vec3 bp, vec3 p)
{

    return box(bp-vec3(0.,-2.,-4),vec3(10,3.+cos(p.x+p.z+p.y)/4.+bp.x/2.,.1));
}


vec2 map(vec3 p)
{


	vec3 pp = p+vec3(0.,8.,0.);
    pp.yz = rotate(pp.yz,0.);
    vec3 bp = pp+vec3(-T-1.,3.,0.);
    vec3 lp = p;
    lp.x-=T;
    float o =30.;
    float b = 1000000000.;

    float mx = 10.;
    for(float i=0.;i<mx;i++)
    {
        pp.yz = rotate(pp.yz,.6+i*.0001);
        bp = pp+vec3(-T-12.,0.,-1.-cos(T-i)/10.);
        bp.z+=((lp.x-o)*(lp.x-o))/25.;
        b=min(b,husk(bp,p));
    }

    p.y=p.y+abs(p.z*p.z)/25.;
    p.y+=(lp.x*lp.x)/400.;
    float c = p.y+.5*(1.-smoothstep(-1.,1.,abs(cos(abs(p.z)))*3.))+smoothstep(0.2,2.,(1.-abs(sin(p.x*2.))/1.5));

    float r=min(c,b);

    float m=0.0;

    if(r==c)m=1.;
    else if(r==b)m=2.;

    return vec2(r,m);
}

vec3 normal(vec3 p)
{
    vec2 e = vec2(0.0001,0.);
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
    ));

}

vec2 ray(vec3 ro, vec3 rd)
{
    float t =0.0;
    float m = 0.0;

    for(int i=0;i<128;i++)
    {

        vec3 p=ro+rd*t;
        vec2 h=map(p);
        m=h.y;
        if(h.x<0.00001)break;
        t+=h.x/2.;
        if(t>MAX_DIST)break;
    }

    if(t>MAX_DIST)t=-1.;

    return vec2(t,m);
}

vec3 color(vec3 p, vec3 n, vec2 t)
{
    vec3 c = vec3(0.);
    vec3 mate = vec3(1.32,1,0);
    if(t.y>1.5)
    {
        mate=vec3(0.,.5,0.)/4.*(.5+(1.+cos(p.x/2.)));
        mate+=(mate*texture(iChannel0,vec2(p.x,(p.z/p.y))).rgb)/2.;
    }
    vec3 sun=normalize(vec3(0.2,0.5,-0.5));
    float dif=clamp(dot(n,sun),0.0,1.0);
    float sha = step(ray(p+n*.001,sun).x,0.0);
    float sky = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.,1.);
    float bou = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.,1.);

    c=mate*vec3(0.5,0.6,0.5)*dif*sha;
    c+=mate*vec3(0.2,0.3,.8)*sky;
    c+=mate*vec3(0.2,0.1,0.1)*bou;

    return c;
}

vec3 render(vec3 ro, vec3 rd)
{    

    vec2 st = vec2(atan(rd.y,rd.z), length(ro));

	vec3 c = vec3(0.,.1,0.)*(.5+smoothstep(-0.,1.,cos(st.x*40.+iTime*3.)));


    vec2 t = ray(ro,rd);


    if(t.x>0.)
    {
        vec3 p = ro+rd*t.x;
        vec3 n=normal(p);

        c=color(p,n,t);

    }
   	c=pow(c,vec3(0.454));

    return c;
}


void mainImage( out vec4 c, in vec2 f )
{
    vec2 uv = (2.*f-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(T,2,0.);
    vec3 ta = vec3(T+10.,0,0.);
    vec3 camF =	normalize(ta-ro);
    vec3 camU = normalize(cross(camF,vec3(0,1,0)));
    vec3 camR = normalize(cross(camU,camF));

    vec3 rd = normalize(uv.x*camU+uv.y*camR+2.*camF);

    c.rgb=render(ro,rd);
}

float expStep( float x, float k, float n )
{
    return exp( -k*pow(x,n) );
}

float parabola( float x, float k )
{
    x = fract(x);
    return pow( 4.0*x*(1.0-x), k );
}
#define PI 3.141
float sinc( float x, float k )
{
    float a = PI*(k*x-1.0);
    return sin(a)/a;
}

