#define PI 3.141592653589793

float hash21(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * uvec2(1597334673U, 3812015801U);
	uint n = (q.x ^ q.y) * 1597334673U;
	return float(n) / float(0xffffffffU);
}

vec3 hash13(float p) {
   vec3 p3 = fract(vec3(p) * vec3(.1031,.11369,.13787));
   p3 += dot(p3, p3.yzx + 19.19);
   return fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}

float rainDrops(vec2 st, float time, float size)
{
    vec2 uv = st * size;
    uv.x *= iResolution.x / iResolution.y;
    vec2 gridUv = fract(uv) - .5; // grid
   	vec2 id = floor(uv);
    vec3 h = (hash13(id.x * 467.983 + id.y * 1294.387) - .5) * .8;
    vec2 dropUv = gridUv - h.xy;
    vec4 noise = textureLod(iChannel1, id * .05, 0.);
    float drop = smoothstep(.25, 0., length(dropUv)) *
        max(0., 1. - fract(time * (noise.b + .1) * .2 + noise.g) * 2.);
    return drop;
}

vec2 wigglyDrops(vec2 st, float time, float size)
{
    vec2 wigglyDropAspect = vec2(2., 1.);
    vec2 uv = st * size * wigglyDropAspect;
    uv.x *= iResolution.x / iResolution.y;
    uv.y += time * .23;

    vec2 gridUv = fract(uv) - .5; // rectangular grid
    vec2 id = floor(uv);

    float h = hash21(id);
    time += h * 2. * PI;
    float w = st.y * 10.;
    float dx = (h - .5) * .8;
    dx += (.3 - abs(dx)) * pow(sin(w), 2.) * sin(2. * w) *
        pow(cos(w), 3.) * 1.05; // wiggle
    float dy = -sin(time + sin(time + sin(time) * .5)) * .45; // slow down drop before continuing falling
    dy -= (gridUv.x - dx) * (gridUv.x - dx);

    vec2 dropUv = (gridUv - vec2(dx, dy)) / wigglyDropAspect;
    float drop = smoothstep(.06, .0, length(dropUv));

    vec2 trailUv = (gridUv - vec2(dx, time * .23)) / wigglyDropAspect;
    trailUv.y = (fract((trailUv.y) * 8.) - .5) / 8.;
    float trailDrop = smoothstep(.03, .0, length(trailUv));
    trailDrop *= smoothstep(-.05, .05, dropUv.y) * smoothstep(.4, dy, gridUv.y) *
        	(1.-step(.4, gridUv.y));

    float fogTrail = smoothstep(-.05, .05, dropUv.y) * smoothstep(.4, dy, gridUv.y) *
			smoothstep(.05, .01, abs(dropUv.x)) * (1.-step(.4, gridUv.y));

    return vec2(drop + trailDrop, fogTrail);
}

vec2 getDrops(vec2 st, float time)
{
    vec2 largeDrops = wigglyDrops(st, time * 2., 1.6);
    vec2 mediumDrops = wigglyDrops(st + 2.65, (time + 1296.675) * 1.4, 2.5);
    vec2 smallDrops = wigglyDrops(st - 1.67, time - 896.431, 3.6);
    float rain = rainDrops(st, time, 20.);

    vec2 drops;
    drops.y = max(largeDrops.y, max(mediumDrops.y, smallDrops.y));
    drops.x = smoothstep(.4, 2., (1. - drops.y) * rain + largeDrops.x +
                          mediumDrops.x + smallDrops.x); // drops kinda blend together

    return drops;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord / iResolution.xy;
    float time = mod(iTime + 100., 7200.);

    vec2 drops = getDrops(st, time);
    vec2 offset = drops.xy;
    float lod = (1. - drops.y) * 4.8;

    // This is kinda expensive, would love to use a cheaper method here.
    vec2 dropsX = getDrops(st + vec2(.001, 0.), time);
    vec2 dropsY = getDrops(st + vec2(0., .001), time);
    vec3 normal = vec3(dropsX.x - drops.x, dropsY.x - drops.x, 0.);
    normal.z = sqrt(1. - normal.x * normal.x - normal.y * normal.y);
    normal = normalize(normal);

    float lightning = sin(time * sin(time * 30.)); // screen flicker
    float lightningTime = mod(time, 10.) / 9.9;
   	lightning *= 1. - smoothstep(.0, .1, lightningTime)
        + smoothstep(.9, 1., lightningTime); // lightning flash mask

	vec3 col = textureLod(iChannel0, st+normal.xy * 3., lod).rgb;
    col *= (1. + lightning);

    col *= vec3(1., .8, .7); // slight red-ish tint
    col += (drops.y > 0. ? vec3(.5, -.1, -.15)*drops.y : vec3(0.)); // bloody trails
    col *= (drops.x > 0. ? vec3(.8, .2, .1) * (1.-drops.x) : vec3(1.)); // blood colored drops

    col = mix(col, col*smoothstep(.8, .35, length(st - .5)), .6); // vignette

    fragColor = vec4(col, 1.0);
}

float map(vec3 p)
{
	vec3 q = fract(p) * 2.0 - 1.0;
    return length(q) - 0.4;
}

float trace(vec3 o, vec3 r)
{
	float t = 0.0;
    for(int i; i < 32; i++){
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv * 2.0 - 1.0;
	uv.x *= iResolution.x / iResolution.y;
    float time = iTime * 0.25;

    vec3 o = vec3(0.0, time, time);

    vec3 r = normalize(vec3(uv, 1.0));
    r.xz *= mat2(cos(time), -sin(time), sin(time), cos(time));

    float t = trace(o, r);
    float fog = 1.0 / (1.0 + t * t * 0.1);
    vec3 fc = vec3(fog);

    vec3 col = 0.5 + 0.5 * cos(time + uv.xyx + vec3(0, 2, 4));

    // Output to screen
    fragColor = vec4(fc * col, 1.0);
}

#define S(a, b, t) smoothstep(a, b, t)
#define STAR_SPEED .5
#define TWINKLE_INT 12.
#define TWINKLE_SPEED 0.5
#define TWINKLE_MIN 0.1
#define GRID_MAX_SIZE 15.
#define GRID_MIN_SIZE .5
#define LAYER_SPEED .07
#define LAYER_COUNT 5.
#define SOUND_BOOST 1.2

float N21(vec2 p) {
    p = fract(p*vec2(233.34, 851.73));
    p += dot(p, p+23.45);
    return fract(p.x * p.y);
}

vec2 N22(vec2 p) {
    float n = N21(p);
    return vec2(n, N21(p+n));
}

float DistLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa, ba)/ dot(ba, ba), 0., 1.);
    return length(pa - ba*t);
}

vec2 GetPos(vec2 id, vec2 offs) {
    vec2 r = N22(id + offs)*iTime;
    return offs + sin(r)*STAR_SPEED;
}

float DrawLine(vec2 p, vec2 a, vec2 b) {
    float d = DistLine(p, a, b);
    float m = S(.03, .01, d);
    float d2 = length(a-b);
    m *= S(1.2, .5, d2) + S(.05, .03, abs(d2-.75));
    return m;
}

float Layer(vec2 uv) {
    float m = 0.;
    //make 5x5 grid centered
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);


    vec2 p[9];
    int i = 0;
    //get random point in each 3x3 grid cell
    for (float y=-1.; y <= 1.; y++) {
        for (float x=-1.; x <= 1.; x++) {
            p[i] = GetPos(id, vec2(x,y));
            i++;
        }
    }

    float t = iTime * TWINKLE_SPEED;

    for (int i=0; i<9; i++) {
        m += DrawLine(gv, p[4], p[i]);

        //point twinkle
        vec2 dFromP = (p[i] - gv) * TWINKLE_INT;
        //inverse square approximation
        float sparkle = 1. / length(dot(dFromP, dFromP));

        //animate
        m += sparkle*(sin(t+fract(p[i].x)*10.)*(.5-TWINKLE_MIN/2.) + (.5+TWINKLE_MIN/2.));
    }
    m += DrawLine(gv, p[1], p[3]);
    m += DrawLine(gv, p[1], p[5]);
    m += DrawLine(gv, p[5], p[7]);
    m += DrawLine(gv, p[3], p[7]);
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -.5 to .5)
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    //Normalized mouse coord from -.5 to .5
    vec2 mouse = (iMouse.xy / iResolution.xy)-.5;
    vec3 col = vec3(0);
    float gradient = uv.y;

    float m = 0.;
    float t = iTime*LAYER_SPEED;
    float s = sin(t);
    float c = cos(t);
    mat2 rot = mat2(c, -s, s, c);
    uv *= rot;
    mouse *= rot;

    for (float i=0.; i <= 1.; i+=1./LAYER_COUNT) {
        float z = fract(i+t);
        float size = mix(GRID_MAX_SIZE, GRID_MIN_SIZE, z);
        float fade = S(0., .5, z) * S(1., .8, z);
        m += Layer(uv*size + i*20. + mouse) * fade;
    }


    vec3 base = sin(t*5.*vec3(.345, .456, .678))*.4 + .6;
    col = m*base;

    float fft = texelFetch( iChannel0, ivec2(.7, 0), 0).x;
    gradient *= fft*SOUND_BOOST;
    col -= gradient * base;

    //if (gv.x > .48 || gv.y > .48) col = vec3(1,0,0);    
    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 700
#define MIN_DIS .01
#define STEP_SIZE .075

#define PI 3.141592

#define DISP 1.

#define MIN_SHADOW .1
#define MAX_SHADOW .2

#define AO_SAMPLES 1.
#define AO_INTENSITY 1.

#define T iTime*.5
#define L_P vec3(0., 10., 100.)
#define SHININNESS 16.

#define B vec3(.1)
#define W vec3(1.)
#define C1 vec3(.0, .1, .0)
#define C2 vec3(.0, .6, .0)

float hash21(in vec2 p)
{
    return fract(sin(dot(p, vec2(23.1, 98.3))) * 65192.8);
}

float plane(in vec3 p)
{
    float h21 = hash21(floor(p.xz))*DISP;
    h21 += (sin((p.x*.1)+T)*cos((p.z*.1)+T))*2.5;
    return p.y-h21;
}

float scene(in vec3 p)
{
    return plane(p);
}

vec3 normal(in vec3 p)
{
    vec2 e = vec2(.01, 0.);
    float d = scene(p);
    vec3 n = d - vec3(scene(p-e.xyy), scene(p-e.yxy), scene(p-e.yyx));
    return normalize(n);
}

float phong(in vec3 p, in vec3 n, in vec3 o)
{
    vec3 ld = normalize(L_P - p);
    vec3 vd = normalize(o - p);
    vec3 hv = normalize(ld + vd);

    float Kd = max(dot(n, ld), 0.);
    float Ks = pow(max(dot(n, hv), 0.), SHININNESS);

    return Kd+Ks;
}

float hShadow(in vec3 o)
{
    vec3 d = normalize(L_P - o);
    float t = MIN_SHADOW;
    for (int i = 0; i < 1; i+=0)
    {
        float s = scene(o + d * t);
        if (s < .01)
            return 0.5;
        if (t > MAX_SHADOW)
            break;
        t += s;
    }
   	return 1.;
}

float ambientOcclusion(in vec3 p, in vec3 n)
{
    float s = 1. / AO_SAMPLES;
    float ao = clamp((s-scene(p + n * s))*AO_INTENSITY, 0., 1.);
    return 1. - ao / AO_SAMPLES;
}

float shade(in vec3 p, in vec3 o)
{
    vec3 n = normal(p);
    float ph = phong(p, n, o);
    float a = .25;
    float s = hShadow(p);
    s = pow(s, 2.);
    float ao = ambientOcclusion(p, n);
    return clamp(ph*s + a*ao, 0., 1.);
}

float marcher(in vec3 o, in vec3 d)
{
    float t = 0.;
    for (int i = 0; i < MAX_STEPS; i++)
    {
        float s = scene(o + d * t);
        if (s < MIN_DIS)
            return t;
        t += s*STEP_SIZE;
    }
    return -1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x/iResolution.y;

    vec2 ms = iMouse.xy/iResolution.xy*2.-1.;
    ms.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);
    float h = 12.;
    vec3 o = vec3(ms.x*5., h, 0.);
    vec3 t = vec3(0., h-2.+ms.y*5., h/2.);
    vec3 f = normalize(o-t);
    vec3 r = cross(vec3(0., 1., 0.), f);
    vec3 u = cross(f, r);

	vec3 d = uv.x*r + uv.y*u - f;

    float m = marcher(o, d);
    if ( m > -1.)
    {
        vec3 p = o + d * m;

        float h21 = hash21(floor(p.xz));
        col += shade(p, o);
        vec3 C = abs(vec3(sin(T), 0., cos(T)));
        if (h21 < .94)
          	col *= B;
        else if (h21 >= .94 && h21 < .96)
            col *= C1+C;
        else if (h21 >= .96 && h21 < .98)
            col *= C2+C;
        else if (h21 >= .98)
            col *= W;
    }

    fragColor = vec4(sqrt(col), 1.);
}

float _Gloss = 400.;
float _BallCount = 6.;

//controls ball positioning
vec3 getSource(float i) {
    i = (sin(i + .2) * 6.) + 2.;
    return vec3(4., -2.,22.) 
           		 + vec3(dot(i,0.1)*sin(iTime*mod(i,5.0)*0.6)*4.0,
                        dot(i,0.4)*sin(iTime*mod(i,3.)*0.4)*4.0, dot(i,0.2)*sin(iTime*mod(i,3.)*.5)*0.1);    
}

//controls the merging
float metaField(in vec3 m, out vec3 normal) {

    normal = vec3(0,0,0);
    float fieldi = 0.0;

    //mouse control of ball

    vec3 so = vec3((iMouse.xy / iResolution.xy - vec2(0.35, 0.55)) * 15., 22.);
    float dis = length(m - so);
    float s = 1. / (dis*dis);
    vec3 l = normalize(m - so);
    normal += l * s;
    fieldi += s;

    for(float i=0.; i<_BallCount; i++) {

        vec3 source = getSource(i); //source is the ball position
        float d = length(m - source); //d is the distance from the position to the current draw dist

        float size = 1. / (d*d); //how big i want that layer to draw
        vec3 localNormal = normalize(m - source); //the normal of the blobs

        normal += localNormal * size;
        fieldi += size;
    }

    normal = normalize(normal);
    return fieldi;
}


float rayMarching(in vec3 origin, in vec3 ray, out vec3 m, out vec3 normal) {

    float	mDist = 0.0;

 	m = origin;   




    for(int i=0; i<140; i++) {
    	float fieldIntensity = metaField(m, normal);

        if(fieldIntensity > 0.5) {
            //return 1.;
        }
        //else if(fieldIntensity > 0.475 && fieldIntensity < 0.5){
        //	return 0.8;
        //}
        else {
            mDist += 0.2;
        	m = origin + ray * mDist;    
        }
    }

    float fieldIntensity = metaField(m, normal);
    if(fieldIntensity > 0.5){
    	return 1.;
    }//returns the glow
    else if(fieldIntensity > 0.01){
    	return pow(fieldIntensity * 2.6, 2.);
    }
	return 0.;    
}

vec4 getCol(in vec3 ray, in vec3 m, in vec3 normal, in vec3 light) {

    vec3 lightRay = normalize(m - light);
    float diffuse = dot(normal, -lightRay);

    vec3 reflectedL  = reflect(lightRay, normal);
    float hLight 		 = pow(max(dot(reflectedL, -ray),0.0), _Gloss);

    vec3 baseColor = vec3(0.2,0.5,0.8)*1.5 + normal * .4;

    vec3 diff = baseColor * 0.5 * diffuse;
    vec3 shine = vec3(1.0, 1.0, 1.0) * hLight;    
    vec3 ambiant = baseColor * 0.7;        

    return vec4(diff + shine + ambiant, 1.0);
}


mat3 rotXY(const in vec2 angle ) 
{
	vec2 c = cos( angle );
	vec2 s = sin( angle );	
	return mat3(c.y, 0.0, -s.y,
				s.y * s.x, c.x, c.y * s.x,
				s.y * c.x, -s.x, c.y * c.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    vec2 mo = vec2(0.5);
	mo = iMouse.xy/iResolution.xy;

    uv -= vec2(0.5);

    vec3 ro = vec3(1.,.0,0.);
	ro *= rotXY(vec2(-mo.x,-mo.y*1.5));

    vec3 cam = vec3(-.7, -.45, -0.4);
    //cam.x += sin(iTime) * 0.5;
    //cam.z += cos(iTime) * 0.5;
    vec3 ray = normalize(vec3(uv, 1.) - cam);
    vec3 light = vec3(-15.0, 5.0, 5.0);

    vec3 m;
    vec3 normal;

    float r = rayMarching(cam, ray, m, normal);
    if(r == 1.) {
        fragColor = getCol(ray, m, normal, light);
    }
    else if(r != 0.){
        vec4 outCol = vec4(vec3(r* 8.) * vec3(0.2,0.5,0.8)*1.1, 1.);
    	fragColor = outCol;
    }

    vec2 st = uv;
    st.y -= iTime * 0.002;
    vec3 n = vec3(fract(sin(uv.x * 49.4758 + st.y *742.923) + uv.x * 253.5235 + sin(st.y * 238.432))) * 0.045;
    vec3 cn = n + vec3(0.4, 0.4, 0.4) * 0.56;
    float rim = 1. - pow(length(uv + vec2(0.6, 0.5)), 1.4) * 1.5;
    //cn *= vec3(rim);

    fragColor += vec4(cn, 1.);
    fragColor *= rim * 0.7;
}


