float hash21 (vec2 c)
{return fract(sin(dot(c,vec2(21.45,40.49)))*1245.5);}

float sphe (vec3 p, float r)
{return length(p)-r;}

float g1 = 0.;
float sun (vec3 p)
{
    p.yz -= vec2(0.6, 0.4);
    float d = sphe (p,0.8+sin(iTime)*0.1+0.15);
    g1 += 0.1/(0.1+d*d);
    return d;
}

float g2 = 0.;
float moon (vec3 p)
{
    p.z += 0.4;
    p.y -= 0.5;
    p.y += clamp(sin(iTime*0.1)*3.,0.,3.);
    float d = sphe (p, 0.6);
    g2 += 0.1/(0.1+d*d);
    return d;
}

float water (vec3 p)
{
    p.y += 1.5;
    p.y += sin(length(p.xz*3.)-iTime)*0.1;
    return abs(p.y) -0.2;
}

float SDF (vec3 p)
{
    return min(water(p),min(sun(p), moon(p)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x / iResolution.y;

    float dither = hash21(uv);

    vec3 ro = vec3(0.001,0.001,-3.), 
        p = ro, 
        col = vec3(0.),
        rd = normalize(vec3(uv,1.));

    float shad = 0.;

    for (float i=0.; i<64.; i++)
    {
        float d = SDF(p);
        if(d<0.001)
        {
            shad = i/64.;
            break;
        }
        d *= 0.9+dither*0.1;
        p+=d*rd;
    }
    float t = length(ro-p);

    col = vec3(shad)*0.2;

    col += g1*0.15;
    col -= g2*0.05;

    col = mix(col, vec3(0.,0.,0.03),1.-exp(-0.01*t*t));

    fragColor = vec4(col,1.0);
}

#define AA 2

//gotta love iq and his wonderful website of knowledge
float sub(float d1, float d2){return max(-d1,d2);}

float smin(float d1, float d2, float k)
{
    float h = clamp(0.5+0.5*(d2-d1)/k,0.0,1.0);
    return mix(d2,d1,h)-k*h*(1.0-h);
}

float elipsoid(vec3 pos, vec3 rad)
{
    float k0=length(pos/rad);
    float k1 = length(pos/rad/rad);

    return k0*(k0-1.0)/k1;
}

float capsule(vec3 p, float h, float r)
{
    p.y-=clamp(p.y,0.0,h);
    return length(p)-r;
}


vec2 map(vec3 pos)   
{
    float mat = -1.;
    vec3 wormPos = pos+vec3(0.,1.5+sin(iTime/2.)*.2,0.);
    vec3 hookPos = wormPos-vec3(-0.3,.55,0);
    float w = elipsoid(wormPos+vec3(wormPos.z*.3,abs(wormPos.z)*.1,0.)*sin(iTime-wormPos.z),vec3(.3,.3+fract(wormPos.z*5.)/20.,4.25));
    float hb = elipsoid(hookPos+vec3(-0.3,-.4,0.),vec3(.7,.8,10.));
    float ht = elipsoid(hookPos+vec3(-.3,-.15,0.),vec3(.8,0.9,0.1));
    float base = sub(hb,ht);
    float top = capsule(hookPos+vec3(.35,.0,0.),3.,.1+.05*hookPos.y/2.);
    float hook = smin(base,top,.2);

    float line = capsule(hookPos+vec3(0.35,-3.0,0.),10.,.02);

    vec3 mp = mod(pos+vec3(-5.,-iTime,0.),vec3(30.,4,30.))-.5;

    float bubble = min(length(pos+vec3(-3,-30.+mod(-iTime*5.,220.),0))-.25,
                    length(pos+vec3(0,-90.+mod(-iTime*3.,130.),8))-.15);
    bubble=min(bubble,length(pos+vec3(5.,-90.+mod(-iTime*4.,180.),-8))-.2);
    float d = min(min(w,hook),line);
    d=min(d,bubble);

    if(d==w)mat=1.;
    else if(d==hook)mat=2.;
    else if(d==line)mat=3.;
    else if(d==bubble)mat=4.;

    return vec2(d,mat);
}

vec3 normal(vec3 p)
{
    float eps = 0.005;
    return normalize(vec3(
        map(vec3(p.x+eps,p.y,p.z)).x-map(vec3(p.x-eps,p.y,p.z)).x,
        map(vec3(p.x,p.y+eps,p.z)).x-map(vec3(p.x,p.y-eps,p.z)).x,
        map(vec3(p.x,p.y,p.z+eps)).x-map(vec3(p.x,p.y,p.z-eps)).x
    ));
}

float castShadow(in vec3 ro, in vec3 rd)
{
    float res = 1.0;
    float t = 0.0001;
    for(int i=0;i<128;i++)
    {

        vec3 pos = ro+t*rd;
        vec2 h = map(pos);

        res= min(res, 18.0*h.x/t);
        if(h.x<0.0001)break;
        t+=h.x;
        if(t>20.0)break;

    }

    return clamp(res,0.,1.);
}

vec2 ray(vec3 ro, vec3 rd)
{

    float t = 0.0;
    float m =0.0;
    int bounces = 128;
    float max_dist = 30.0;
    for(int i=0;i<bounces;i++)
    {
        vec3 pos = ro+t*rd;
        vec2 h = map(pos);
        m=h.y;
        if(h.x<0.0001)
        {
            break;
        }
        t+=h.x/2.;
        if(t>max_dist)break;
    }

    if(t>max_dist)t=-1.;

    return vec2(t,m);

}

vec3 color(vec3 p, vec3 n, vec2 r)
{
    vec3 col = vec3(0.);
    vec3 mate=vec3(0.);
    if(r.y<1.5)mate=vec3(0.7,.1,.3)*.6;
    else if(r.y<2.5)
    {
        mate=vec3(1.,1.,2.);
    }
    else if(r.y<3.5)mate=vec3(.1);
    else if(r.y<4.5)mate=vec3(0.3,0.3,4.5);
    vec3 sun_dir = normalize(vec3(.5,2.,0.2));
    float sun_dif = clamp(dot(n,sun_dir),0.0,1.0);
    float sun_sha = castShadow(p+n*0.001,sun_dir);
    float sky_dif = clamp(0.5+0.5*dot(n,vec3(0.,1.,0.)),0.,1.);

    col=mate*vec3(1.0,3.0,2.0)*sun_dif*sun_sha;
    col+=mate*vec3(0.1,0.1,0.3)*sky_dif;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float d = 0.0;
    vec3 tot=vec3(0.0);
    for(int ii=0;ii<AA;ii++)
    {
        for(int jj=0;jj<AA;jj++)
        {
            vec2 offset = vec2(float(ii),float(jj))/float(AA);

            vec2 uv = (2.*(fragCoord+offset)-iResolution.xy)/iResolution.y;

            float time = iTime/10.;
            vec3 ro = vec3(sin(time)*-10.,1.,cos(time)*-9.);
            vec3 ta = vec3(0.,-.5,0.);

            vec3 ww = normalize(ta-ro);

            vec3 uu = normalize(cross(ww,vec3(0,1,0)));
            vec3 vv = normalize(cross(uu,ww));

            vec3 rd = normalize(uv.x*uu+uv.y*vv+2.*ww);

            vec2 r = ray(ro,rd);
            d=r.x;

            vec3 p = ro+rd*r.x;

            vec3 n =normal(p);

            vec3 col = vec3(0.0,0,1.6-p.y)*(.8+sin(sin(p.x)+cos(p.y*20.+iTime-uv.x*5.))*vec3(.1));

            if(r.x>0.0)
            {
                col=color(p,n,r);

                if(r.y==2.||r.y==4.)
                {
                    rd = reflect(rd,n);
                    ro=p+n*.001;
                    r = ray(ro,rd);
                    p=ro+r.x*rd;
                    vec3 c = color(p,normal(p),r);
                    if(r.x>0.)col+=c;
                    else col+=c/4.;
                }

            }

		    col=pow(col,vec3(.454545));
            tot+=col;
        }
    }

    tot/=float(AA*AA);
    fragColor = vec4(tot,normalize(d));
}

const float pi = 4. * atan(1.);

float phi(float x) {
	float s = sin(pi * x);
    return s*s;
}

void mainImage( out vec4 O, in vec2 U )
{
    vec2 p = U / iResolution.x;

    p -= vec2 (0.5, 0.3);
    p *= 5.;

    // Polar coordinates with rotating
    // polar axis
    float r = length (p);
    float t = atan(p.y, p.x) - iTime / 2.;

    // coefft in [0,1], smoothly varying in time
    float w = phi(iTime / 6.);

    // 3 polar curves
    float c0 = 1. + cos(t) * cos(3.*t);
    float c1 = 1. + cos(t) - cos(2.*t);
    float c2 = cos(t) * cos(t) * cos(3.*t);

    // linear interpolation between the 3 curves
    float c = (2.*w-1.)*(w-1.)*c0 +
        	  4.*w*(1.-w)*c1 +
        	  w*(2.*w-1.)*c2;

    float e = smoothstep (-.1, .1, abs(r - c));

    vec4 tex = texture(iChannel0, p / 5.);

	O = tex * vec4(vec3(e), 1.) *
        vec4(mix(vec3(phi(iTime/2.), phi(iTime/3.), phi(iTime/5.)),
                 vec3(phi(iTime/5.), phi(iTime/2.), phi(iTime/3.)), phi(iTime / 10.)), 1.)
        + vec4(vec3(1. - e), 1.);
}

// Fork of "A flow of 33" by pik33. https://shadertoy.com/view/td3Sz8
