////////////////////////////////////////////////////////////////////////////////

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }

float uSDF(float s1, float s2) { return min(s1, s2);}

float sSDF( float d1, float d2 ) { return max(-d1,d2); }

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); 
}

vec3 opRep(vec3 p, vec3 c)
{
     return mod(p+0.5*c,c)-0.5*c;
}

vec2 opU( vec2 d1, vec2 d2 )
{
    return (d1.x < d2.x) ? d1 : d2;
}

// Scene setup

vec2 map(vec3 pos)
{
    vec2 t = vec2(0.0);
    float x = 0.0;
    float y = 0.0;

    t = vec2(pos.y, 0.0);

    // doggo

    vec3 dP = pos + vec3(0.0, -0.1, 1.7);

    dP.xz *= rot(PI/6.0);
    dP.x -= iTime;
    dP.y -= abs(sin(iTime*10.)*.1);
    dP = opRep(dP, vec3(1.0, 0.0, 1.0));
    vec2 dog = vec2(sdBox(dP, vec3(.1, .05, .05)), 0.0);

    vec3 oreP = dP + vec3(-0.08, -0.07, 0.04);
    float or = sdBox(oreP, vec3(0.02, 0.03, 0.01));
    oreP = dP + vec3(-0.08, -0.07, -0.04);
    or = uSDF(or, sdBox(oreP, vec3(0.02, 0.03, 0.01)));
    vec2 orm = vec2(or, 0.0);

    vec3 patP = dP + vec3(-0.09, 0.06, 0.04);
    float pat = sdBox(patP, vec3(0.01));
    patP = dP + vec3(0.09, 0.06, 0.04);
    pat = uSDF(pat, sdBox(patP, vec3(0.01)));
    patP = dP + vec3(0.09, 0.06, -0.04);
    pat = uSDF(pat, sdBox(patP, vec3(0.01)));
    patP = dP + vec3(-0.09, 0.06, -0.04);
    pat = uSDF(pat, sdBox(patP, vec3(0.01)));
    vec2 patm = vec2(pat, 0.0);

    vec3 faceP = dP + vec3(-0.1, -0.02, 0.02);
    vec2 face = vec2(sdBox(faceP, vec3(.005)), 2.0);
    faceP = dP + vec3(-0.1, -0.02, -0.02);
    face = opU(face, vec2(sdBox(faceP, vec3(.005)), 2.0));
    faceP = dP + vec3(-0.1, -0.0, 0.0);
    face = opU(face, vec2(sdBox(faceP, vec3(.04, .02, .015)), 0.0));
    faceP = dP + vec3(-0.13, 0.02, 0.0);
    face = opU(face, vec2(sdBox(faceP, vec3(.002, .01, .005)), 1.0));
    faceP = dP + vec3(-0.14, -0.02, 0.0);
    face = opU(face, vec2(sdBox(faceP, vec3(.01)), 2.0));

    t = opU(t, dog);
    t = opU(t, orm);
    t = opU(t, patm);
    t = opU(t, face);

    return t;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<64 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(float(i)/64., ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = vec3(ro + rd * t.x);

    vec3 col = vec3(0.0);

    if(t.x == -1.0)
    {

        col = vec3(.0); 

    }
    else
    {

        if(t.y == 0.0)
        {
            col = vec3(1.0 - t.x);
        }
        else if(t.y == 1.0)
        {
            col = vec3(1.0, 0.3, 0.7);
        }
        else if(t.y == 2.0)
        {
            col = vec3(0.0, 0.0, 0.0);
        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 1.0 * forward);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float time = iTime * 0.5;

    vec3 cp = vec3(0.0, .25, -2.0);
    vec3 ct = vec3(cos(iTime), 0.1, sin(iTime));

    vec3 vd = GetViewDir(uv, cp, ct);

    vec2 screenUV = fragCoord.xy / iResolution.xy;

    vec3 col = Render(cp, vd, screenUV);

    // compo
    col -= mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 1.0, 1.0), screenUV.y)*.3;

    col *= vec3(Vignette(uv, 0.45));

    col *= 1.1;

    fragColor = vec4(col,1.0);
}

#define PERIOD 5.
#define COLOR vec3(.7, .85, .8)

#define PI 3.14159265359

void tRotate(inout vec2 p, float angle) {
    float s = sin(angle), c = cos(angle);
	p *= mat2(c, -s, s, c);
}

float opU(float a, float b) {
    return min(a, b);
}

float opSU(float a, float b, float k) {
    float h = clamp(.5 + .5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

float sdCell(vec2 p, float size, vec2 dir) {
    return opSU(length(p) - size, distance(p, dir * size) - size * .75, size / 2.);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    float color = 0.;

    float res = min(iResolution.y, iResolution.x);
    vec2 uv = (fragCoord - iResolution.xy / 2.) / res;

    if (abs(uv.x) < .45 && abs(uv.y) < .45) {
        float t = fract(iTime / PERIOD);
        vec2 p = ((fragCoord - iResolution.xy / 2.) / res) * 2.5;

        // add some distortion
        p+= sin(p.yx * 5. + t * PI * 2.) * .01;
        p.x-= sin(p.y * 20. - t * PI * 4.) * .005;
        p.y-= sin(p.y * 50. - t * PI * 6.) * .002;

        // add camera movement
        float camera = smoothstep(.7, .9, t);
        tRotate(p, camera * PI / 2.);
        p += mix(vec2(.5), vec2(1, 0), camera);
        p *= mix(1., 2., camera);

        // entity positions
        vec2 bait = vec2(1);
        vec2 fool = vec2(.2);
        fool = mix(fool, bait, smoothstep(.2, .3, t));
        vec2 predator = vec2(2.6, -.6);
        vec2 bystander1 = mix(vec2(.25, 1), vec2(-.4, 1.4), t);
        vec2 bystander2 = mix(vec2(2, .5), vec2(1, -.5), t);
        vec2 bystander3 = mix(vec2(1.8, 0), vec2(-.5, -.5), t);

        // add cells
        float d = sdCell(p - bait, mix(.2, .0, smoothstep(.4, .8, t)), vec2(0, 1));
        d = opSU(d, sdCell(p - fool, .4, mix(vec2(0, -1), vec2(-1, 0), smoothstep(.0, .8, t)) * (1. + .5 * smoothstep(.05, .0, abs(t - .25)))), .25);
        d = opU(d, sdCell(p - predator, .8, vec2(1, 0)));

        // apply edges
        float edge = mix(.025, .05, smoothstep(.7, .9, t));
        d = abs(abs(d) - edge * 1.6) - edge;

        // add bystanders
        float crowd = distance(p, bystander1) - .1;
        crowd = opU(crowd, distance(p, bystander2) - .2);
        crowd = opU(crowd, distance(p, bystander3) - .1);
        d = opU(d, abs(crowd) - edge);

        // render
        float w = fwidth(d);
        color = min(1.,1. + d / w);
    }

	fragColor = vec4(mix(vec3(0), COLOR, color), 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float audio = texture(iChannel0, vec2(0.0,0.0)).r;


    vec2 transformedUV = (uv - vec2(0.5,0.5)) * mat2(cos(iTime * sin(iTime*0.01)), sin(iTime * sin(iTime*0.01)), -sin(iTime * sin(iTime*0.01)), cos(iTime * sin(iTime*0.01)));

    vec2 transformedUV2 = (uv - vec2(0.5 + cos(iTime*1.5)*0.25,0.5 + sin(iTime*1.5) * 0.25)) * mat2(cos((-iTime + distance(uv, vec2(0.5,0.5))*5.) * sin(iTime*0.01)), sin((-iTime + distance(uv, vec2(0.5,0.5))*5.) * sin(iTime*0.01)), -sin((-iTime + distance(uv, vec2(0.5,0.5))*5.) * sin(iTime*0.01)), cos((-iTime + distance(uv, vec2(0.5,0.5))*5.) * sin(iTime*0.01)));

    vec3 col = 0.5 + 0.5*cos(iTime + audio+uv.xyx+vec3(0,2,4)) + (texture(iChannel1, transformedUV).rgb) + texture(iChannel2, uv + vec2(cos(iTime + uv.y*5.), 0.0)).b*audio;

    bool checka = distance(fragCoord.xy, (iResolution.xy/2.0) + vec2(cos(iTime*5.0)*12.0,sin(iTime*5.0)*cos(iTime*5.0)*5.0)*10.0) < ((75.0 * iResolution.y/300.0) + audio*70.0* iResolution.y/300.0);
    bool checkb = distance(fragCoord.xy, (iResolution.xy/2.0) + vec2(cos(iTime*5.0)*12.0,sin(iTime*5.0)*cos(iTime*5.0)*5.0)*10.0) > ((50.0 * iResolution.y/300.0) + audio*80.0* iResolution.y/300.0);
   	bool checkc = distance(fragCoord.xy, (iResolution.xy/2.0) + vec2(cos(iTime*5.0)*12.0,sin(iTime*5.0)*cos(iTime*5.0)*5.0)*10.0) < ((25.0 * iResolution.y/300.0) + audio*90.0* iResolution.y/300.0);

    if(checka && (checkb || checkc))
        col =  0.5 + 0.5*cos(-iTime+uv.xyx+vec3(0,6,8)+audio) + texture(iChannel1,transformedUV2).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 0.6666666, 0.3333333, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 border = vec2(0.05, 0.1);
    vec4 backCol = vec4(1,1,1,1);
    vec2 uv = fragCoord/iResolution.xy;

    // generate border mask
	vec2 mask2 = step(border, uv) * step(uv, 1.0-border);
    float mask = mask2.x*mask2.y;

    float progress = mod(iTime, 1.0);  
    float blend = ((uv.x - progress) <= 0.0 ? 1.0 : 0.0) * mask;
    vec4 foreCol = vec4(hsv2rgb(vec3(progress*0.33333 - 0.1, 1.0, 1.0)), 1.0);
    fragColor = foreCol*blend + backCol*(1.0-blend);
}

float HexDist(vec2 p)
{
    p = abs(p);
    float c = dot(p, normalize(vec2(1,1.73)));
    c = max(c, p.x);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);

    col += cos(HexDist(uv)*10.0 + iTime);

    col = col * vec3( 0.8, 1, 0.3); col.g = sqrt(col.g);


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define steps 850.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 pos = vec3( fragCoord/iResolution.x - vec2( 0.5, 0.5 ), 0.0 );

    vec3 origin = vec3( 0.0, 0.0, 1.0 );
    vec3 ray_dir = pos - origin ;
    vec3 c = vec3( -1.05241, -.8134335, -.95455);

    vec3 col;
    float pitch = iTime / 5.0;
    float cos_a = cos(pitch);
    float sin_a = sin(pitch);
    float yaw = 0.6+sin(iTime/2.0)/3.0;
    float sin_b = sin( yaw );
    float cos_b = cos( yaw );
    mat3 rot_m = mat3( cos_a, 0.0, -sin_a,
                    sin_a*-sin_b, cos_b, cos_a*-sin_b,
                    sin_a*cos_b, sin_b, cos_a*cos_b );                        

    origin = rot_m*vec3( 0,0,1.0  + sin(iTime*0.9 ) * .55 ) ;
    origin.y = .9;
    ray_dir = rot_m*ray_dir;

    for( float depth = 60.0; depth < steps; depth ++ ) {
        pos = origin +  depth * ray_dir * 0.003;
        ray_dir = ray_dir * 1.003;
        vec3 prev  = vec3( 1,1,1), prev_prev;
        for( int idx=0; idx< 7;idx++ ) 
        {
            prev_prev = prev;
            prev = pos;
            pos = abs(pos);
            pos =  pos / dot( pos, pos ) + c;
            if( dot( pos,pos  )  > 6.89 ) 
            {
                if( idx  >3  ) 
                {
                    col = normalize( prev - prev_prev ) / 2.0 + vec3( 0.5, 0.5, 0.5 );
                } else  
                {

                  col = normalize( -prev-prev_prev ) / 2.0 + vec3( 0.5, 0.5, 0.5 );
                } 
                depth = 100000.0;
                break;
            }
        }
    }    
    fragColor = vec4(col,1.0);
}

