vec2 N(float angle) {
    return vec2(sin(angle), cos(angle));
}

vec2 po (vec2 v) {
	return vec2(length(v),atan(v.y,v.x));
}
vec2 ca (vec2 u) {
	return u.x*vec2(cos(u.y),sin(u.y));
}
float ln (vec2 p, vec2 a, vec2 b) { 
    float r = dot(p-a,b-a)/dot(b-a,b-a);
    r = clamp(r,0.,1.);
    p.x+=(0.7+0.5*sin(0.1*iTime))*0.2*smoothstep(1.,0.,abs(r*2.-1.))*sin(3.14159*(r-4.*iTime));
    return (1.+0.5*r)*length(p-a-(b-a)*r);
}
void mainImage( out vec4 Q, in vec2 U )
{   vec2 R = iResolution.xy;
 	float r = 1e9;
 	vec2 mouse = 2.*iMouse.xy/iResolution.xy-1.; // -1 1 
 	U = 4.*(U-0.5*R)/R.y;
 	U.y += 1.5;
 	Q = vec4(0);
 	for (int i = 1; i < 16; i++) {
        U = ca(po(U)+0.3*(sin(2.*iTime)+0.5*sin(4.53*iTime)+0.1*cos(12.2*iTime))*vec2(0,1));
        r = min(r,ln(U,vec2(0),vec2(0,1.)));
        U.y-=1.;

        vec2 n = N(2.2);
        float d = dot(U, n);
        U -= min(0.,d)*n*2.;

        U.x=abs(U.x);
        U*=1.4+0.1*sin(iTime)+0.05*sin(0.2455*iTime)*(float(i));
        U = po(U);
        U.y += 1.+0.5*sin(0.553*iTime)*sin(sin(iTime)*float(i))+0.1*sin(0.4*iTime)+0.05*sin(0.554*iTime);
        U = ca(U);


        Q+=sin(1.5*exp(-1e2*r*r)*1.4*vec4(1,-1.8,1.9,4)+iTime);


 	}
 	Q/=18.;
}

#define MAX_DIST 20.0
float g;
float bulb(vec3 p)
{

    vec3 z = p;

    {
        const float exBR = 1.5;
        float r = length(p)-exBR;
        if(r>1.0){return r;}
    }

    float dr =1., r=0., pw = 8., fr=.0, theta, phi, zr;
    for(int i=0;i<10;i++)
    {

        r=length(z);
        if(r>2.)
        {
            fr = min(0.5*log(r)*r/dr, length(p)-.72);
            break;
        }
        theta=acos(z.z/r)+iTime/10.;
        phi = atan(z.y,z.x);
        dr = pow(r,7.)*7.*dr+1.;

        zr = pow(r,pw);
        theta = theta*pw;
        phi = phi*pw;

        z=zr*vec3(sin(theta)*cos(phi),
                  sin(phi)*sin(theta),
                  cos(theta))+p;
    }

    return fr;

}

float map(vec3 p)
{
    float s = bulb(p);

    g+=0.1/(0.1+s*s);

    return s;
}

float ray(vec3 ro, vec3 rd)
{
    float t = 0.;
    for(int i=0;i<100;i++)
    {

        vec3 p = ro+rd*t;
        float s = map(p);

        if(s<0.00001)break;
        t+=s;
        if(t>MAX_DIST)break;

    }
    if(t>MAX_DIST)t=-1.;

    return t;
}

vec3 normal(vec3 p)
{
    vec2 e= vec2(0.005,0.);
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
        ));
}

vec3 color(vec3 ro, vec3 rd, float r)
{
    vec3 c = vec3(0.);
    vec3 cs = 0.5 + 0.5*cos(iTime+rd.xyx+vec3(0,2,4));

    if(r>0.)
    {
        vec3 p = ro+rd*r;
        vec3 n = normal(p);
        vec3 sun = normalize(vec3(0.2,0.5,0.3));
        float dif = clamp(dot(sun,n),0.0,1.0);
        float sky = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.,1.);

        c=vec3(r)*-.1;
        c+=cs*r*dif;
        c+=r*sky*vec3(0.9,0.5,0.5);

    }


    return c+(g/75.*cs);
}

void mainImage( out vec4 c, in vec2 f )
{
    vec2 uv = (2.*f-iResolution.xy)/iResolution.y;


    float d = 1.2, t=3.1415927/2.;
    vec3 ro = vec3(cos(t)*d,0,sin(t)*d);

    vec3 ta = vec3(0);

    vec3 cf = normalize(ta-ro);
    vec3 cu = normalize(cross(cf,vec3(0,1,0)));
    vec3 cr = normalize(cross(cf,cu));

    float dst =( min((1.+cos(iTime/5.))/2.+.1,1.)*.5)/2.+.3;
    vec3 rd = normalize(uv.x*cu+uv.y*cr+dst*cf);

    float r = ray(ro,rd);

    c = vec4(color(ro,rd,r),1.0);
}

struct CircleData
{
    vec2 uv;
    float number;
    float cr;
    float distScale;
    float offsetScale;
    float time;
    float speed;
    vec2 idOff;
};

float hash21(vec2 p)
{
    return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

//rotate incoming vector
vec2 rot(vec2 inVec, float a)
{
    float c = cos(a);
    float s = sin(a);
    return vec2(
        inVec.x * c - inVec.y * s,
        inVec.x * s + inVec.y * c);
}


//Vec3 x,y = offset; z = mask
vec3 circleUV(vec2 uv, vec2 offset, float radius)
{
    vec2 off = (uv - offset);
  	float mask = step(-radius, -length(off));
    vec2 cv =  off/radius;
    vec2 maskedOff = cv * mask;

    return vec3(maskedOff, mask);
}

//smooth 2dnoise based on textures that moves with time
vec2 smNoiseOff(vec2 uv, vec2 scale, float str)
{
    return texture(iChannel2, uv * scale + iTime * 0.1).xy * str;
}

//Return uv offset + mask of circle
vec3 GetCircleOffset(vec2 uv, vec2 center, float radius)
{
    vec3 circle = circleUV(uv, center, radius);
    vec2 cv = circle.xy;
    float cm =circle.z;

    vec2 cvOff = pow(cv,vec2(3.)) * 2.;
    cvOff += smNoiseOff(uv, vec2(2.), .5) * cm;

    return vec3(cvOff, cm);
}

//Generates a custer of circles
vec3 multiCircleOffset(CircleData data)
{
    vec3 off = vec3(0);

    for(float y=-data.number; y<= data.number; y++)    
    {
     	for(float x=-data.number; x<= data.number; x++)    
    	{            
            vec2 pos = vec2(x,y);
          	pos += data.idOff;// <----- id randomizer

            vec2 offCenter = vec2(hash21(pos.xy)-1.,hash21(pos.yx)-1.)* data.offsetScale;
            offCenter += .5;

            vec2 offTime = vec2(data.time * data.speed + sin(data.time * data.speed * .5) * 0.2) * (offCenter*0.5);
            float crOff = (sin(offTime.x)*.5+.5) * data.cr;

            vec2 cc = vec2(sin(offTime.x), cos(offTime.y)) * data.distScale + offCenter;
            off += GetCircleOffset(data.uv, cc, data.cr + crOff);
        }
    } 

    return off;
}

//Samples neighbour cells to allow for overlaps
//Expensive but easy to implement
vec3 multiCircleOffsetMultiCell(CircleData data, int range)
{
    CircleData cpy = data;
    vec3 avg = vec3(0);
    for(int y = -range; y <= range; y++)
    {
        for(int x = -range; x <= range; x++)
        {
            vec2 idlocal = vec2(1);
            idlocal *= vec2(x,y);

            cpy.uv = data.uv + idlocal * 2.;
            cpy.idOff = data.idOff - idlocal;

            avg += multiCircleOffset(cpy);
        }
    }
    return avg;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy)/ iResolution.y;
    vec3 col = vec3(0.);    

    //Dynamic zoom
    vec2 uvm = uv * 3.;
    uvm *=  sin(iTime * 0.25) * 0.3 + 1.;

    //Rotate bg
    uv = rot(uv, iTime * 0.1);

    //Dynamic pan
    float panTime = iTime * 0.1;
    float panScale = 0.1;
    uv += vec2(sin(panTime),cos(panTime)* panScale);

    //Gridify cells
    float gridCells = 2.; //Bug here!! why is only two working for this O_o?
    vec2 gv = fract(uvm )* gridCells - vec2(gridCells * 0.5);
    vec2 id = floor(uvm);


    //Modulate circle radius with time and music
    float musicMod = texture(iChannel1, vec2(0.,-.5)).x * 0.1;
    float cr = sin(iTime) * 0.01 + (0.3 +  musicMod);    
    cr *= 0.3;

    //Circle params <----- Circle Cluster Config!
    float speed = 2.6;
    float time = iTime + 1000.;
    float number = 2.;
    float distScale = 1.8 + sin(iTime * 0.3)*.5-.5;
    float offsetScale = 1.5;
    int range = 1;
    CircleData data = CircleData(gv, number, cr, distScale, offsetScale,time, speed, id); 

    //Get circle uv distortion
    vec3 circleOff = multiCircleOffsetMultiCell(data, range) * 0.05;
    vec2 uvRef = uv +circleOff.xy;

    //Sample bg with offset
  	col += texture(iChannel0, uvRef).xxx;
    //Enhance circle to background contrast
    col *= 0.3 + circleOff.z * 3.0;
    //USe mask to color only bubbles
    col += vec3(circleOff.z,0,sin(iTime*0.1) * circleOff.z) * 0.5;

    //Debug grid
    float gridWidth = 0.01;
  	//col += vec3(1.,.0,.0) * max(step(1.- gridWidth, gv.x), step(1.- gridWidth, gv.y));


    // Output to screen
    fragColor = vec4(col,1.0);
}

