#define hr vec2(1.,sqrt(3.))
#define PI 3.141592

float xor (float a, float b)
{return (1.-a)*b + (1.-b)*a;}

float hdist (vec2 uv)
{
    uv = abs(uv);
    return max(uv.x, dot(uv, normalize(hr)));
}


vec3 hgrid (vec2 uv)
{
    uv *= 4.;

    vec2 ga = mod(uv,hr)-hr*0.5;
    vec2 gb = mod(uv-hr*0.5,hr)-hr*0.5;
    vec2 guv = (dot(ga,ga) < dot(gb,gb)) ? ga : gb;

    float m = 0.;
    vec2 id = guv - uv;

    float dist = length(id)*5.;
    for(float i = -1.; i<=1.;i++)
    {
     	for(float j = -1.; j<=1.;j++)
    	{
        	vec2 offset = vec2(i,j);
            float r = mix(0.5,1.8,sin(iTime-dist)*0.5+0.5);
            float d = hdist(guv-offset);
            m = xor(m,smoothstep(r,r*0.95, d));
    	}   
    }
    return vec3(m,id);
}

vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{return a+b * cos(2.*PI*(c*t+d));}

vec3 frame (vec2 uv)
{
	vec3 hg = hgrid(uv);
    return palette(length(uv*2.),
                   vec3(0.5), 
                   vec3(0.5), 
                   vec3(hg.x*0.3), 
                   vec3(0.2,0.,0.7));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col = frame(uv);

    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}

#define num_dots 50
#define step_size 400.0
#define step_width 1000.0

const vec3 colors[3] = vec3[3] (
    vec3( 1.0, 0.0, 0.0  ),
    vec3( 0, 1.0, 0.0 ),
    vec3( 0, 0.0, 1.0 )
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 sc = vec2( 0.5, 0.5*iResolution.y/iResolution.x );
    vec2 pos = fragCoord/iResolution.x - sc;

    vec2 dots[ num_dots ];

    float charge = 0.0;
    float g = iTime*0.012 + 0.89;
    float f = iTime*0.0452 + 0.389;
    for( int idx=0; idx<num_dots;idx++ ) 
    {
        vec2 charge_pos  = vec2(sin( iTime*0.823+float(idx)*g )*0.417,
                            sin( iTime*0.5125+float(idx)*f)*0.25);
        float chr = 2.0+sin( iTime*1.4231 + float(idx)*0.9232)*2.0;                            
        charge = charge + chr / dot( pos-charge_pos, pos-charge_pos);
    }    

    vec3 final_color = vec3( 0.0, 0.0, 0.0 );
    for( int c=0; c<3; c ++ ) 
    {
        float intensity = smoothstep( 1000.0 + float(c)*step_size, 
                1000.0 + float(c)*step_size + step_width, charge);

        final_color = final_color + colors[ c ]*intensity;
    }
    fragColor = vec4(final_color,1.0);
}

