vec3 rotateY(vec3 p, float rad) {
    p.x = cos(rad)*p.x + sin(rad)*p.z;
    p.z = -sin(rad)*p.x + cos(rad)*p.z;
    return p;
}

vec3 rotateZ(vec3 p, float rad) {
    p.x = cos(rad)*p.x - sin(rad)*p.y;
    p.y = sin(rad)*p.x + cos(rad)*p.y;
    return p;
}

