*/
}

#define pi 3.141592

float fOpUnionStairs(float a, float b, float r, float n) {
	float s = r/n;
	float u = b-r;
	return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2. * s)) - s)));
}
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}


float sphere(vec3 p, float radius)
{
    return length(p)- radius;
}
// 中
float zhong(vec3 p){

    vec3 pLeft = p+vec3(2.1,-0.5,0.);
    float left = sdCappedCylinder(pLeft,vec2(.1,.7));


    vec3 pRight = p+vec3(.4,-0.5,0.);
    float right = sdCappedCylinder(pRight,vec2(.1,0.7));

    vec3 pTop = (p+vec3(1.25,-1.15,0.)).yxz;
    float top = sdCappedCylinder(pTop,vec2(.1,1.));


    vec3 pDown = (p+vec3(1.25,0.,0.)).yxz;
    float down = sdCappedCylinder(pDown,vec2(.1,1.));


    vec3 pMiddle = p+vec3(1.25,0.,0.);
    float middle = sdCappedCylinder(pMiddle,vec2(.1,1.5));


    return fOpUnionStairs(
                            fOpUnionStairs(
                  				fOpUnionStairs(right,left,.1,3.),
                   				fOpUnionStairs(top,down,0.1,3.),
                                .1,
                                3.),
                             middle,
                               .1,
                               3.);

}
//国
float guo(vec3 p) {

    vec3 pLeft = p+vec3(2.,0.0,0.);
    float left = sdCappedCylinder(pLeft,vec2(.1,1.5));


    vec3 pRight = p+vec3(-.5,0.,0.);
    float right = sdCappedCylinder(pRight,vec2(.1,1.5));

    vec3 pTop = (p+vec3(0.9,-1.30,0.)).yxz;
    float top = sdCappedCylinder(pTop,vec2(.1,1.3));


    vec3 pDown = (p+vec3(0.9,1.30,0.)).yxz;
    float down = sdCappedCylinder(pDown,vec2(.1,1.3));




    vec3 pMiddle = p+vec3(.70,0.,0.);
    float middle = sdCappedCylinder(pMiddle,vec2(.1,1.));

    vec3 pMiddleTop = (p+vec3(0.7,-1.,0.)).yxz;
    float middleTop = sdCappedCylinder(pMiddleTop,vec2(.1,0.9));


    vec3 pMiddleMiddle = (p+vec3(0.7,.0,0.)).yxz;
    float middleMiddle = sdCappedCylinder(pMiddleMiddle,vec2(.1,0.7));

    vec3 pMiddleDown = (p+vec3(0.7,1.,0.)).yxz;
    float middleDown = sdCappedCylinder(pMiddleDown,vec2(.1,0.9));

    vec3 pMiddleDot = (p+vec3(-.1,.8,0.)).yxz;
    pMiddleDot.xy *= rotate2d(-45.);
    float middleDot= sdCappedCylinder(pMiddleDot,vec2(.1,0.3));

    float st = 3.;
    float l = 0.05;
    float kou = fOpUnionStairs(
        						fOpUnionStairs(
                                    fOpUnionStairs(right,left,.1,st),
                                    fOpUnionStairs(top,down,.1,st),
                                    .1,
                                    st),
        						middleDot,
    							.1,
    							st);
    float sheng = fOpUnionStairs(middleDown,fOpUnionStairs(middleMiddle,
                                     fOpUnionStairs(middleTop,middle,l,st),
                                     l,
                      				 st),
                                 l,
                                 st);
    return fOpUnionStairs(kou,sheng,l,st);
}
float SDF(vec3 p){

     p *= .5;
    float s = sphere(p+vec3(0.+sin(iTime+3.)/1.,0.+cos(iTime+3.)/1.,-2.),sin(iTime)/5.+p.z+3.);
    float zhongguo =  min(zhong(p+vec3(1.,0.,0.)),guo(p+vec3(-3.,0.,0.)));
    return min(max(s,zhongguo),mix(s,zhongguo,1.+sin(iTime)/5.));

}


vec3 get_normal(vec3 p){
  vec2 eps = vec2(0.1,0.);
    return normalize(
             vec3(
                  SDF(p+eps.xyy) - SDF(p-eps.xyy), // Diff in X
                  SDF(p+eps.yxy) - SDF(p-eps.yxy), // Diff in Y
                  SDF(p+eps.yyx) - SDF(p-eps.yyx)  // Diff in Z
             )
            );  // Math Vector
}

