#define PI 3.141592
#define ITER 100.

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

void moda (inout vec2 p, float rep)
{
    float per = (2.*PI)/rep;
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a,per)-per*0.5;
    p = vec2(cos(a),sin(a))*l;
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float pour (vec3 p, float rythm, float width)
{
    p.xz *= rot(sin(-p.y*rythm+iTime));
    moda(p.xz, 3.);
    p.x -= width;
    return cyl(p.xzy, width*0.5, 10.);
}

float SDF (vec3 p)
{
    p.xz *= rot(p.y*0.5+iTime);
    float ry = 0.5,wi = 0.5;
    float d = pour(p, ry, wi);
    for (int i=-2; i<=2; i+=2)
    {
        ry += 0.2;
        wi -= 0.1;
        for (int j=-2; j<=2; j+=2)
    	{
            vec3 offset = vec3(float(i),0.,float(j));
            d = smin(d, pour(p+offset, ry, wi), 2.);
    	}
    }
    return d;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(vec3( SDF(p+eps.xyy)-SDF(p-eps.xyy),
                         SDF(p+eps.yxy)-SDF(p-eps.yxy),
                         SDF(p+eps.yyx)-SDF(p-eps.yyx)
                         )
                    );
}

float lighting (vec3 n, vec3 l)
{return dot(n, normalize(l))*0.5+0.5;}

// courtesy of Alkama
vec3 pales (vec2 uv)
{
  uv *= rot(-iTime*.2);
  return floor(smoothstep(0.1, 0.2,cos(atan(uv.y, uv.x)*5.)))*vec3(0.4);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
	vec3 ro = vec3(0.001,0.001,-7.),
        p = ro,
        rd = normalize(vec3(uv,1.)),
        l = vec3(0.,5.,-2.),
        col = mix(vec3(0.),
              mix(vec3(0.3,0.25,0.15), vec3(0.8,0.7,0.4), pales(uv)),
              smoothstep(0.35,1.,abs(uv.x)));

    float shad = 0.;
    bool hit = false;

    for (float i=0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            hit = true;
            shad = i/ITER;
            break;
        }
        p += d*rd*0.5;
    }
    float t = length(ro-p);
    if (hit)
    {
        vec3 n = getnorm(p);
        col = mix(vec3(0.01,0.005,0.005), vec3(0.05,0.01,0.01), lighting(n,l));
        col += vec3(0.,0.02,0.05)*(1.-pow(clamp(dot(n,-rd),0.,1.),.2));
        vec3 h = normalize(l-rd);
        col += pow(max(dot(h,n) ,0.), 25.)*vec3(0.2,0.25,0.25);
        col *= vec3(1.-shad);
    }
    // Output to screen
    fragColor = vec4(pow(col,vec3(0.4545)),1.0);
}

vec3 cicle(vec2 pt, vec2 center, float r)
{
    vec3 ret = vec3(0.0, 0.0, 0.0);
	float dis = distance(pt, center);
    if (dis < r) {
    	ret = vec3(1.0, 1.0, 1.0);
    }
    return ret;
}

vec3 rect(vec2 pt, vec2 center, float r)
{
    vec3 ret = vec3(0.0, 0.0, 0.0);
	float disx = abs(pt.x - center.x);
    float disy = abs(pt.y - center.y);
    if (disx * 2.0f < r && disy * 2.0f < r) {
       ret = vec3(1.0, 1.0, 0.0);   
    }

    return ret;       

}

vec3 drawSin(vec2 pt)
{
    vec3 ret = vec3(0.0, 0.0, 0.0);
    float si = sin((pt.x + iTime / 10.0f) * 3.14f * 4.0f) * 0.5f;
    if (pt.y - 0.5 < si) {
         ret = vec3(1.0, 1.0, 1.0);
    }
    return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 tcol = texture(iChannel0, uv).rgb;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 center = vec2(0.5, 0.5);
    vec3 c = cicle(uv, center, 0.2f);
    vec3 r = rect(uv, center, 0.3f); 
    vec3 s = drawSin(uv);
    vec3 ns = vec3(1.0, 1.0, 1.0) - s;

    vec3 col = c * 0.4 + r *0.6;


    // Output to screen
    fragColor = vec4(s * tcol + ns * vec3(1.0, 1.0, 0.0) ,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	// normalize coordinates and move origin to center
	vec2 st = (fragCoord.xy / iResolution.xy) - vec2(0.5);

	// animate global scale: zoom in and out
	st *= cos(iTime * 0.5);

	// animate point distance from center
	vec2 offset = (0.03 + pow(sin(iTime * 0.5), 4.5)) * vec2(0.5, 0.0);

	// calculate gradient colors for two diametrically opposite points
	float c1 = distance(st, offset); float c2 = 0.95 - distance(st, -offset);

	// set output color as combination of both colors
	fragColor = vec4(vec3(pow(c1 + c2, 20.0)), 1.0);
}

#define DOTS 14
#define DOTOFFSET 0.04
#define SPEED 0.2
#define POWER 3.
#define COLTOP vec3(.3,0.6,1.)
#define COLBTM vec3(1.,.4,.3)

#define lerp mix
#define pi 3.1415

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x*=iResolution.x/iResolution.y;

    float d = 9999.;
    vec3 col;
    for (int i=0;i<DOTS;i++) {
    	float t = pow(fract(iTime*SPEED+(float(i)*DOTOFFSET)),POWER);
    	float s = pi*2.;
    	vec2 o = vec2(sin(t*s), cos(t*s))*.2;
        float dist = length(uv+o)-.01;
        if (dist<d){
        	col=lerp(COLTOP,COLBTM, cos(t*s)*.5+.5);
        }
    	d = smin(d,dist, 0.04);


    }



    col = lerp(col, vec3(1.,1.,.9), smoothstep(0., 0.005, d));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float sdBox( vec3 p, vec3 b , float r)
{
  r = min(r,0.15);
  vec3 q = abs(p) - (b-r);
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}
float sdSphere(vec3 p, float r) {
 	return length(p) - r;
}
float sdCone( in vec3 p, in vec2 c , in vec3 size)
{
    // c is the sin/cos of the angle
    float q = length(p.xy);
    float dotProduct = dot(c,vec2(q,p.z));
    float d = max(dotProduct,sdBox(p,size,0.0));
    return d;
}
float sdCappedCylinder( vec3 p, float h, float r ) //This, the cone and the box are -s-t-o-l-e-n- borrowed from Inigo's SDF page
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
vec2 getSnowman(in vec3 pos) {
	float lowerBody = sdSphere((pos), 0.2);
	float middleBody  = sdSphere(pos-vec3(0,0.2,0),0.16);
    float upperBody = sdSphere(pos-vec3(0,0.36,0), 0.13);

    float coneAngle = 1.4;
    float nose = sdCone(pos-vec3(0,0.36,0.2),vec2(sin(coneAngle),cos(coneAngle)),vec3(0.1));

    float rightEye = sdSphere(pos-vec3(0.05,.4,0.12),0.01);
    float leftEye = sdSphere(pos-vec3(-0.05,.4,0.12),0.01);
    float lowerHat = sdCappedCylinder(pos-vec3(0,0.47,0),0.13,0.01);
    float upperHat = sdCappedCylinder(pos-vec3(0,0.47,0),0.1,0.1);



    float d = min(lowerBody,min(middleBody,min(upperBody,min(nose,min(rightEye,min(leftEye,min(lowerHat,upperHat)))))));
    float identifier = 0.0;
    if(d == nose) identifier = 5.0;
    if(d == leftEye || d == rightEye || d == lowerHat || d == upperHat) identifier = 6.0;
    return vec2(d, identifier);
}
vec2 map(in vec3 pos) {
    float identifier = 0.0;
    float animationSpeed = 1.5;
	vec3 animation = vec3(sin(iTime*5.0*animationSpeed)/20.0*max(pos.y,0.0),sin(iTime*10.0*animationSpeed)/20.0*max(pos.y,0.0),0.0);
    float ground = pos.y + 0.2 - length(sin(pos/1.0))/20.0-length(sin(pos/11.0))/1.0;
	vec2 snowman = getSnowman(pos-animation);
    float d = min(ground,snowman.x);
    //OH LAWD THIS IS A MESS
    //are arrays a thing in glsl
    //if so, it would make things a lot easier


	if(d == ground) identifier = 4.0;
    if(d == snowman.x) identifier = snowman.y;
    return vec2(d,identifier);
}
vec3 calcNormal( in vec3 pos) {
    vec2 e = vec2(0.0001,0.0);
 	return normalize( vec3(map(pos+e.xyy).x-map(pos-e.xyy).x,
                           map(pos+e.yxy).x-map(pos-e.yxy).x,
                           map(pos+e.yyx).x-map(pos-e.yyx).x));   
}

vec2 castRay(in vec3 ro, in vec3 rd, in float renderDistance) {
 	vec2 t = vec2(0.0);
    for(int i = 0; i < 100; i++) {
     	vec3 pos = ro + t.x*rd;

        vec2 h = map(pos);
        if(h.x < 0.001) break;

        t.x += h.x;
        t.y = h.y;
        if(t.x > renderDistance) break;
    }
    if(t.x > renderDistance) t.x = -1.0;
    return t;
}
