void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float an = iMouse.x / iResolution.x*6.245;
    float en = -iMouse.y / iResolution.y + 3.1415 / 5.0;
    //float an = iTime;
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec3 ro = vec3(sin(an),sin(en),-cos(an));
    vec3 ta = vec3(0,0.2,0);

    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww,vec3(0,1,0)));
    vec3 vv = normalize(cross(uu,ww));

    vec3 rd = normalize(p.x * uu + p.y * vv + 1.5 * ww);

    float renderDistance = 20.0;
	vec2 t = vec2(0.0);
    t = castRay(ro,rd,renderDistance);
    vec3 sunVector = normalize(vec3(0.8,0.4,0.2));
    vec3 col = pow(vec3(0.6,0.8,0.9), vec3(rd.y*7.0)) * ((dot(sunVector,rd)*normalize(vec3(7.0,3.0,0.0))+ 1.0)*normalize(vec3(7,4,3)));




    col += vec3(pow(max(dot(sunVector,rd),0.0), 100.0)) * normalize(vec3(7,5.5,4));



    if(t.x > 0.0) {
        vec3 pos = ro + t.x*rd;
        vec3 norm = calcNormal(pos);
        vec3 mat = vec3(0.2,0.3,0.4);
        if(t.y == 5.0) mat = vec3(0.5,0.3,0.0);
		if(t.y == 6.0) mat = vec3(0.01);
        if(t.y == 4.0) mat -= length(sin(pos/2.0))/20.0;
        vec3 skyVector = normalize(vec3(0.0,1.0,0.0));

        float sunDiffuse = clamp(dot(norm,sunVector),0.0,1.0);
        float skyDiffuse = clamp(0.5 + 0.5*dot(norm,skyVector),0.0,1.0);
        float bounceDiffuse = clamp(0.5 + 0.5*dot(norm,-skyVector),0.0,1.0);

        float sunShadow = step(castRay(pos + norm * 0.001,sunVector, renderDistance).x,0.0);



    	col = mat * vec3(15,7,3) * sunDiffuse * sunShadow;
        col += mat * vec3(0.6,0.8,0.9) * skyDiffuse;
        col += mat * vec3(0.7,0.3,0.2) * bounceDiffuse;
    }
	col = pow(col, vec3(0.4545));
    // Output to screen
    fragColor = vec4(col,1.0);
}

