float GetDist(vec3 p){
    vec4 sphere = vec4(0,1,6,1);
    float dSphere = length(p-sphere.xyz)-sphere.w;
    float dPlane = p.y;

  vec3 c;
    c=p;

    vec4 color = vec4(1., 1., 1., 1.);

  c.z = repeat(c.z, 2.);

  c.x = abs(c.x)-0.5; //Mirror

  vec3 boxSize = vec3(.5, 1., .5);
  vec3 boxOffset = vec3(1., -1.2, 0.);
  float b1 = fBox(c+ boxOffset, vec3(3.0, .2, .2)); //c- vec3(x, y, z) how much to move the object
  float b2 = fBox(c- boxOffset, vec3(3.0, .2, .2)); //c- vec3(x, y, z) how much to move the object
  float b3 = fBox(c- vec3(.8, 0., 0.), vec3(.07, 1., .05)); //left box on column
  float b4 = fBox(c- vec3(1., 0., -0.17), vec3(.07, 1., .05)); //front box on column
  float b5 = fBox(c- vec3(1., 0., 0.17), vec3(.07, 1., .05)); //back box on column
  float b6 = fBox(c- vec3(-.8, 0., 0.), vec3(.07, 1., .05)); //right box on column
  float b7 = fBox(c- vec3(.0, -1.5, 0.), vec3(.5, .2, 2.));//small box on bottom

  float col1 = fCylinder(c- vec3(1., 0., 0.), .2, .9);
  float col2 = fCylinder(c- vec3(1.9, 0., 0.), .2, .9);






    //float d = min(dSphere, dPlane);
    float d;

    d=min(dPlane, b1);
    d=min(d, b2);
    d=min(d, b3);
    d=min(d, b4);
    d=min(d, b5);
    d=min(d, b6);
    d=min(d, b7);

    d=min(d, col1);
    d=min(d, col2);

    return d;
}

