float sphere(vec3 p, float r)
{
	return length(p)-r;
}

float shrub(vec3 p)
{  
    return length(vec3(mod(p.xz,1.)-.5,p.y+12.).xyz)-.5;
}

float cube(vec3 p)
{
    float h = length(p)-14.;
    return min(max(-h,0.),max(abs(p.y-11.99)-.01,0.));    
}

float ray_box(vec3 ro, vec3 rd, float mint, float maxt)
{
	float t = 0.;
    for (int i = 0; (i<150 && t<maxt); i++) {
    	vec3 p = ro + rd * t;
        float h = cube(p);
        t += h;
        if (h*1e5<mint+t) return t;
    }
    return -1.;
}

float ray_veg(vec3 ro, vec3 rd, float mint, float maxt) {
	float t = 0.;
    for (int i = 0; (i<150 && t<maxt); i++) {
        float h = shrub(ro + rd * t);
        t += h;
        if (h*1e5<mint+t) return t;
    }
    return -1.;
}

vec2 ray(vec3 ro, vec3 rd) {
    float t = 0.;
    float grid = 24.;
    vec3 pr_stp = step(0., rd)*grid;
    float dec = .3;
    while(t<300.) {
        vec3  p   = ro + rd*t,
              ip  = floor((ro + rd*t)/grid)*grid,
              dt  = (pr_stp + ip-p) / rd;
    	float dlt = max(min(min(dt.x,dt.y),dt.z),1e-3),
              pr_hash = hash(uvec3(ip/grid+1e4)).x;
        if (pr_hash<dec && ip.y<0.) {
			float t2 = ray_box(p-ip-grid*.5,rd,t,dlt);  
            if (t2!=-1.) {
				return vec2(t+t2,2); 
            }
        } else if (ip.y<1.) {
			pr_hash = hash(uvec3((ip-vec3(0,1,0))/grid+1e4)).x;
			if (pr_hash<dec) {
                float t2 = ray_veg(p-ip-grid*.5,rd,t,dlt);
                if (t2!=-1.) {
					return vec2(t+t2,3); 
                }
            }

        }
    	t += dlt;
    }
    return vec2(0);
}


void mainImage( out vec4 O, in vec2 U ) 
{
    vec2 uv = (U-iResolution.xy*.5)/iResolution.y*2.;

    vec3 viewPos = vec3(0,36,iTime*20.),
         rd = normalize(vec3(uv,-1.6)).xzy;

    vec2 hit = ray(viewPos,rd);
    vec3 p = viewPos + rd*hit.x;

    vec3 col = vec3(0.);
    if (hit.y>0.) {  
        float grid = (3.-hit.y)*24.;    
        vec3 ip = floor(p/grid)*grid;
        vec3 fp = p-ip-grid*.5;
        col = hash(uvec3(ip+1e4))*0.2+vec3(0,.3,0.);

        col = 1.- exp(-col*exp((36.-hit.x)*.06));
        col = pow(col.rgb, vec3(1./2.2));
        vec3 noise = pow(texelFetch(iChannel0, ivec2(U)%1024, 0).rgb,vec3(1./2.2));
        noise = (noise*2.-1.)*0.499*2.;
        col += noise/256.;
    }
    O = col.xyzx;
}

#define CAM_ZOOM  2.
#define MAX_STEPS 200
#define MAX_DIST  1000.
#define SURF_DIST .03
#define PI 3.1415926
#define CAM_TO_CENTER false
#define LIGHT true
#define BALL_SPACING 5.


vec3 VecMod(vec3 v, float m) {
  vec3 ret = v;
  for(int i = 0; i < 3; i++){
    float rem = v[i];
    if (rem > 0.){
      while (rem > m) {
        rem -= m;
      }
      ret[i] = rem-m/2.;
    } else {
      while (rem < -m) {
        rem += m;
      }
      ret[i] = rem+m/2.;
    }
  }

  return ret;
}


float Distance(vec3 rn){
  return length(VecMod(rn, BALL_SPACING)) - .5;
}

float Light(vec3 rn){
  	float dist = Distance(rn);
    if (dist < SURF_DIST) {
        vec3 relpos = VecMod(rn, BALL_SPACING);
        vec3 normal = normalize(relpos);
        return dot(normal, normalize(vec3(.5, 1, -.1)));
    } else {
    	return 2.;
    }
}

float March(vec3 ro, vec3 rd) {
    float ditr = 0.;
    for(int i = 0; i < MAX_STEPS; i++){
    float dist = Distance(ro + rd*ditr);
        ditr += dist;
        if(dist < SURF_DIST || ditr > MAX_DIST) break;
    }
    return ditr;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xx - vec2(.5, .5*iResolution.y/iResolution.x);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.yxx+vec3(1));

    vec3 ro = vec3(100.*sin(iTime/10.),0,100.*cos(iTime/10.));
    //vec3 ro = vec3(0.,1.5,-10.);
    vec3 camd = normalize(ro);
    float xzCAngle;
    if (CAM_TO_CENTER){
    	xzCAngle= atan(camd.x/camd.z);
        if (ro.z>0.) {
            xzCAngle += PI;
        }
    } else {
    	ro = vec3(0, 0, iTime/.5);
        xzCAngle = 0.;
    }
    vec3 rd = normalize(vec3(uv.x, uv.y, 1./CAM_ZOOM));
    float xzAngle = atan(rd.x/rd.z) + xzCAngle;
    rd.x = sin(xzAngle);
    rd.z = cos(xzAngle);

    vec3 col;
    float dist = March(ro,rd);
    if(LIGHT){
    	float light = Light(dist*rd + ro);
        if (light <= 1.) {
        	col = vec3(light);
        } else {
        	col = vec3(dist/MAX_DIST);
        }
    } else {
    	col = vec3(dist/MAX_DIST);
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

// SHADERTOBER 10 Pattern
