#define VOLUME 0.001
#define PI 3.14159



////////////////////////////////////////////////////////////////////////////////
float opU(float s1, float s2) { return min(s1, s2);}

vec2 opU2(vec2 d1, vec2 d2) { return (d1.x < d2.x) ? d1 : d2; }

float sat(float a) {return clamp(a, .0, 1.); }


vec2 sdLine( vec3 p, vec3 a, vec3 b )
{
    vec3 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return vec2( length( pa - ba*h ), h );
}



float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float sdChronoLine(vec3 p, float o, float s, float le)
{
    float time = iTime + o;
    vec3 pa = vec3(.0);
    vec3 pb = vec3(sin(time), cos(time), 0.0) * le;
    vec2 b = sdLine( p, pa, pb );

    float tr = s;
    float l = b.x - tr;

    return l;
}

// -------------------------------------------------------------------------

vec2 map(vec3 pos)
{
    vec2 t = vec2(0.0, -1.0);

    // ground
    t.x = pos.y;

    vec3 p = pos + vec3(0.0, -1.5, 0.0);

    vec2 l = vec2(1.0, 0.0);
    float anim = abs(sin(iTime));
    for(int i=0; i<16 ; i++)
    {
        l = opU2(l, vec2(sdChronoLine(p, float(i)*PI/8.0, anim, 1.0), float(i)*.1));
    }


    t = opU2(t, l);

    return t;
}

float GetShadow (vec3 pos, vec3 at, float k) {
    vec3 dir = normalize(at - pos);
    float maxt = length(at - pos);
    float f = 01.;
    float t = VOLUME*50.;
    for (float i = 0.; i <= 1.; i += 1./15.) {
        vec2 dist = map(pos + dir * t);
        if (dist.x < VOLUME) return 0.;
        f = min(f, k * dist.x / t);
        t += dist.x;
        if (t >= maxt) break;
    }
    return f;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<64 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(t, ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

vec3 GetNormal (vec3 p) 
{ 
    vec2 e = vec2(0.01, 0.0); 
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
        )); 
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = ro+rd*t.x;
    vec3 col = vec3(0.0);
    float depth = clamp(pos.z, 0.0, 1.0);
    depth = 1.0 - sat(pos.z*.1);

    if(t.x == -1.0)
    {        
        col = vec3(rd.y);

    }
    else
    {
        float glp = clamp(length(pos)*.1, 0.0, 1.0);
        vec3 N = GetNormal(ro+rd*t.x);
        vec3 L = vec3(0.0, 1.0, 1.0);
        float light = dot(N,L);
        float shade = GetShadow(pos, L, 4.);

        if(t.y == -1.0) 
        {
            col = vec3(.3+pos.z*.1);
            col *= shade;
        }
        else
        {
            col = vec3(1.0, 0.2+t.y, 1.0-t.y);
            col *= vec3(light)*.7;
        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float time = iTime*.25;

    vec3 cp = vec3(0.0, 1.0, 5.0);
    vec3 ct = vec3(0.0, 1.5, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = vec3(length(uv));
    vec2 screenUV = fragCoord.xy / iResolution.xy;

    col = Render(cp, vd, uv);
    col.z += screenUV.y*.2;
    col -= random(screenUV) * .1;
    col *= 1.0-length(uv)*.8;

    fragColor = vec4(col,1.0);
}

#define CS(a)       vec2( cos(a), sin(a) )           // complex operators
#define Arg(z)      atan( (z).y,(z).x )
#define expZ(z)   ( length(z) * CS(z) )
#define logZ(z)     vec2( length(z), Arg(z) )
#define mulZ(a,b) ( mat2(a, -(a).y,(a).x) * (b) )

vec2 R;

#define S(d,r,pix) smoothstep( .75, -.75, (d)/(pix)-(r))   // antialiased draw. r >= 1.
#define F(p,f)     S( abs(f-3.*(p).y-2.), 1., fwidth(f-3.*(p).y) ) // draw curve

// segment with disc ends: seamless distance to segment
float line(vec2 p, vec2 a,vec2 b, float pix, float d) {  // draw line
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);      // proj coord on line
    if ( d >= 0. && mod(h*10.-d, 3.) < 2.) return 0.;    // dashs
    d = length(p - b * h);                               // dist to segment 
    return S( d ,1., 2.*pix/R.y );                       // draw line
}

#define blend0(a0,a1) ( a0 + a1 ) / 2.
#define blend1(a0,a1) ( mod(a0,TAU) + mod(a1,TAU) ) / 2.
#define blend2(a0,a1)   Arg( CS(a0) + CS(a1) )

void mainImage( out vec4 O, vec2 u )
{
          R = iResolution.xy;      
    vec2  U = ( 2.*u - R ) / R.y,
         C0 = vec2(0);

    float r = .8,
         TAU = 6.2831853,
          w0 = -1., w1 = 2.713,
          a0 = w0* iTime, a1 = w1* iTime,
          x0 = 2.*w0*U.x, x1 = 2.*w1*U.x;

#define draw(a,e,d) line(U, C0, r* CS(a), e, d)
    O.b  = draw( blend0(a0,a1), 1., 0. );//  +  F( U, sin( blend0(x0,x1)) );
    O.g  = draw( blend1(a0,a1), 1., 1. );//  +  F( U, sin( blend1(x0,x1)) );
    O.r  = draw( blend2(a0,a1), 1., 2. );//  +  F( U, sin( blend2(x0,x1)) );
    O   += draw(        a0    , 2.,-1. );//  +  F( U, sin( x0 ) );
    O   += draw(           a1 , 2.,-1. );//  +  F( U, sin( x1 ) )*.5;
    O   += draw(       0.     , 1.,-1. ) * .3;
}

vec2 pos = vec2(0,0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = pow(0.999,float(iFrame));
    vec2 normCoord = vec2((fragCoord.x/iResolution.x*4.-2.)*zoom + pos.x, (fragCoord.y/iResolution.y*2.25-1.125)*zoom + pos.y);
    vec2 z = normCoord;
    float zsize;
    int i;
    for(i = 0; i < 150000; i++){
        zsize = dot(z,z);
        z = mat2( z, -z.y,z.x) * z + iMouse.xy / iResolution.xy*4.-2.;
        if(zsize > 4.){
            break;
        }
    }
    if(i == 150000){
    	fragColor = vec4(0,0,0, 0);
    }
    else{

        fragColor = vec4(float(i%137)/137.,float(i%298)/298.,1.-float(i%255)/64.,0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    uv.y += sin(uv.x*3.0)/((uv.x+4.0+sin(iTime))/0.1);

    uv.x += round(uv.y*49.3) * (5.347*round(uv.y*49.3+iTime+uv.x*(sin(iTime/10.0)*10.0)*sin(uv.x*3.14)));

    fragColor = vec4(col,1.0)*mod(uv.x-iTime/5.0,0.4);
}

// also see 
// voronoi  version, https://www.shadertoy.com/view/tdyGzK
// simplex version, https://www.shadertoy.com/view/WsG3zd
// box version, https://www.shadertoy.com/view/tsK3Rd

#define RAY_MAX_STEPS 100
#define RAY_MAX_DISTANCE 30.0

#define PI	3.1415926535897932384626433832795


// Flags to use tecture lookups instead of noise functions, should be faster but introduces repetiton.
//#define noiseTecture3d
#define noiseTecture2d

struct camera {
   vec3 origin, forward, right, up;
   float zoom; // Distance from screen
};

struct ray {
   vec3 origin, direction;
};

mat3x3 eulerToMatrix(float roll, float pitch, float yaw){
  float
      cPitch = cos(pitch),
      sPitch = sin(pitch),
      cYaw = cos(yaw),
      sYaw = sin(yaw),
      cRoll = cos(roll),
      sRoll = sin(roll);

	mat3x3 mPitch = mat3x3(	1.0,	0.0,	0.0,
							0.0,	cPitch,	sPitch,
							0.0,	-sPitch,	cPitch);

    mat3x3 mYaw = mat3x3(	cYaw,	0.0,	sYaw,
							0.0,	1.0,	0.0,
							-sYaw,	0.0,	cYaw);

    mat3x3 mRoll = mat3x3(	cRoll,	-sRoll,	0.0,
							sRoll,	cRoll,	0.0,
							0.0,	0.0,	1.0);

    return mPitch * mYaw * mRoll;
}

camera getCamera(vec3 origin, float roll, float pitch, float yaw, float zoom) {
	camera camera;
	camera.origin = origin;
    mat3x3 rotationMatrix = eulerToMatrix( roll, pitch, yaw);

    camera.forward = vec3(0.0,0.0,1.0) * rotationMatrix;
	camera.right = vec3(1.0,0.0,0.0) * rotationMatrix;
	camera.up = vec3(0.0,1.0,0.0) * rotationMatrix;

	camera.zoom = zoom;
	return camera;
}

ray getRay(vec2 uv, camera camera) {
	ray ray;
    ray.origin = camera.origin;
    vec3 center = ray.origin + camera.forward * camera.zoom;
    vec3 intersection = center + (uv.x * camera.right) + ( uv.y * camera.up );
    ray.direction = normalize(intersection - ray.origin);
	return ray;   
}

float random(in float st) {
#if defined(noiseTecture3d)
	return float(texelFetch( iChannel0, ivec3(vec3(139.181,151.199,229.211)*st)&31,0));
#elif defined(noiseTecture2d)
	return float(texelFetch( iChannel1, ivec2(vec2(139.181,151.199)*st)&255,0));
# else
	return fract(sin(st*139.181))* 437.5453123);
#endif
}

float random3(in vec3 st) {
#if defined(noiseTecture3d)
	return float(texelFetch( iChannel0, ivec3(st)&31,0));
#elif defined(noiseTecture2d)
	return float(texelFetch( iChannel1, ivec2(st.xy*st.z)&255,0));
# else
	return fract(sin(dot(st,vec3(12.9898,79.233,96.9723)))* 437.5453123);
#endif
}

float boxNoise(vec3 samplePoint) {
    vec3 pointI =floor(samplePoint);
    vec3 pointF = fract(samplePoint);


    float bbl = random3(pointI + vec3(0.0,0.0,0.0) );
    float bbr = random3(pointI + vec3(1.0,0.0,0.0) );
    float btl = random3(pointI + vec3(0.0,1.0,0.0) );
    float btr = random3(pointI + vec3(1.0,1.0,0.0) );

    float fbl = random3(pointI + vec3(0.0,0.0,1.0) );
    float fbr = random3(pointI + vec3(1.0,0.0,1.0) );
    float ftl = random3(pointI + vec3(0.0,1.0,1.0) );
    float ftr = random3(pointI + vec3(1.0,1.0,1.0) );

    vec3 u =pointF;

    float bb = mix(bbl,bbr,u.x);
    float bt = mix(btl,btr,u.x);

    float b = mix(bb,bt,u.y);

    float fb = mix(fbl,fbr,u.x);
    float ft = mix(ftl,ftr,u.x);

    float f = mix(fb,ft,u.y);

    return mix(b,f,u.z);
}

vec3 RayMarch(ray ray, float maxDistance, int maxSteps)
{
    float maxStepsf = float(maxSteps);
    float stepSize = maxDistance/maxStepsf;
    vec3 total = vec3(0.0);
    float currentDistance = 1.0;

    for(float i=0.0; i<maxStepsf; i++) {
        vec3 project = (ray.direction* currentDistance);
        //Add some offsets to hide the aligned features in the box noise.
        project.x +=sin(project.y*0.5);
        project.y +=sin(project.z*0.2);
        project.z +=sin(project.x*0.3);
		vec3 currentPoint = ray.origin + project  ;

        float stepf = ((maxStepsf - i)/maxStepsf);
        float s = 1.0-(abs(boxNoise(currentPoint)-0.5)*2.0);
        s=smoothstep(0.7,1.1, s);

        s *= stepf;
        vec3 light = sin((currentPoint+vec3(iTime/10.0,iTime/13.0,iTime/15.0))/4.0)*0.5+1.0; //Yes we blow out the colour a little.
        total += light*s;
        currentDistance += stepSize;
    }
    return (total*(3.0/maxStepsf));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized Pixel coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    // Normalized Mouse coordinates (from -0.5 to +0.5, center at 0,0)
    vec2 mouse = ((iMouse.xy-0.5*iResolution.xy)/iResolution.y);// - vec2(0.5,0.5)*(iMouse.w*0.1);
    //Snap to center on startup.
    if(iMouse.x <= 1.0 && iMouse.y <= 1.0) {
        mouse = vec2(0.0,0.0);
    }

	camera camera = getCamera(vec3(0.0,0.0,iTime), iTime*0.1, mouse.y*PI, mouse.x*2.0*PI,  0.5);
    ray ray = getRay(uv, camera);

    vec3 cloudColour = RayMarch(ray, RAY_MAX_DISTANCE, RAY_MAX_STEPS);

    //Stars
    float starBase = random3(floor(ray.direction*iResolution.x*0.5));
    float stars = starBase>0.05?0.0:starBase*20.0;
    stars *= stars * stars * stars;
    float cloudColourLength = length(cloudColour);
   	stars *= clamp(pow(clamp(1.0-smoothstep(0.1,1.0,length(cloudColourLength)),0.01,1.0),1.0),0.0,1.0)*0.6;
    vec3 starColour = vec3(stars);

    //Sun
    vec2 facing = vec2(iTime*0.01+1.2,iTime*0.03+0.3);
    vec3 sunDir = normalize(vec3(
        sin(facing.x),
    	sin(facing.y),
    	cos(facing.x)
     ));

    float sunBright = clamp(dot(sunDir,ray.direction),0.0,1.0);
    sunBright = pow(smoothstep(0.8,1.0,sunBright),2.0)* 10.0; //blow it right out
    sunBright*=(clamp(1.0-cloudColourLength*0.5,0.1,1.0)); //obscure by nebula
    vec3 suncolour = vec3(0.9, 0.7, 0.6); //nice orange/yellow

    //Lightning
    float strikeBright = pow(clamp(sin(ray.direction.x+iTime*10.0)*sin(ray.direction.y+iTime*15.0)*cos(ray.direction.z+iTime*20.0),0.0,1.0),15.0+sin(iTime*2.0)*10.0);
    strikeBright *= 8.0*clamp(1.0-cloudColourLength,0.1,1.0)*smoothstep(0.7,1.0,random(floor(iTime*5.0)));
    vec3 strikeColour = vec3(0.7,0.9,1.0);

    //
    vec3 colour = max(starColour,suncolour*sunBright*(clamp(1.0-cloudColourLength*0.5,0.1,1.0)) + cloudColour); 
	colour+=strikeBright*strikeColour;

    //modualte expose, so the screen darkens when looking at the sun,
    //rewrite the UV to remove aspect ratio but still in 0 > 1 range
    uv = fragCoord.xy / iResolution.xy;
    uv *=  1.0 - uv; 
    float exposure = smoothstep(-0.7,1.0,( 1.0-pow(clamp(dot(camera.forward, sunDir),0.0,1.0),4.0)));
    colour *= exposure;

    //Harsh gamma gives it a nicer look.
    colour = pow(colour, vec3(1.0/0.8));
    //flatten the colours
    //colour = 1.35*colour/(1.0+colour);
    colour = clamp(colour,0.0,1.0);
  	colour = colour*colour*(3.0-2.0*colour);

    float vignette = clamp(pow( uv.x*uv.y*5.0,0.2)+0.3,0.0,1.0);
    colour*=vignette;
    fragColor = vec4(colour,1.0);
}

#define MAX_STEPS 100
#define SURFACE_DIST .06
#define MAX_DIST 100.
#define CAMERA_POS vec3(0, 1, 0)

#define SPHERE_POS vec4(0, 1.2, 6, 1)
#define MATERIAL vec3(.12, .5, .99)

#define LIGHT_COLOR vec3(1, 0, 0)
#define LIGHT_RAD .01
#define LIGHT_POS vec3(0, 3, 5)
#define LIGHT_SPEED 1.5

vec4 GetLightPos() {
    vec3 lightPos = LIGHT_POS;
    float speed = iTime * LIGHT_SPEED;
    lightPos.xz += vec2(sin(speed), cos(speed))*3.;
    lightPos.y += sin(speed/4.)*2.;
    return vec4(lightPos, LIGHT_RAD);
}

vec3 GetLightColor() {
    return 0.5 + 0.5*cos(iTime+LIGHT_COLOR.xyz+vec3(0,2,4));
}

float GetDist (vec3 p, bool shadow) {
    vec4 sphere = SPHERE_POS;
    sphere.y += sin(iTime)*.5 + .5;
    vec4 light = GetLightPos();
    float dS = length(p - sphere.xyz)-sphere.w;
    float dL = length(p - light.xyz)-light.w;
    float dP = p.y;
    float d = min(dS, shadow ? dP : min(dP, dL));
    return d;
}

float RayMarch (vec3 ro, vec3 rd, bool shadow) {
    float dO = 0.;
    for (int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro+dO*rd;
        float ds = GetDist(p, shadow);
        dO += ds;
        if (ds < SURFACE_DIST || dO > MAX_DIST) break;
    }
    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p, false);
    vec2 e = vec2(SURFACE_DIST, 0.);
    vec3 n = d - vec3(
        GetDist(p - e.xyy, false),
        GetDist(p - e.yxy, false),
        GetDist(p - e.yyx, false));
    return normalize(n);
}

vec3 GetLight(vec3 o, vec3 p) {
    vec3 lightPos = vec3(GetLightPos().xyz);
    vec3 l = normalize(lightPos - p);
    vec3 v = normalize(p - o);
    vec3 n = GetNormal(p);
    vec3 r = reflect(l, n);

    float dif = clamp(dot(n, l), 0., 1.);
    float spec = pow(max(dot(v, r), 0.), 32.);
    vec3 wVal = vec3(1., dif, spec) * MATERIAL;
    float s = wVal.y + wVal.z;

    float d = RayMarch(p+n*SURFACE_DIST*2., l, true);
    if (d < length(lightPos-p)) {
        s *= .1;
    }
    vec3 lCol = GetLightColor();

    return (wVal.x + s) * lCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -.5 to .5)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    vec3 ro = CAMERA_POS;
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));
    float d = RayMarch(ro, rd, false);

    if (d < MAX_DIST) {
    	vec3 p = ro + d * rd;
        vec3 l = GetLightPos().xyz;
        if (length(p - l) < 2.*SURFACE_DIST) {
            col = GetLightColor();
        }
        else {
    		col = GetLight(ro, p);
        }
    }
    else {
        col = vec3(0.);
    }

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.y;
    float xy_ratio = iResolution.x / iResolution.y;

    vec2 center = vec2(0.5 * xy_ratio, 0.5);
    float r = 0.6;
    float omega = 5.;

    vec3 col = vec3(r * (cos(omega * iTime) + 2.) / 2. - length(uv - center), 0., 0.);


    // Output to screen
    fragColor = vec4(col, 1.0);
}

/*


	Framed Geometric Pattern
	------------------------

	I made this a while ago. It's not that exciting, but I thought I'd put
	it up anyway. :)

    The picture-framed image is a bit of a computer graphics cliche, but an 
	effective way to display an otherwise simple geometric pattern. Like a 
	lot of grid-based patterns out there, this one is Truchet based. The 
	picture frame itself was applied using standard distance field and 
	layering techniques.

	The background image is just some offset circles and is based on an
	underlying herringbone grid pattern. The timber is just an application of 
	an old layered noise technique, which I'd imagine was first oulined by 
	Ken Perlin, back in the day.


	Other Examples:

    // BigWIngs's popular Youtube channel. It's always informative seeing how 
	// others approach various graphics topics.
    Shader Coding: Truchet Tiling Explained! -  The Art of Code
	https://www.youtube.com/watch?v=2R7h76GoIJM


	// Flipped Truchet pattern basics. Fabrice does it in a few of his 
	// examples too, if anyone wants to track them down.
    TruchetFlip - JT
    https://www.shadertoy.com/view/4st3R7


*/

// Originally, this was a plain pattern I put together, just for something to do.
// I thought it looked a little too plain, so got a bit carried away. Anyway, if
// you're more of a "less is more" person, like me, here's the original.
//#define PATTERN_ONLY

// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


// IQ's vec2 to float hash.
float hash21(vec2 p){  return fract(sin(dot(p, vec2(27.619, 57.583)))*43758.5453); }

// IQ's animated vec2 to float hash.
float hash21A(vec2 p){  
    float x = fract(sin(dot(p, vec2(27.619, 57.583)))*43758.5453); 
    return sin(x*6.2831 + iTime/3.)*.5 + .5;
}

// IQ's rounded box formula -- slightly modified.
float sBoxS(in vec2 p, in vec2 b, in float rf){

  vec2 d = abs(p) - b + rf;
  return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - rf;

}

// Textureless 3D Value Noise:
//
// This is a rewrite of IQ's original. It's self contained, which makes it much
// easier to copy and paste. I've also tried my best to minimize the amount of 
// operations to lessen the work the GPU has to do, but I think there's room for
// improvement. I have no idea whether it's faster or not. It could be slower,
// for all I know, but it doesn't really matter, because in its current state, 
// it's still no match for IQ's texture-based, smooth 3D value noise.
//
// By the way, a few people have managed to reduce the original down to this state, 
// but I haven't come across any who have taken it further. If you know of any, I'd
// love to hear about it.
//
// I've tried to come up with some clever way to improve the randomization line
// (h = mix(fract...), but so far, nothing's come to mind.

float n3D(vec3 p){

    // Just some random figures, analogous to stride. You can change this, if you want.
	const vec3 s = vec3(27, 113, 57);

	vec3 ip = floor(p); // Unique unit cell ID.

    // Setting up the stride vector for randomization and interpolation, kind of. 
    // All kinds of shortcuts are taken here. Refer to IQ's original formula.
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);

	p -= ip; // Cell's fractional component.

    // A bit of cubic smoothing, to give the noise that rounded look.
    p = p*p*(3. - 2.*p);

    // Smoother version of the above. Weirdly, the extra calculations can sometimes
    // create a surface that's easier to hone in on, and can actually speed things up.
    // Having said that, I'm sticking with the simpler version above.
	//p = p*p*p*(p*(p * 6. - 15.) + 10.);

    // Even smoother, but this would have to be slower, surely?
	//vec3 p3 = p*p*p; p = ( 7. + ( p3 - 7. ) * p ) * p3;	

    // Cosinusoidal smoothing. OK, but I prefer other methods.
    //p = .5 - .5*cos(p*3.14159);

    // Standard 3D noise stuff. Retrieving 8 random scalar values for each cube corner,
    // then interpolating along X. There are countless ways to randomize, but this is
    // the way most are familar with: fract(sin(x)*largeNumber).
    h = mix(fract(sin(h)*43758.5453), fract(sin(h + s.x)*43758.5453), p.x);

    // Interpolating along Y.
    h.xy = mix(h.xz, h.yw, p.y);

    // Interpolating along Z, and returning the 3D noise value.
    return mix(h.x, h.y, p.z); // Range: [0, 1].

}

// vec2 to vec2 hash.
vec2 hash22(vec2 p) { 

    // Faster, but doesn't disperse things quite as nicely. However, when framerate
    // is an issue, and it often is, this is a good one to use. Basically, it's a tweaked 
    // amalgamation I put together, based on a couple of other random algorithms I've 
    // seen around... so use it with caution, because I make a tonne of mistakes. :)
    float n = sin(dot(p, vec2(27, 57)));
    //return fract(vec2(262144, 32768)*n)*2. - 1.; 

    // Animated.
    p = fract(vec2(262144, 32768)*n);
    return sin(p*6.2831853 + iTime*1.57); 

}


// The Truchet distance field. Truchet patterns, in their various forms, are 
// pretty easy to put together; Render some rotationally symmetric tiles, then
// randomly rotate them. If you know how to render simple 2D objects like 
// squares, circles, arcs, etc, you should be good to go. As you can see from
// the imagery, these tiles consist of a line of circles and some chopped
// out circles.
float distField(vec2 p){

    // Saving the original position.
    vec2 oP = p;

    // Cell ID and local coordinates.
    vec2 ip = floor(p);
    p -= ip + .5;

    // Random value for the tile. This one is time based.
    float rnd = hash21A(ip);

    // If the random number generated by the unique ID for the
    // tile is above a certain threshold, flip it upside down.
    if(rnd<.5) p.y = -p.y;


    // Distance field.
    float d = 1e5;

    // Radius.
    float r = .09;

    // Two circles on diagonally opposite corners.
    d = min(d, length((p) - vec2(-.5, .5)) - .5);
    d = min(d, length((p) - vec2(.5, -.5)) - .5);

    // Some small circles down the center.
    float d2 = length(p - .3) - r*.9;
    d2 = min(d2, length(p - .1) - r*.7); 
    d2 = min(d2, length(p + .1) - r*.7); 
    d2 = min(d2, length(p + .3) - r*.9);  

    // Flip checkered tiles. It's a necessary operation to perform
    // for this style of Truchet tile.
    if(mod(ip.x + ip.y, 2.)<.5) d = -d;
    if(rnd>=.5) d = -d;

    // Put in some decorative borders. I like them, but you can take
    // them out if you feel it's too much.
    d = max((d - r*1.25), -(abs(abs(d ) - .05) - r/3.));

    // Combining the dotted lines and circles, whilst allowing for
    // the checkerboard flipping.
    if(mod(ip.x + ip.y, 2.)<.5){
        if(rnd<.5) d = max(d, -d2);
        else d = min(d, d2);
    }
    else {
       if(rnd<.5) d = min(d, d2);
        else d = max(d, -d2); 
    }

    // Adding a grid. Not necessary, but I like it.
    p = abs(p);
    float grid = abs(max(p.x, p.y) - .5) - .015;
    d = min(d, grid);


    // Rendering circles at the grid vertices, whilst accounting
    // for checkerboard flipping.
    vec2  q = oP - .5;
    vec2 iq = floor(q);
    q -= iq + .5;

    if(mod(iq.x + iq.y, 2.)<.5){
        d = min(d, length(q) - r*1.4);
    }
    else {
       d = max(d, -(length(q) - r*1.4));
    }


    // Return the distance field value.
    return d;
}

// Skewing and unskewing.
vec2 skewXY(vec2 p, vec2 v){ return mat2(1, -v.y, v.x, 1)*p; }
vec2 unskewXY(vec2 p, vec2 v){ return inverse(mat2(1, -v.y, v.x, 1))*p; }


// The background pattern.
vec4 bgField(vec2 q2){

    // Helper variables: Scale, skewing vector, 
    // individual cell dimension, and block dimension.
    const float scale = 1./1.25;
	const vec2 sk = vec2(1, -1)/5.; // 12 x .2
	vec2 dim = vec2(1.5, 1)*scale;
	vec2 s = (vec2(2.5, 2.5) - abs(sk)/2.)*scale; // 12 x .2



    float d = 1e5;
    vec2 cntr, p, ip;

    vec2 id = vec2(0);
    vec2 l = dim;
    cntr = vec2(0);
    float boxID = 0.;
    vec2 offs = vec2(1, 0);

    for(int i = 0; i<4; i++){

        // With herringbone arrangements, the rectangular tile needs to
        // be intermittently rotated by 90 degrees, which means flipping
        // dimensions, etc.
        if(i==2) {
            cntr = vec2((dim.x + dim.y)/2., -dim.y/4.);
            l = l.yx;
            offs = offs.yx;
        }

    	p = q2 - cntr; // Local coordinates, based on a square grid.
    	p = skewXY(p, sk); // Skewing by the X and Y skewing values.
        ip = floor(p/s); // Local tile ID.
        p -= (ip + .5)*s; // New local position.
        p = unskewXY(p, sk); // Unskewing.


        // Individual positional tile ID.
        vec2 idi = ip*s + cntr + l/2.;
        // Don't forget to unskew the ID... Yeah, skewing is confusing. :)
        idi = unskewXY(idi, sk);

        // The brick dimension itself. Not used here.
        //float di2D = sBoxS(p, l/2., .04);


        // We're rendering a large circle and two smaller circles in each 
        // rectangular tile, which are randomly flipped in accordance with
        // the tile ID.
        if(hash21(idi)<.5) p = -p;

        float lw = dim.y/4.; // Offset.
        float sz = dim.y/2.; // Size.
        float ew = .04*scale; // Empty border width, or margin, to CSS folk.

        vec3 df;

        // Large circle, and two smaller circles.
        df.x = length(p - offs*lw) - (sz - ew);
        df.y = length(p + offs*lw*2. + offs.yx*lw) - (sz/2. - ew);
        df.z = length(p + offs*lw*2. - offs.yx*lw) - (sz/2. - ew);

        // A little offset circle inside the large circle.
        vec2 rnd22 = hash22(idi);
        df.x = max(df.x, -(length(p - offs*lw - rnd22*(lw/2. + .05*scale)) - lw/2.5));          

        // Obtain the smallest of the three circles for this particular tile.
        vec3 dfi = df.x<df.y && df.x<df.z? vec3(df.x, offs*lw) :
        		   df.y<df.z? vec3(df.y, offs*lw*2. + offs.yx*lw) :
        		   vec3(df.z, offs*lw*2. - offs.yx*lw);

        // If one of the circle objects in this particular tile is smaller than the
        // overall smallest object, update the distance field, object ID, and make
        // a note of the rectangular tile ID.
        if(dfi.x<d){

            d = dfi.x;
            id = idi + dfi.yz;
            boxID = float(i);

        }

        // Move the position down by the longest length.
        cntr -= -dim.y;

    }

    // Return the distance, ID and rectangular tile ID.
    return vec4(d, id, boxID);
}


// A hatch-like algorithm, or a stipple... or some kind of textured pattern.
float doHatch(vec2 p, float res){


    // The pattern is physically based, so needs to factor in screen resolution.
    p *= res/16.;

    // Random looking diagonal hatch lines.
    float hatch = clamp(sin((p.x - p.y)*3.14159*200.)*2. + .5, 0., 1.); // Diagonal lines.

    // Slight randomization of the diagonal lines, but the trick is to do it with
    // tiny squares instead of pixels.
    float hRnd = hash21(floor(p*6.) + .73);
    if(hRnd>.66) hatch = hRnd;  

    // Return the hatch value.
    return hatch;

}


// One of many simple wood grain formulas. Not much thought was put into it.
// This one has accentuated grain marks for a more cartoonish look, but more
// realistic grains can be created in a simpilar fashion.
vec4 woodGrain(in vec3 p){


    // Noise.
    float ns = (n3D(p*96.)*.67 + n3D(p*192.)*.33);

    // Slight perturbation.
    //p += (n3D(2.5*p)*.66 + n3D(5.*p)*.34)*.5;
    p += (n3D(2.*p)*.57 + n3D(4.*p)*.28 + n3D(8.*p)*.15)*.5;

    // Stretching things out along one of the axes -- In this case the Y axis.
    p *= vec3(1, 80, 1)*4.;

    // Cheap fract lines.
    float v = fract(-p.y*.1);
    //float v = fract(dot(p, vec3(-.1)));
    v *= v;
    v = min(v, v*(1. - v)*5.); // Smoothing the fract lines.

    // Using the value above to produce the timber color. I did this a while
    // ago, but I'm sure I knew what I was doing at the time. :D
    vec3 rWood = pow(min(vec3(1.5, 1, 1)*mix(.3, .9, v), 1.), vec3(1, 3, 12));
    vec3 wood = mix(vec3(.6, .4, .2), vec3(1.2, .8, .4), v);
    //vec3 wood = mix(vec3(.5, .125, .025)/1.25, vec3(.75, .27, .05)*1.5, v);
    //vec3 wood = mix(vec3(1, .8, .6)/3., vec3(1.2, 1, .8), v);

    wood = mix(wood, rWood, .25 - v*.25);

    // Adding in a sprinking of noise.
    vec3 rNoise = pow(min(vec3(1, 1, 1)*mix(.8, 1., ns), 1.), vec3(1, 2, 3));
    wood = min(wood*rNoise*1.1, 1.);

    // Toning things down, just a touch.
    //wood = pow(wood, vec3(.95));

    // Returning the timber color value, and the distance value -- which in this
    // case, is just the red channel, but it could be something more sophisticated.
    return vec4(wood, wood.x);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord){


    // Aspect correct screen coordinates.
    float iRes = min(iResolution.y, 800.);
	vec2 uv = (fragCoord - iResolution.xy*.5)/iRes;

    // Scaling only.
    float gSc = 10.;
    vec2 p = uv*gSc;

    // Saving a copy of the original.
    vec2 oP = p;

    // Resolution based scaling.
    float sf = gSc/iResolution.y;

    // The framed distance field.
    float d = distField(p);

    // The background pattern.
    vec4 dBg = bgField(rot2(-3.14159/4.)*oP);

    // Frame width, rounding factors and frame dimensions.
    const float frW = .45;
    const float rF = .05, rF2 = .01;
    vec2 fDim = vec2(14, 6);

    #ifdef PATTERN_ONLY
    // A quick hack to display the Truchet pattern only.
    fDim *= 2.;
    #endif



    // The square frame.
    vec2 q = p;
    float sqr = sBoxS(q, fDim/2. + frW*2., rF);
    float fr = max(sqr, -sBoxS(q,fDim/2., rF2)); 

    // The frame shadow.
    q = (p - vec2(frW/2., -frW/2.));
    float sqr2 = sBoxS(q, fDim/2. + frW*2., rF);
    float fr2 = max(sqr2, -sBoxS(q, fDim/2., rF2));


    // Using a standard distance filed operation to cut down the size of 
    // the cavas to the frame.
    d = max(d, sqr + frW*2.);


    // A hatch value, just to add a little extra texture here and there.
    float hatch = doHatch(oP/gSc, iRes);


    // The timber color.
    float frSh = clamp(.25 - fr/frW*2., 0., 1.);
    vec4 wGrain = woodGrain(vec3(oP/4., 1. + frSh*.1));
    // Last minute adjustment: Toning the wood grain down a bit, if desired.
    //wGrain.xyz = mix(wGrain.xyz, wGrain.w*wGrain.w*vec3(.85, .8, .75), .25);


    // Applying some cheap hatching.
    wGrain.xyz *= hatch*.3 + .8;


    // The scene color. Initialized to zero.
    vec3 col = vec3(0);


    // Applying the background circle pattern.
    vec4 wGrainBg = woodGrain(vec3(oP/3. + .5, 1.));
    col = vec3(.95, .8, .7)*(wGrainBg.w*.3 + .85);
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*1.5, dBg.x))*.35);
    col = mix(col, vec3(.95, .8, .7)*(hatch*.2 + .9), (1. - smoothstep(0., sf, dBg.x + .05))*.7);

    // A cleaner background.
    //col = vec3(.95, .8, .7);
    //col *= wGrainBg.w*.5 + .7;     

    // Applying some subtle hatching.
    col *= hatch*.35 + .75;


    // Putting the frame shadow down first.
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*15., fr2 - .05))*.65);

    // The frame.
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*4., fr))*.5);
    col = mix(col, wGrain.xyz*frSh, (1. - smoothstep(0., sf, fr)));


    // The frame content: In this case, the dot-like Truchet pattern.
    col = mix(col, vec3(.8) + hatch*.3, (1. - smoothstep(0., sf, sqr + frW*2.)));
    col = mix(col, vec3(.01, .005, 0) + hatch*.05, (1. - smoothstep(0., sf, d)));

    // Outer edging.
    col = mix(col, vec3(.1, .05, 0), (1. - smoothstep(0., sf*1.5, abs(fr + .01) - .01))*.85);

    // Inner edging... or something like that -- Like everyone else, I make this stuff 
    // up as I go along. :)
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*1.5, abs(fr + .15) - .01))*.75);

    // Subtle highlighting.
    col = mix(col, col*1.35, (1. - smoothstep(0., sf*4., max((fr + .02), -(fr2 + .02)))));


    // Cheap paper grain.
    oP = floor(oP/gSc*1024.);
    vec3 rn3 = vec3(hash21(oP), hash21(oP + 2.37), hash21(oP + 4.83));
    col *= .9 + .1*rn3.xyz  + .1*rn3.xxx;    



    // Applying a subtle silhouette, for art's sake.
	uv = fragCoord/iResolution.xy;
    col *= pow(16.*(1. - uv.x)*(1. - uv.y)*uv.x*uv.y, 1./32.); 

    // Output to screen
    fragColor = vec4(sqrt(max(col, 0.)), 1);
}

float lineDist(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p - a;
    vec2 ba = b - a;
    float t = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    return length(pa - ba*t);
}

float H21(vec2 p) {
    return fract(sin(p.x * 123.456 + p.y * 7654.321) * 314.159);
}

vec2 getCirc(vec2 id) {
    float f1 = 0.3 + H21(id);
    float f2 = 0.3 + H21(id + 50.);
    float p1 = H21(id + 100.) * 100.;
    float p2 = H21(id + 150.) * 100.;
    float r = 0.35;
    float x = sin(f1 * iTime + p1);
    float y = sin(f2 * iTime + p2);
    return 0.5 + r * vec2(x, y);
}

float distScale(float dist) {
    return smoothstep(1.5, .6, dist);
}

float connectVal(vec2 p, vec2 circ1, vec2 circ2) {
    float ds = distScale(distance(circ1, circ2));
    return smoothstep(ds*.08+0.04, .0, lineDist(p, circ1, circ2)) * ds;
}

float combineGrad(float grad, float cv) {
    return grad + cv;
}

#define UPDATE_GRAD(n1, n2) \
  grad = combineGrad(grad, connectVal(sp, neighbors[n1], neighbors[n2]))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 id = floor(fragCoord / 40.);
    vec2 sp = fract(fragCoord / 40.);

    vec2 neighbors[9];
    for (int i = 0, dx = -1; dx < 2; dx++) {
        for (int dy = -1; dy < 2; dy++) {
            neighbors[i++] = getCirc(id + vec2(dx, dy)) + vec2(dx, dy);
        }
    }
    float grad = 0.;
    for (int i = 0; i < 9; i++) {
        if (i != 4) {
            UPDATE_GRAD(i, 4);
        }
    }

    UPDATE_GRAD(1, 5);
    UPDATE_GRAD(5, 7);
    UPDATE_GRAD(7, 3);
    // UPDATE_GRAD(3, 1);

    vec3 col = vec3(grad);

    fragColor = vec4(col,1.0);
}

float sphere(vec2 ray, vec2 dir, vec2 center, float radius, float n)
{
	vec2 rc = ray-center;
	float c = dot(rc, rc) - (radius*radius);
	float b = dot(dir, rc);
	float d = b*b - c;
	float t = -b - n*sqrt(abs(d));
	float st = step(0.0, min(t,d));
	return mix(-1., t, st);
}
float line(vec2 uv, vec2 p, vec2 p2){
	vec2 n=uv-p;
	vec2 w=p2-p;
	float r=clamp(dot(n,w)/dot(w,w),0.,1.);
	r=length(n-w*r);

    return clamp(smoothstep(0.01,0.004,r)*(smoothstep(0.6,1.,1.)),0.,1.);
}
float point(vec2 uv, vec2 p){
    p-=uv;
	return 0.0001/dot(p,p);
}
const float n_air=1.;
const float n_glass=1.2;
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y*1.+vec2(0.7,0);

    vec2 ro=vec2(-1,0);
    vec2 rd=normalize(vec2(-0.5,sin(iTime)*0.125)-ro);
    vec2 c=vec2(1,0);
    float r=0.5;


    float dist=sphere(ro,rd,c,r,1.);
    vec2 surf=ro+rd*dist;

    vec2 norm=normalize(surf-c);
    vec2 rd_refle=reflect(rd,norm);
    vec2 rd_refra_1=refract(rd,norm,n_air/n_glass);

    float dist2=sphere(surf,rd_refra_1,c,r,-1.);
    vec2 surf2=surf+rd_refra_1*dist2;

    vec2 norm2=normalize(c-surf2);

    vec2 rd_refra_2=refract(rd_refra_1,norm2,n_glass/n_air);



    float k=distance(c,uv)-r;
    vec3 col = smoothstep(0.0001,0.,k*k)*vec3(0,0,1);


	col+=point(uv,ro);
    col+=point(uv,c);
    col+=point(uv,rd*0.2+ro);
    col+=point(uv,surf2);
    col+=point(uv,surf);
    col+=point(uv,surf+0.2*rd_refle);
    col+=point(uv,surf+0.2*rd_refra_1);
    col+=point(uv,surf2+0.2*rd_refra_2);

    col+=line(uv,ro,surf)*vec3(1,0,0);
    col+=line(uv,surf,surf2)*vec3(1,0,0);
    col+=line(uv,surf,surf+rd_refle)*vec3(1,0,0);
    col+=line(uv,c,(surf-c)*2.+c)*vec3(0,1,0);
	col+=line(uv,c,(surf2-c)*2.+c)*vec3(0,1,0);
    col+=line(uv,surf2,surf2+rd_refra_2)*vec3(1,0,0);



    // Output to screen
    fragColor = vec4(col,1.0);
}

