#define ITER 100.
#define PI 3.141592
#define t (iTime*2.*PI)

float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(12.45,46.5)))*124.5);}

// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

void moda (inout vec2 p, float rep)
{
    float per = 2.*PI/rep;
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a, per)-per*0.5;
    p = vec2(cos(a),sin(a))*l;
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r, abs(p.z)-h);}

float prim (vec3 p)
{
    p.xz *= rot(sin(p.y*2.)+iTime);
  	moda(p.xz, 5.);
    p.x -= .18;
   	return cyl(p.xzy, 0.1, 1e10);
}

float g1 = 0.;
float eel (vec3 p)
{
    p.x = abs(p.x)-2.;
    p.z -= t;
    p.x -= sin(p.z+t);
    p.y += cos(p.z+t);
    float d = cyl(p, 0.2+sin(p.z*5.)*0.1, 1.);
    g1 += 0.1/(0.1+d*d);
    return d; 
}

float grid (vec3 p)
{
    float per = 4.;
    p = mod(p,per)-per*0.5;

    float p1 = prim(p);
    p.xy*=rot(PI/2.);  
    float p2 = prim(p);
    p.yz *= rot(PI/2.);
    return min(min(p1,p2),prim (p));
}

float SDF (vec3 p)
{return min(grid(p),eel(p));}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
    vec3 f = normalize(tar-ro);
    vec3 l = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = normalize(cross(f,l));
    return normalize(f + l*uv.x + u*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    uv += texNoise(texNoise(uv*0.5+iTime*0.2).rg*0.2).rg*0.7;

    float dither = hash21(uv);

    vec3 ro = vec3(4.,1.4,-4.+t),
        p = ro,
        tar = vec3(0.,-.5,t ),
        rd = getcam(ro, tar,uv),
        col = vec3(0.);

    float shad = 0.;

    for (float i =0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.01)
        {
            shad = i/ITER;
            break;
        }
        d *= 0.5+dither*0.2;
        p += d*rd;
    }

    float ad = length(ro-p);

    col = vec3(shad)*0.8;
	col += g1 * vec3(0.2,0.7,0.8+p.x*0.5)*0.4;

    col = mix(col, vec3(0.,0.15,0.35), 1.-exp(-0.008*ad*ad));
    // Output to screen
    fragColor = vec4(col,1.0);
}

float texXY(int x, int y) {
    return x >= 0 && y >= 0 && (x & y) == 0 ? 1.0 : 0.0;
}

float texY(float x, int y) {
    x -= 0.5 * float(y);
    float texX1 = texXY(int(floor(x)), y);
    float texX2 = texXY(int(floor(x)) + 1, y);
    return mix(texX1, texX2, fract(x));
}

float tex(vec2 pos) {
    float texY1 = texY(pos.x, int(pos.y));
    float texY2 = texY(pos.x, int(pos.y) + 1);
    return mix(texY1, texY2, fract(pos.y));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime * 0.35);

    float zoom = pow(2.0, t);

    fragCoord *= 0.8 / zoom;
    fragCoord.x *= 0.8660254;

    float texM = mix(tex(fragCoord), tex(fragCoord * 2.0), zoom - 1.0);
    fragColor = vec4(texM, texM, texM, 1.0);
}

// derivative noise by iq. https://www.shadertoy.com/view/4dffRH

vec3 hash( vec3 p ) // i'm supposed to replace this with something better
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
// returns 3D value noise (in .x)  and its derivatives (in .yzw)
vec4 noised( in vec3 x )
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    // quintic interpolant
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);
    vec3 du = 30.0*w*w*(w*(w-2.0)+1.0);

    // gradients
    vec3 ga = hash( p+vec3(0.0,0.0,0.0) );
    vec3 gb = hash( p+vec3(1.0,0.0,0.0) );
    vec3 gc = hash( p+vec3(0.0,1.0,0.0) );
    vec3 gd = hash( p+vec3(1.0,1.0,0.0) );
    vec3 ge = hash( p+vec3(0.0,0.0,1.0) );
    vec3 gf = hash( p+vec3(1.0,0.0,1.0) );
    vec3 gg = hash( p+vec3(0.0,1.0,1.0) );
    vec3 gh = hash( p+vec3(1.0,1.0,1.0) );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolation
    float v = va + 
              u.x*(vb-va) + 
              u.y*(vc-va) + 
              u.z*(ve-va) + 
              u.x*u.y*(va-vb-vc+vd) + 
              u.y*u.z*(va-vc-ve+vg) + 
              u.z*u.x*(va-vb-ve+vf) + 
              u.x*u.y*u.z*(-va+vb+vc-vd+ve-vf-vg+vh);

    vec3 d = ga + 
             u.x*(gb-ga) + 
             u.y*(gc-ga) + 
             u.z*(ge-ga) + 
             u.x*u.y*(ga-gb-gc+gd) + 
             u.y*u.z*(ga-gc-ge+gg) + 
             u.z*u.x*(ga-gb-ge+gf) + 
             u.x*u.y*u.z*(-ga+gb+gc-gd+ge-gf-gg+gh) +   

             du * (vec3(vb-va,vc-va,ve-va) + 
                   u.yzx*vec3(va-vb-vc+vd,va-vc-ve+vg,va-vb-ve+vf) + 
                   u.zxy*vec3(va-vb-ve+vf,va-vb-vc+vd,va-vc-ve+vg) + 
                   u.yzx*u.zxy*(-va+vb+vc-vd+ve-vf-vg+vh) );

    return vec4( v, d );                   
}

vec4 recursive_noise(vec3 p, float octaves){
    vec4 n = vec4(0.);
    for (float i = 0.; i < octaves; i += 1.){
        float e = pow(2., i);
        vec3 mini_pos = p*e;
        //mini_pos.w *= e; // Smaller is noisier
        n += noised(mini_pos)/e;
    }
    return n/2.;
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 2.0;
const float MAX_DIST = 10.0;
const float EPSILON = 0.2; // cutoff for clouds

const vec3 LIGHT_1 = vec3(0, 1, 0);
const vec3 LIGHT_2 = normalize(vec3(10, 3, -10)); // backlight

//const vec3 LIGHT_1 = LIGHT_2;


float sphere(vec3 samplePoint, float radius) {
    return clamp(-length(samplePoint) + radius, 0., 1.);
}


float spheres(vec3 p){
	vec3 side = vec3(3, 0, 1);
    //samplePoint.z += 1.;
    return sphere(p, 2.) + sphere(p+side, 1.) + sphere(p-side, 1.);
}


vec4 sceneDeriv(vec3 voxel) {
    //return spheres(voxel);
    float scale = 3.; // larger noise
    float p_scale = .5;
    //vec4 p_noise = noised(voxel/8.-iTime); // perterbation noise, using derivative

    // Recursive displacement on normal noise
    //vec4 p_noise = recursive_noise(voxel/p_scale-iTime/32., 8.);

    // move
    voxel.xyz -= iTime/8.;
    // change
    float change = iTime/1.;
    vec4 p_noise = noised(voxel*vec3(.5, 1, 1)-change/16.)/4.;
    p_noise += noised(voxel*vec3(1, 2, 2) -change/4.)/4.;
    p_noise += noised(-voxel*vec3(6, 6, 6)+change)/16.;
    //vec4 noise = recursive_noise(voxel/scale + p_noise.yzw/4.1, 8.);
    vec4 noise = noised(voxel/scale + p_noise.yzw);

	//vec4 noise = recursive_noise(voxel*scale, 8.);
    //vec4 noise = recursive_noise(vec3(voxel.x, voxel.y, voxel.z-iTime/(scale)), 8.);
    noise.x = (0., (noise.x - EPSILON)/(1.-noise.x)); // cut off below epsilon and respread
    return noise;
    // we have no use for negative noise values, unless we want them as bad distance estimates
}

float scene(vec3 voxel){
    //return spheres(voxel);
	return sceneDeriv(voxel).x;
}

// Returns the extent to which a cloud bit is in "shadow"
// Assuming a relative to the normalized light direction
float getShadow(vec3 point, vec3 lightDirection, float end){
    float step_length = .05;
    float dist = 0.; // distance to edge of cloud in light direction
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        dist += step_length;
        float new_point = scene(point + dist * lightDirection);
        if (new_point < EPSILON) {
            return dist;
        }
        dist += step_length;
        if (dist >= end) {
            return dist;
        }
    }
    return dist;
}

// Check one point, shadow is relative change
// Pick an arbitrary lightward neighbor
// Shadow is density of neighbor + difference in densities
float fastShadow(vec3 point, vec3 lightDirection){
    float dist = .2; // distance to edge of cloud in light direction
    float density = scene(point);
    float neighbor = scene(point + dist * lightDirection);
    //float volume_difference = scene(point + dist * lightDirection) - density;
    // If volume_difference is negative (lighter), no shadow
    float shadow = clamp((neighbor*4.-density*1.)*1.+.1, 0., 1.);
    // effectively mixing difference (rough approximation of derivative)
    // with neighbor density
    return shadow;
}

float sampleShadow(vec3 point, vec3 lightDirection){
    float dist = 0.0; // distance to edge of cloud in light direction
    float density = scene(point);
    float shadow = 0.;
    for (int i = 0; i < 3; i++) {
        dist += 0.1;
        float neighbor = scene(point + dist * lightDirection);
        shadow += neighbor;
        shadow -= density/2.; // this makes trends matter.
        // in liight density, it will still be dark if neighbors are darker.
    }
    //float volume_difference = scene(point + dist * lightDirection) - density;
    // If volume_difference is negative (lighter), no shadow
    shadow = clamp(shadow, 0., 1.);

    return shadow;
}

// Blends colors moving forward using alpha channels.
vec4 composite(vec4 foreground, vec4 background){
    float f_alpha = foreground.w;
    float b_alpha = background.w * (1.-f_alpha);
    vec3 color = foreground.xyz + background.xyz * b_alpha;
	//alpha = 1.-(1.-alpha)*(1.-background.w);
	float new_alpha = f_alpha + b_alpha;
	return vec4(color, new_alpha);
}

vec4 shootRay(vec3 eye, vec3 marchingDirection, float start, float end) {
    vec4 color = vec4(0); // start transparent
    float depth = start;
    float volume = 0.;
    float max_shaded = 2.;
    float step_length = .05;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        // Distance according to SDF
        vec3 point = eye + depth * marchingDirection;
        float density = scene(point);
        // float travel = dist;
        float travel = step_length;
        if (density > 0.) {
            float max_density = .5;
            // put a cap on density variance. 
            // clouds can still have irregular translucent edges, but no weird opaque cores.
            density = min(density, max_density)/(1.-max_density);
            float skyFade = 1.-(depth/end); // further away becomes fainter
            //float light = (max_shaded-getShadow(point, vec3(0, 1, 0), max_shaded))/max_shaded;
			float opacity = density * .1 + .0;
            //float opacity = .1;
            vec3 light = vec3((1.-fastShadow(point, LIGHT_1)));
            vec3 light2 = vec3((1.-fastShadow(point, LIGHT_2)));
            //vec3 light = vec3((1.-sampleShadow(point, LIGHT_1)));
            //vec3 light2 = vec3((1.-sampleShadow(point, LIGHT_2)));
            light *= vec3(.2, .3, .6)* 2.;
            light2 *= vec3(.4, .2, .1)* 2.;
            vec4 backColor = vec4(light+light2, opacity);
			//backColor.b = skyFade;
            color = composite(color, backColor);
            //color = composite(backColor, color);
            //volume += .1 * step_length;
            //volume += step_length * density * light;
            //volume += ()
            //volume += step_length * density;
           	//travel = .05;
        }
        //else{ travel = dist;}
        depth += travel;
        if (depth >= end) {
            return color;
        }
    }
    return color;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(85.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, 5.0);
    vec4 color = shootRay(eye, dir, MIN_DIST, MAX_DIST);
    //volume = clamp(volume, 0., 1.);
    // volume *= .2;
    float height = fragCoord.y/iResolution.y;
    vec4 skyColor = vec4(.9-height*.6, .86-height*.4, .75-height*.15, 1);
    skyColor = composite(color, skyColor);

    // gama correction. i.e. converting from light amount to screen/eye colorspace.
    skyColor.rgb = pow( skyColor.rgb, vec3(1.0/2.2) );
    fragColor = skyColor;
}

