// vec3 plan2(Ray ray, Plane plan

Ray2 inter_ray_plane(Ray ray, Plane plan) {
    // https://www.scratchapixel.com/
    float denom = dot(plan.direction, ray.direction);
    if (denom > 1e-6) {
        vec3 p010 = normalize(plan.origin-ray.origin);
    	float t = dot(p010, plan.direction)/denom;
    	vec3 p = ray.origin+ray.direction*t;
        // if (t < 0.) {
        	// float a = mod(p.x, 1.);
        	// float b = mod(p.z, 1.);
        	// vec3 col = vec3(min(a, b), a, max(a, b));
        	// checkerboard texture knowledge from
        	// http://web.cse.ohio-state.edu/~shen.94/681/Site/Slides_files/texture.pdf
        	vec3 col = vec3(mod(
                floor(p.x*2.)+
                floor(p.z*2.),
            2.));
            return Ray2(
                true,
                col,
                Ray(p, reflect(ray.direction, plan.direction))
            );
        // }
	}
    return Ray2(false, noColor, noRay);
}

Ray2 inter_ray_sph(Ray ray, Sphere sph) {
    // Much help from TheArtOfCode
    // https://www.youtube.com/watch?v=HFPlKQGChpE
    // t is the closest point on the ray to the center
    // of the sphere (unless im mistaken)
    float t = dot(sph.center-ray.origin, ray.direction);
    // no t is the coefficient of rd to get that point
    // p is the point on the ray closest to the center
    // of the sphere
    vec3 p = ray.origin+ray.direction*t;
    // pi_0 and pi_1 have nothing to do with any circle
    // constant. they are the initial point of intersection
    // and secondary point of intersection of the ray with
    // the sphere respectively
    float y = length(p-sph.center);
    if (t > 0.) {
        if (y < sph.radius) {
            float x = sqrt(sph.radius*sph.radius-y*y);
            vec3 pi_0 = ray.origin+ray.direction*(t-x);
            vec3 pi_1 = ray.origin+ray.direction*(t+x);
            Plane plan_r = Plane(pi_0, normalize(pi_0-sph.center) );
            vec3 col = vec3(.2, 0., 0.);
            // col = vec3(0., 1., 200./255.);
            vec3 r = reflect(ray.direction, plan_r.direction);
            float spec = abs(dot(r, r))/5.;
            col = (col+vec3(spec))/2.;
            return Ray2(
                true,
                col,
                Ray(pi_0, reflect(ray.direction, plan_r.direction))
            );
    	}
    }
    return Ray2(false, noColor, noRay);
}

int closer(Ray ray, Ray2 obj1, Ray2 obj2) {
    if (obj1.exists) {
        if (obj2.exists) {
            if (length(obj2.ray.origin-ray.origin) < length(obj1.ray.origin-ray.origin)) {
                return 2;
            }
            else {
                return 1;
            }
        }
        else {
            return 1;
        }
    }
    else if (obj2.exists) {
        return 2;
    }
    else {
        return 0;
    }
}

